Line.create([
  { :line => "It was the hour before the Gods awake.", :no => 1, :created_at => "2012-09-28 14:45:47", :updated_at => "2012-09-28 14:45:47", :stanza_id => 1 },
  { :line => "Across the path of the divine Event", :no => 2, :created_at => "2012-09-28 14:48:14", :updated_at => "2012-09-28 14:48:14", :stanza_id => 2 },
  { :line => "The huge foreboding mind of Night, alone", :no => 3, :created_at => "2012-09-28 14:55:41", :updated_at => "2012-09-28 14:55:41", :stanza_id => 2 },
  { :line => "In her unlit temple of eternity,", :no => 4, :created_at => "2012-09-28 14:56:00", :updated_at => "2012-09-28 14:56:00", :stanza_id => 2 },
  { :line => "Lay stretched immobile upon Silenceâ€™ marge.", :no => 5, :created_at => "2012-09-28 14:56:16", :updated_at => "2012-09-28 14:56:16", :stanza_id => 2 },
  { :line => "Almost one felt, opaque, impenetrable,", :no => 6, :created_at => "2012-09-28 14:56:32", :updated_at => "2012-09-28 14:56:32", :stanza_id => 3 },
  { :line => "In the sombre symbol of her eyeless muse", :no => 7, :created_at => "2012-09-28 14:56:48", :updated_at => "2012-09-28 14:56:48", :stanza_id => 3 },
  { :line => "The abysm of the unbodied Infinite;", :no => 8, :created_at => "2012-09-28 14:57:05", :updated_at => "2012-09-28 14:57:05", :stanza_id => 3 },
  { :line => "A fathomless zero occupied the world.", :no => 9, :created_at => "2012-09-28 14:57:28", :updated_at => "2012-09-28 14:57:28", :stanza_id => 3 },
  { :line => "A power of fallen boundless self awake", :no => 10, :created_at => "2012-09-28 14:57:50", :updated_at => "2012-09-28 14:57:50", :stanza_id => 4 },
  { :line => "Between the first and the last Nothingness,", :no => 11, :created_at => "2012-09-28 14:58:06", :updated_at => "2012-10-08 11:31:43", :stanza_id => 4 },
  { :line => "Recalling the tenebrous womb from which it came,", :no => 12, :created_at => "2012-09-28 14:58:24", :updated_at => "2012-09-28 14:58:24", :stanza_id => 4 },
  { :line => "Turned from the insoluble mystery of birth", :no => 13, :created_at => "2012-09-28 14:58:39", :updated_at => "2012-09-28 14:58:39", :stanza_id => 4 },
  { :line => "And the tardy process of mortality", :no => 14, :created_at => "2012-09-28 14:58:55", :updated_at => "2012-09-28 14:58:55", :stanza_id => 4 },
  { :line => "And longed to reach its end in vacant Nought.", :no => 15, :created_at => "2012-09-28 14:59:20", :updated_at => "2012-09-28 14:59:20", :stanza_id => 4 },
  { :line => "As in a dark beginning of all things,", :no => 16, :created_at => "2012-10-04 11:02:23", :updated_at => "2012-10-04 11:02:23", :stanza_id => 5 },
  { :line => "A mute featureless semblance of the Unknown", :no => 17, :created_at => "2012-10-04 11:02:55", :updated_at => "2012-10-04 11:02:55", :stanza_id => 5 },
  { :line => "Repeating for ever the unconscious act,", :no => 18, :created_at => "2012-10-04 11:03:14", :updated_at => "2012-10-04 11:03:14", :stanza_id => 5 },
  { :line => "Prolonging for ever the unseeing will,", :no => 19, :created_at => "2012-10-04 11:03:32", :updated_at => "2012-10-04 11:03:32", :stanza_id => 5 },
  { :line => "Cradled the cosmic drowse of ignorant Force", :no => 20, :created_at => "2012-10-04 11:03:49", :updated_at => "2012-10-04 11:03:49", :stanza_id => 5 },
  { :line => "Whose moved creative slumber kindles the suns", :no => 21, :created_at => "2012-10-04 11:04:05", :updated_at => "2012-10-04 11:04:05", :stanza_id => 5 },
  { :line => "And carries our lives in its somnambulist whirl.", :no => 22, :created_at => "2012-10-04 11:04:21", :updated_at => "2012-10-04 11:04:21", :stanza_id => 5 },
  { :line => "Athwart the vain enormous trance of Space,", :no => 23, :created_at => "2012-10-04 11:06:05", :updated_at => "2012-10-04 11:06:05", :stanza_id => 6 },
  { :line => "Its formless stupor without mind or life,", :no => 24, :created_at => "2012-10-04 11:06:24", :updated_at => "2012-10-04 11:06:24", :stanza_id => 6 },
  { :line => "A shadow spinning through a soulless Void,", :no => 25, :created_at => "2012-10-04 11:06:43", :updated_at => "2012-10-04 11:06:43", :stanza_id => 6 },
  { :line => "Thrown back once more into unthinking dreams,", :no => 26, :created_at => "2012-10-04 11:06:57", :updated_at => "2012-10-04 11:06:57", :stanza_id => 6 },
  { :line => "Earth wheeled abandoned in the hollow gulfs", :no => 27, :created_at => "2012-10-04 11:07:18", :updated_at => "2012-10-04 11:07:18", :stanza_id => 6 },
  { :line => "Forgetful of her spirit and her fate.", :no => 28, :created_at => "2012-10-04 11:07:36", :updated_at => "2012-10-04 11:07:36", :stanza_id => 6 },
  { :line => "The impassive skies were neutral, empty, still.", :no => 29, :created_at => "2012-10-04 11:13:12", :updated_at => "2012-10-04 11:13:12", :stanza_id => 7 },
  { :line => "Then something in the inscrutable darkness stirred;", :no => 30, :created_at => "2012-10-04 11:13:43", :updated_at => "2012-10-04 11:13:43", :stanza_id => 8 },
  { :line => "A nameless movement, an unthought Idea", :no => 31, :created_at => "2012-10-04 11:14:05", :updated_at => "2012-10-04 11:14:05", :stanza_id => 8 },
  { :line => "Insistent, dissatisfied, without an aim,", :no => 32, :created_at => "2012-10-04 11:14:28", :updated_at => "2012-10-04 11:14:28", :stanza_id => 8 },
  { :line => "Something that wished but knew not how to be,", :no => 33, :created_at => "2012-10-04 11:14:48", :updated_at => "2012-10-04 11:14:48", :stanza_id => 8 },
  { :line => "Teased the Inconscient to wake Ignorance.", :no => 34, :created_at => "2012-10-04 11:15:07", :updated_at => "2012-10-04 11:15:07", :stanza_id => 8 },
  { :line => "A throe that came and left a quivering trace,", :no => 35, :created_at => "2012-10-10 12:15:44", :updated_at => "2012-10-10 12:15:44", :stanza_id => 9 },
  { :line => "Gave room for an old tired want unfilled,", :no => 36, :created_at => "2012-10-10 12:15:44", :updated_at => "2012-10-10 12:15:44", :stanza_id => 9 },
  { :line => "At peace in its subconscient moonless cave", :no => 37, :created_at => "2012-10-10 12:15:44", :updated_at => "2012-10-10 12:15:44", :stanza_id => 9 },
  { :line => "To raise its head and look for absent light,", :no => 38, :created_at => "2012-10-10 12:15:44", :updated_at => "2012-10-10 12:15:44", :stanza_id => 9 },
  { :line => "Straining closed eyes of vanished memory,", :no => 39, :created_at => "2012-10-10 12:15:45", :updated_at => "2012-10-10 12:15:45", :stanza_id => 9 },
  { :line => "Like one who searches for a bygone self", :no => 40, :created_at => "2012-10-10 12:15:45", :updated_at => "2012-10-10 12:15:45", :stanza_id => 9 },
  { :line => "And only meets the corpse of his desire.", :no => 41, :created_at => "2012-10-10 12:15:45", :updated_at => "2012-10-10 12:15:45", :stanza_id => 9 },
  { :line => "It was as though even in this Noughtâ€™s profound,", :no => 42, :created_at => "2012-10-10 12:19:39", :updated_at => "2012-10-10 12:19:39", :stanza_id => 10 },
  { :line => "Even in this ultimate dissolutionâ€™s core", :no => 43, :created_at => "2012-10-10 12:19:40", :updated_at => "2012-10-10 12:19:40", :stanza_id => 10 },
  { :line => "There lurked an unremembering entity,", :no => 44, :created_at => "2012-10-10 12:19:40", :updated_at => "2012-10-10 12:19:40", :stanza_id => 10 },
  { :line => "Survivor of a slain and buried past", :no => 45, :created_at => "2012-10-10 12:19:40", :updated_at => "2012-10-10 12:19:40", :stanza_id => 10 },
  { :line => "Condemned to resume the effort and the pang,", :no => 46, :created_at => "2012-10-10 12:19:40", :updated_at => "2012-10-10 12:19:40", :stanza_id => 10 },
  { :line => "Reviving in another frustrate world.", :no => 47, :created_at => "2012-10-10 12:19:41", :updated_at => "2012-10-10 12:19:41", :stanza_id => 10 },
  { :line => "An unshaped consciousness desired light", :no => 48, :created_at => "2012-10-10 12:20:57", :updated_at => "2012-10-10 12:21:28", :stanza_id => 11 },
  { :line => "And a blank prescience yearned towards distant change.", :no => 49, :created_at => "2012-10-10 12:20:57", :updated_at => "2012-10-10 12:26:16", :stanza_id => 11 },
  { :line => "As if a childlike finger laid on a cheek", :no => 50, :created_at => "2012-10-10 12:29:10", :updated_at => "2012-10-10 12:29:10", :stanza_id => 12 },
  { :line => "Reminded of the endless need in things", :no => 51, :created_at => "2012-10-10 12:29:10", :updated_at => "2012-10-10 12:29:10", :stanza_id => 12 },
  { :line => "The heedless Mother of the universe,", :no => 52, :created_at => "2012-10-10 12:29:10", :updated_at => "2012-10-10 12:29:10", :stanza_id => 12 },
  { :line => "An infant longing clutched the sombre Vast.", :no => 53, :created_at => "2012-10-10 12:29:10", :updated_at => "2012-10-10 12:29:10", :stanza_id => 12 },
  { :line => "Insensibly somewhere a breach began:", :no => 54, :created_at => "2012-10-10 12:30:23", :updated_at => "2012-10-10 12:30:23", :stanza_id => 13 },
  { :line => "A long lone line of hesitating hue", :no => 55, :created_at => "2012-10-10 12:30:24", :updated_at => "2012-10-10 12:30:24", :stanza_id => 13 },
  { :line => "Like a vague smile tempting a desert heart", :no => 56, :created_at => "2012-10-10 12:30:24", :updated_at => "2012-10-10 12:30:24", :stanza_id => 13 },
  { :line => "Troubled the far rim of lifeâ€™s obscure sleep.", :no => 57, :created_at => "2012-10-10 12:30:24", :updated_at => "2012-10-10 12:30:24", :stanza_id => 13 },
  { :line => "Arrived from the other side of boundlessness", :no => 58, :created_at => "2012-10-10 12:31:21", :updated_at => "2012-10-10 12:31:21", :stanza_id => 14 },
  { :line => "An eye of deity pierced through the dumb deeps;", :no => 59, :created_at => "2012-10-10 12:31:21", :updated_at => "2012-10-10 12:31:21", :stanza_id => 14 },
  { :line => "A scout in a reconnaissance from the sun,", :no => 60, :created_at => "2012-10-10 12:31:21", :updated_at => "2012-10-10 12:31:21", :stanza_id => 14 },
  { :line => "It seemed amid a heavy cosmic rest,", :no => 61, :created_at => "2012-10-10 12:31:22", :updated_at => "2012-10-10 12:31:22", :stanza_id => 14 },
  { :line => "The torpor of a sick and weary world,", :no => 62, :created_at => "2012-10-10 12:31:22", :updated_at => "2012-10-10 12:31:22", :stanza_id => 14 },
  { :line => "To seek for a spirit sole and desolate", :no => 63, :created_at => "2012-10-10 12:31:22", :updated_at => "2012-10-10 12:31:22", :stanza_id => 14 },
  { :line => "Too fallen to recollect forgotten bliss.", :no => 64, :created_at => "2012-10-10 12:31:22", :updated_at => "2012-10-10 12:31:22", :stanza_id => 14 },
  { :line => "Intervening in a mindless universe,", :no => 65, :created_at => "2012-10-10 12:32:14", :updated_at => "2012-10-10 12:32:14", :stanza_id => 15 },
  { :line => "Its message crept through the reluctant hush", :no => 66, :created_at => "2012-10-10 12:32:14", :updated_at => "2012-10-10 12:32:14", :stanza_id => 15 },
  { :line => "Calling the adventure of consciousness and joy", :no => 67, :created_at => "2012-10-10 12:32:14", :updated_at => "2012-10-10 12:32:14", :stanza_id => 15 },
  { :line => "And, conquering Natureâ€™s disillusioned breast,", :no => 68, :created_at => "2012-10-10 12:32:14", :updated_at => "2012-10-10 12:32:14", :stanza_id => 15 },
  { :line => "Compelled renewed consent to see and feel.", :no => 69, :created_at => "2012-10-10 12:32:14", :updated_at => "2012-10-10 12:32:14", :stanza_id => 15 },
  { :line => "A thought was sown in the unsounded Void,", :no => 70, :created_at => "2012-10-10 12:33:51", :updated_at => "2012-10-10 12:33:51", :stanza_id => 16 },
  { :line => "A sense was born within the darknessâ€™ depths,", :no => 71, :created_at => "2012-10-10 12:33:52", :updated_at => "2012-10-10 12:33:52", :stanza_id => 16 },
  { :line => "A memory quivered in the heart of Time", :no => 72, :created_at => "2012-10-10 12:33:52", :updated_at => "2012-10-10 12:33:52", :stanza_id => 16 },
  { :line => "As if a soul long dead were moved to live:", :no => 73, :created_at => "2012-10-10 12:33:52", :updated_at => "2012-10-10 12:33:52", :stanza_id => 16 },
  { :line => "But the oblivion that succeeds the fall,", :no => 74, :created_at => "2012-10-10 12:33:52", :updated_at => "2012-10-10 12:33:52", :stanza_id => 16 },
  { :line => "Had blotted the crowded tablets of the past,", :no => 75, :created_at => "2012-10-10 12:33:53", :updated_at => "2012-10-10 12:33:53", :stanza_id => 16 },
  { :line => "And all that was destroyed must be rebuilt", :no => 76, :created_at => "2012-10-10 12:33:53", :updated_at => "2012-10-10 12:33:53", :stanza_id => 16 },
  { :line => "And old experience laboured out once more.", :no => 77, :created_at => "2012-10-10 12:33:53", :updated_at => "2012-10-10 12:33:53", :stanza_id => 16 },
  { :line => "All can be done if the god-touch is there.", :no => 78, :created_at => "2012-10-10 12:34:49", :updated_at => "2012-10-10 12:34:49", :stanza_id => 17 },
  { :line => "A hope stole in that hardly dared to be", :no => 79, :created_at => "2012-10-10 12:35:45", :updated_at => "2012-10-10 12:35:45", :stanza_id => 18 },
  { :line => "Amid the Nightâ€™s forlorn indifference.", :no => 80, :created_at => "2012-10-10 12:35:46", :updated_at => "2012-10-10 12:35:46", :stanza_id => 18 },
  { :line => "As if solicited in an alien world", :no => 81, :created_at => "2012-10-10 12:36:23", :updated_at => "2012-10-10 12:36:23", :stanza_id => 19 },
  { :line => "With timid and hazardous instinctive grace,", :no => 82, :created_at => "2012-10-10 12:36:24", :updated_at => "2012-10-10 12:36:24", :stanza_id => 19 },
  { :line => "Orphaned and driven out to seek a home,", :no => 83, :created_at => "2012-10-10 12:36:24", :updated_at => "2012-10-10 12:36:24", :stanza_id => 19 },
  { :line => "An errant marvel with no place to live,", :no => 84, :created_at => "2012-10-10 12:36:24", :updated_at => "2012-10-10 12:36:24", :stanza_id => 19 },
  { :line => "Into a far-off nook of heaven there came", :no => 85, :created_at => "2012-10-10 12:36:24", :updated_at => "2012-10-10 12:36:24", :stanza_id => 19 },
  { :line => "A slow miraculous gestureâ€™s dim appeal.", :no => 86, :created_at => "2012-10-10 12:36:24", :updated_at => "2012-10-10 12:36:24", :stanza_id => 19 },
  { :line => "The persistent thrill of a transfiguring touch", :no => 87, :created_at => "2012-10-10 12:37:02", :updated_at => "2012-10-10 12:37:02", :stanza_id => 20 },
  { :line => "Persuaded the inert black quietude", :no => 88, :created_at => "2012-10-10 12:37:02", :updated_at => "2012-10-10 12:37:02", :stanza_id => 20 },
  { :line => "And beauty and wonder disturbed the fields of God.", :no => 89, :created_at => "2012-10-10 12:37:02", :updated_at => "2012-10-10 12:37:02", :stanza_id => 20 },
  { :line => "A wandering hand of pale enchanted light", :no => 90, :created_at => "2012-10-10 12:37:34", :updated_at => "2012-10-10 12:37:34", :stanza_id => 21 },
  { :line => "That glowed along a fading momentâ€™s brink,", :no => 91, :created_at => "2012-10-10 12:37:35", :updated_at => "2012-10-10 12:37:35", :stanza_id => 21 },
  { :line => "Fixed with gold panel and opalescent hinge", :no => 92, :created_at => "2012-10-10 12:37:35", :updated_at => "2012-10-10 12:37:35", :stanza_id => 21 },
  { :line => "A gate of dreams ajar on mysteryâ€™s verge.", :no => 93, :created_at => "2012-10-10 12:37:35", :updated_at => "2012-10-10 12:37:35", :stanza_id => 21 },
  { :line => "One lucent corner windowing hidden things", :no => 94, :created_at => "2012-10-10 12:38:23", :updated_at => "2012-10-10 12:38:23", :stanza_id => 22 },
  { :line => "Forced the worldâ€™s blind immensity to sight.", :no => 95, :created_at => "2012-10-10 12:38:24", :updated_at => "2012-10-10 12:38:24", :stanza_id => 22 },
  { :line => "The darkness failed and slipped like a falling cloak", :no => 96, :created_at => "2012-10-10 12:39:17", :updated_at => "2012-10-10 12:39:17", :stanza_id => 23 },
  { :line => "From the reclining body of a god.", :no => 97, :created_at => "2012-10-10 12:39:18", :updated_at => "2012-10-10 12:39:18", :stanza_id => 23 },
  { :line => "Then through the pallid rift that seemed at first", :no => 98, :created_at => "2012-10-10 12:39:57", :updated_at => "2012-10-10 12:39:57", :stanza_id => 24 },
  { :line => "Hardly enough for a trickle from the suns,", :no => 99, :created_at => "2012-10-10 12:39:58", :updated_at => "2012-10-10 12:39:58", :stanza_id => 24 },
  { :line => "Outpoured the revelation and the flame.", :no => 100, :created_at => "2012-10-10 12:39:58", :updated_at => "2012-10-10 12:39:58", :stanza_id => 24 },
  { :line => "The brief perpetual sign recurred above.", :no => 101, :created_at => "2012-10-10 12:40:41", :updated_at => "2012-10-10 12:40:41", :stanza_id => 25 },
  { :line => "A glamour from the unreached transcendences", :no => 102, :created_at => "2012-10-10 12:41:10", :updated_at => "2012-10-10 12:41:10", :stanza_id => 26 },
  { :line => "Iridescent with the glory of the Unseen,", :no => 103, :created_at => "2012-10-10 12:41:11", :updated_at => "2012-10-10 12:41:11", :stanza_id => 26 },
  { :line => "A message from the unknown immortal Light", :no => 104, :created_at => "2012-10-10 12:41:11", :updated_at => "2012-10-10 12:41:11", :stanza_id => 26 },
  { :line => "Ablaze upon creationâ€™s quivering edge,", :no => 105, :created_at => "2012-10-10 12:41:11", :updated_at => "2012-10-10 12:41:11", :stanza_id => 26 },
  { :line => "Dawn built her aura of magnificent hues", :no => 106, :created_at => "2012-10-10 12:41:12", :updated_at => "2012-10-10 12:41:12", :stanza_id => 26 },
  { :line => "And buried its seed of grandeur in the hours.", :no => 107, :created_at => "2012-10-10 12:41:12", :updated_at => "2012-10-10 12:41:12", :stanza_id => 26 },
  { :line => "An instantâ€™s visitor the godhead shone:", :no => 108, :created_at => "2012-10-10 12:42:25", :updated_at => "2012-10-10 12:42:25", :stanza_id => 27 },
  { :line => "On lifeâ€™s thin border awhile the Vision stood", :no => 109, :created_at => "2012-10-10 12:42:25", :updated_at => "2012-10-10 12:42:25", :stanza_id => 27 },
  { :line => "And bent over earthâ€™s pondering forehead curve.", :no => 110, :created_at => "2012-10-10 12:42:26", :updated_at => "2012-10-10 12:42:26", :stanza_id => 27 },
  { :line => "Interpreting a recondite beauty and bliss", :no => 111, :created_at => "2012-10-10 12:42:43", :updated_at => "2012-10-10 12:42:43", :stanza_id => 28 },
  { :line => "In colourâ€™s hieroglyphs of mystic sense,", :no => 112, :created_at => "2012-10-10 12:42:44", :updated_at => "2012-10-10 12:42:44", :stanza_id => 28 },
  { :line => "It wrote the lines of a significant myth", :no => 113, :created_at => "2012-10-10 12:42:44", :updated_at => "2012-10-10 12:42:44", :stanza_id => 28 },
  { :line => "Telling of a greatness of spiritual dawns,", :no => 114, :created_at => "2012-10-10 12:42:44", :updated_at => "2012-10-10 12:42:44", :stanza_id => 28 },
  { :line => "A brilliant code penned with the sky for page.", :no => 115, :created_at => "2012-10-10 12:42:44", :updated_at => "2012-10-10 12:42:44", :stanza_id => 28 },
  { :line => "Almost that day the epiphany was disclosed", :no => 116, :created_at => "2012-10-10 12:43:00", :updated_at => "2012-10-10 12:43:00", :stanza_id => 29 },
  { :line => "Of which our thoughts and hopes are signal flares;", :no => 117, :created_at => "2012-10-10 12:43:01", :updated_at => "2012-10-10 12:43:01", :stanza_id => 29 },
  { :line => "A lonely splendour from the invisible goal", :no => 118, :created_at => "2012-10-10 12:43:01", :updated_at => "2012-10-10 12:43:01", :stanza_id => 29 },
  { :line => "Almost was flung on the opaque Inane.", :no => 119, :created_at => "2012-10-10 12:43:01", :updated_at => "2012-10-10 12:43:01", :stanza_id => 29 },
  { :line => "Once more a tread perturbed the vacant Vasts;", :no => 120, :created_at => "2012-10-10 12:43:22", :updated_at => "2012-10-10 12:43:22", :stanza_id => 30 },
  { :line => "Infinityâ€™s centre, a Face of rapturous calm", :no => 121, :created_at => "2012-10-10 12:43:22", :updated_at => "2012-10-10 12:43:22", :stanza_id => 30 },
  { :line => "Parted the eternal lids that open heaven;", :no => 122, :created_at => "2012-10-10 12:43:22", :updated_at => "2012-10-10 12:43:22", :stanza_id => 30 },
  { :line => "A Form from far beatitudes seemed to near.", :no => 123, :created_at => "2012-10-10 12:43:23", :updated_at => "2012-10-10 12:43:23", :stanza_id => 30 },
  { :line => "Ambassadress twixt eternity and change,", :no => 124, :created_at => "2013-03-04 14:36:14", :updated_at => "2013-03-04 14:36:14", :stanza_id => 31 },
  { :line => "The omniscient Goddess leaned across the breadths", :no => 125, :created_at => "2013-03-04 14:36:14", :updated_at => "2013-03-04 14:36:14", :stanza_id => 31 },
  { :line => "That wrap the fated journeyings of the stars", :no => 126, :created_at => "2013-03-04 14:36:14", :updated_at => "2013-03-04 14:36:14", :stanza_id => 31 },
  { :line => "And saw the spaces ready for her feet.", :no => 127, :created_at => "2013-03-04 14:36:15", :updated_at => "2013-03-04 14:36:15", :stanza_id => 31 },
  { :line => "Once she half looked behind for her veiled sun,", :no => 128, :created_at => "2013-03-04 14:36:15", :updated_at => "2013-03-04 14:36:15", :stanza_id => 32 },
  { :line => "Then, thoughtful, went to her immortal work.", :no => 129, :created_at => "2013-03-04 14:36:15", :updated_at => "2013-03-04 14:36:15", :stanza_id => 32 },
  { :line => "Earth felt the Imperishableâ€™s passage close:", :no => 130, :created_at => "2013-03-04 14:36:16", :updated_at => "2013-03-04 14:36:16", :stanza_id => 33 },
  { :line => "The waking ear of Nature heard her steps", :no => 131, :created_at => "2013-03-04 14:36:16", :updated_at => "2013-03-04 14:36:16", :stanza_id => 33 },
  { :line => "And wideness turned to her its limitless eye,", :no => 132, :created_at => "2013-03-04 14:36:16", :updated_at => "2013-03-04 14:36:16", :stanza_id => 33 },
  { :line => "And, scattered on sealed depths, her luminous smile", :no => 133, :created_at => "2013-03-04 14:36:16", :updated_at => "2013-03-04 14:36:16", :stanza_id => 33 },
  { :line => "Kindled to fire the silence of the worlds.", :no => 134, :created_at => "2013-03-04 14:36:17", :updated_at => "2013-03-04 14:36:17", :stanza_id => 33 },
  { :line => "All grew a consecration and a rite.", :no => 135, :created_at => "2013-03-04 14:36:17", :updated_at => "2013-03-04 14:36:17", :stanza_id => 34 },
  { :line => "Air was a vibrant link between earth and heaven;", :no => 136, :created_at => "2013-03-04 14:36:17", :updated_at => "2013-03-04 14:36:17", :stanza_id => 35 },
  { :line => "The wide-winged hymn of a great priestly wind", :no => 137, :created_at => "2013-03-04 14:36:17", :updated_at => "2013-03-04 14:36:17", :stanza_id => 35 },
  { :line => "Arose and failed upon the altar hills;", :no => 138, :created_at => "2013-03-04 14:36:18", :updated_at => "2013-03-04 14:36:18", :stanza_id => 35 },
  { :line => "The high boughs prayed in a revealing sky.", :no => 139, :created_at => "2013-03-04 14:36:18", :updated_at => "2013-03-04 14:36:18", :stanza_id => 35 },
  { :line => "Here where our half-lit ignorance skirts the gulfs", :no => 140, :created_at => "2013-03-04 14:36:18", :updated_at => "2013-03-04 14:36:18", :stanza_id => 36 },
  { :line => "On the dumb bosom of the ambiguous earth,", :no => 141, :created_at => "2013-03-04 14:36:19", :updated_at => "2013-03-04 14:36:19", :stanza_id => 36 },
  { :line => "Here where one knows not even the step in front", :no => 142, :created_at => "2013-03-04 14:36:19", :updated_at => "2013-03-04 14:36:19", :stanza_id => 36 },
  { :line => "And Truth has her throne on the shadowy back of doubt,", :no => 143, :created_at => "2013-03-04 14:36:19", :updated_at => "2013-03-04 14:36:19", :stanza_id => 36 },
  { :line => "On this anguished and precarious field of toil", :no => 144, :created_at => "2013-03-04 14:36:19", :updated_at => "2013-03-04 14:36:19", :stanza_id => 36 },
  { :line => "Outspread beneath some large indifferent gaze,", :no => 145, :created_at => "2013-03-04 14:36:19", :updated_at => "2013-03-04 14:36:19", :stanza_id => 36 },
  { :line => "Impartial witness to our joy and bale,", :no => 146, :created_at => "2013-03-04 14:36:20", :updated_at => "2013-03-04 14:36:20", :stanza_id => 36 },
  { :line => "Our prostrate soil bore the awakening ray.", :no => 147, :created_at => "2013-03-04 14:36:20", :updated_at => "2013-03-04 14:36:20", :stanza_id => 36 },
  { :line => "Here too the vision and prophetic gleam", :no => 148, :created_at => "2013-03-04 14:36:20", :updated_at => "2013-03-04 14:36:20", :stanza_id => 37 },
  { :line => "Lit into miracles common meaningless shapes;", :no => 149, :created_at => "2013-03-04 14:36:20", :updated_at => "2013-03-04 14:36:20", :stanza_id => 37 },
  { :line => "Then the divine afflatus, spent, withdrew,", :no => 150, :created_at => "2013-03-04 14:36:20", :updated_at => "2013-03-04 14:36:20", :stanza_id => 37 },
  { :line => "Unwanted, fading from the mortalâ€™s range.", :no => 151, :created_at => "2013-03-04 14:36:21", :updated_at => "2013-03-04 14:36:21", :stanza_id => 37 },
  { :line => "A sacred yearning lingered in its trace,", :no => 152, :created_at => "2013-03-04 14:36:21", :updated_at => "2013-03-04 14:36:21", :stanza_id => 38 },
  { :line => "The worship of a Presence and a Power", :no => 153, :created_at => "2013-03-04 14:36:21", :updated_at => "2013-03-04 14:36:21", :stanza_id => 38 },
  { :line => "Too perfect to be held by death-bound hearts,", :no => 154, :created_at => "2013-03-04 14:36:21", :updated_at => "2013-03-04 14:36:21", :stanza_id => 38 },
  { :line => "The prescience of a marvellous birth to come.", :no => 155, :created_at => "2013-03-04 14:36:22", :updated_at => "2013-03-04 14:36:22", :stanza_id => 38 },
  { :line => "Only a little the God-light can stay:", :no => 156, :created_at => "2013-03-04 14:36:22", :updated_at => "2013-03-04 14:36:22", :stanza_id => 39 },
  { :line => "Spiritual beauty illumining human sight", :no => 157, :created_at => "2013-03-04 14:36:22", :updated_at => "2013-03-04 14:36:22", :stanza_id => 39 },
  { :line => "Lines with its passion and mystery Matterâ€™s mask", :no => 158, :created_at => "2013-03-04 14:36:22", :updated_at => "2013-03-04 14:36:22", :stanza_id => 39 },
  { :line => "And squanders eternity on a beat of Time.", :no => 159, :created_at => "2013-03-04 14:36:23", :updated_at => "2013-03-04 14:36:23", :stanza_id => 39 },
  { :line => "As when a soul draws near the sill of birth,", :no => 160, :created_at => "2013-03-04 14:36:23", :updated_at => "2013-03-04 14:36:23", :stanza_id => 40 },
  { :line => "Adjoining mortal time to Timelessness,", :no => 161, :created_at => "2013-03-04 14:36:23", :updated_at => "2013-03-04 14:36:23", :stanza_id => 40 },
  { :line => "A spark of deity lost in Matterâ€™s crypt", :no => 162, :created_at => "2013-03-04 14:36:23", :updated_at => "2013-03-04 14:36:23", :stanza_id => 40 },
  { :line => "Its lustre vanishes in the inconscient planes,", :no => 163, :created_at => "2013-03-04 14:36:24", :updated_at => "2013-03-04 14:36:24", :stanza_id => 40 },
  { :line => "That transitory glow of magic fire", :no => 164, :created_at => "2013-03-04 14:36:24", :updated_at => "2013-03-04 14:36:24", :stanza_id => 40 },
  { :line => "So now dissolved in bright accustomed air.", :no => 165, :created_at => "2013-03-04 14:36:24", :updated_at => "2013-03-04 14:36:24", :stanza_id => 40 },
  { :line => "The message ceased and waned the messenger.", :no => 166, :created_at => "2013-03-04 14:36:24", :updated_at => "2013-03-04 14:36:24", :stanza_id => 41 },
  { :line => "The single Call, the uncompanioned Power,", :no => 167, :created_at => "2013-03-04 14:36:25", :updated_at => "2013-03-04 14:36:25", :stanza_id => 42 },
  { :line => "Drew back into some far-off secret world", :no => 168, :created_at => "2013-03-04 14:36:25", :updated_at => "2013-03-04 14:36:25", :stanza_id => 42 },
  { :line => "The hue and marvel of the supernal beam:", :no => 169, :created_at => "2013-03-04 14:36:25", :updated_at => "2013-03-04 14:36:25", :stanza_id => 42 },
  { :line => "She looked no more on our mortality.", :no => 170, :created_at => "2013-03-04 14:36:25", :updated_at => "2013-03-04 14:36:25", :stanza_id => 42 },
  { :line => "The excess of beauty natural to God-kind", :no => 171, :created_at => "2013-03-04 14:36:26", :updated_at => "2013-03-04 14:36:26", :stanza_id => 43 },
  { :line => "Could not uphold its claim on time-born eyes;", :no => 172, :created_at => "2013-03-04 14:36:26", :updated_at => "2013-03-04 14:36:26", :stanza_id => 43 },
  { :line => "Too mystic-real for space-tenancy", :no => 173, :created_at => "2013-03-04 14:36:26", :updated_at => "2013-03-04 14:36:26", :stanza_id => 43 },
  { :line => "Her body of glory was expunged from heaven:", :no => 174, :created_at => "2013-03-04 14:36:26", :updated_at => "2013-03-04 14:36:26", :stanza_id => 43 },
  { :line => "The rarity and wonder lived no more.", :no => 175, :created_at => "2013-03-04 14:36:27", :updated_at => "2013-03-04 14:36:27", :stanza_id => 43 },
  { :line => "There was the common light of earthly day.", :no => 176, :created_at => "2013-03-04 14:36:27", :updated_at => "2013-03-04 14:36:27", :stanza_id => 44 },
  { :line => "Affranchised from the respite of fatigue", :no => 177, :created_at => "2013-03-04 14:36:27", :updated_at => "2013-03-04 14:36:27", :stanza_id => 45 },
  { :line => "Once more the rumour of the speed of Life", :no => 178, :created_at => "2013-03-04 14:36:28", :updated_at => "2013-03-04 14:36:28", :stanza_id => 45 },
  { :line => "Pursued the cycles of her blinded quest.", :no => 179, :created_at => "2013-03-04 14:36:28", :updated_at => "2013-03-04 14:36:28", :stanza_id => 45 },
  { :line => "All sprang to their unvarying daily acts;", :no => 180, :created_at => "2013-03-04 14:36:28", :updated_at => "2013-03-04 14:36:28", :stanza_id => 46 },
  { :line => "The thousand peoples of the soil and tree", :no => 181, :created_at => "2013-03-04 14:36:28", :updated_at => "2013-03-04 14:36:28", :stanza_id => 46 },
  { :line => "Obeyed the unforeseeing instantâ€™s urge,", :no => 182, :created_at => "2013-03-04 14:36:28", :updated_at => "2013-03-04 14:36:28", :stanza_id => 46 },
  { :line => "And, leader here with his uncertain mind,", :no => 183, :created_at => "2013-03-04 14:36:29", :updated_at => "2013-03-04 14:36:29", :stanza_id => 46 },
  { :line => "Alone who stares at the futureâ€™s covered face,", :no => 184, :created_at => "2013-03-04 14:36:29", :updated_at => "2013-03-04 14:36:29", :stanza_id => 46 },
  { :line => "Man lifted up the burden of his fate.", :no => 185, :created_at => "2013-03-04 14:36:30", :updated_at => "2013-03-04 14:36:30", :stanza_id => 46 },
  { :line => "And Savitri too awoke among these tribes", :no => 186, :created_at => "2013-03-04 14:36:30", :updated_at => "2013-03-04 14:36:30", :stanza_id => 47 },
  { :line => "That hastened to join the brilliant Summonerâ€™s chant", :no => 187, :created_at => "2013-03-04 14:36:30", :updated_at => "2013-03-04 14:36:30", :stanza_id => 47 },
  { :line => "And, lured by the beauty of the apparent ways,", :no => 188, :created_at => "2013-03-04 14:36:31", :updated_at => "2013-03-04 14:36:31", :stanza_id => 47 },
  { :line => "Acclaimed their portion of ephemeral joy.", :no => 189, :created_at => "2013-03-04 14:36:31", :updated_at => "2013-03-04 14:36:31", :stanza_id => 47 },
  { :line => "Akin to the eternity whence she came,", :no => 190, :created_at => "2013-03-04 14:36:31", :updated_at => "2013-03-04 14:36:31", :stanza_id => 48 },
  { :line => "No part she took in this small happiness;", :no => 191, :created_at => "2013-03-04 14:36:31", :updated_at => "2013-03-04 14:36:31", :stanza_id => 48 },
  { :line => "A mighty stranger in the human field,", :no => 192, :created_at => "2013-03-04 14:36:32", :updated_at => "2013-03-04 14:36:32", :stanza_id => 48 },
  { :line => "The embodied Guest within made no response.", :no => 193, :created_at => "2013-03-04 14:36:32", :updated_at => "2013-03-04 14:36:32", :stanza_id => 48 },
  { :line => "The call that wakes the leap of human mind,", :no => 194, :created_at => "2013-03-04 14:36:33", :updated_at => "2013-03-04 14:36:33", :stanza_id => 49 },
  { :line => "Its chequered eager motion of pursuit,", :no => 195, :created_at => "2013-03-04 14:36:33", :updated_at => "2013-03-04 14:36:33", :stanza_id => 49 },
  { :line => "Its fluttering-hued illusion of desire,", :no => 196, :created_at => "2013-03-04 14:36:33", :updated_at => "2013-03-04 14:36:33", :stanza_id => 49 },
  { :line => "Visited her heart like a sweet alien note.", :no => 197, :created_at => "2013-03-04 14:36:33", :updated_at => "2013-03-04 14:36:33", :stanza_id => 49 },
  { :line => "Timeâ€™s message of brief light was not for her.", :no => 198, :created_at => "2013-03-04 14:36:34", :updated_at => "2013-03-04 14:36:34", :stanza_id => 50 },
  { :line => "In her there was the anguish of the gods", :no => 199, :created_at => "2013-03-04 14:36:34", :updated_at => "2013-03-04 14:36:34", :stanza_id => 51 },
  { :line => "Imprisoned in our transient human mould,", :no => 200, :created_at => "2013-03-04 14:36:34", :updated_at => "2013-03-04 14:36:34", :stanza_id => 51 },
  { :line => "The deathless conquered by the death of things.", :no => 201, :created_at => "2013-03-04 14:36:35", :updated_at => "2013-03-04 14:36:35", :stanza_id => 51 },
  { :line => "A vaster Natureâ€™s joy had once been hers,", :no => 202, :created_at => "2013-03-04 14:36:35", :updated_at => "2013-03-04 14:36:35", :stanza_id => 52 },
  { :line => "But long could keep not its gold heavenly hue", :no => 203, :created_at => "2013-03-04 14:36:35", :updated_at => "2013-03-04 14:36:35", :stanza_id => 52 },
  { :line => "Or stand upon this brittle earthly base.", :no => 204, :created_at => "2013-03-04 14:36:35", :updated_at => "2013-03-04 14:36:35", :stanza_id => 52 },
  { :line => "A narrow movement on Timeâ€™s deep abysm,", :no => 205, :created_at => "2013-03-04 14:36:36", :updated_at => "2013-03-04 14:36:36", :stanza_id => 53 },
  { :line => "Lifeâ€™s fragile littleness denied the power,", :no => 206, :created_at => "2013-03-04 14:36:36", :updated_at => "2013-03-04 14:36:36", :stanza_id => 53 },
  { :line => "The proud and conscious wideness and the bliss", :no => 207, :created_at => "2013-03-04 14:36:36", :updated_at => "2013-03-04 14:36:36", :stanza_id => 53 },
  { :line => "She had brought with her into the human form,", :no => 208, :created_at => "2013-03-04 14:36:37", :updated_at => "2013-03-04 14:36:37", :stanza_id => 53 },
  { :line => "The calm delight that weds one soul to all,", :no => 209, :created_at => "2013-03-04 14:36:37", :updated_at => "2013-03-04 14:36:37", :stanza_id => 53 },
  { :line => "The key to the flaming doors of ecstasy.", :no => 210, :created_at => "2013-03-04 14:36:37", :updated_at => "2013-03-04 14:36:37", :stanza_id => 53 },
  { :line => "Earthâ€™s grain that needs the sap of pleasure and tears", :no => 211, :created_at => "2013-03-04 14:36:38", :updated_at => "2013-03-04 14:36:38", :stanza_id => 54 },
  { :line => "Rejected the undying raptureâ€™s boon:", :no => 212, :created_at => "2013-03-04 14:36:38", :updated_at => "2013-03-04 14:36:38", :stanza_id => 54 },
  { :line => "Offered to the daughter of infinity", :no => 213, :created_at => "2013-03-04 14:36:38", :updated_at => "2013-03-04 14:36:38", :stanza_id => 54 },
  { :line => "Her passion-flower of love and doom she gave.", :no => 214, :created_at => "2013-03-04 14:36:38", :updated_at => "2013-03-04 14:36:38", :stanza_id => 54 },
  { :line => "In vain now seemed the splendid sacrifice.", :no => 215, :created_at => "2013-03-04 14:36:39", :updated_at => "2013-03-04 14:36:39", :stanza_id => 55 },
  { :line => "A prodigal of her rich divinity,", :no => 216, :created_at => "2013-03-04 14:36:39", :updated_at => "2013-03-04 14:36:39", :stanza_id => 56 },
  { :line => "Her self and all she was she had lent to men,", :no => 217, :created_at => "2013-03-04 14:36:39", :updated_at => "2013-03-04 14:36:39", :stanza_id => 56 },
  { :line => "Hoping her greater being to implant", :no => 218, :created_at => "2013-03-04 14:36:39", :updated_at => "2013-03-04 14:36:39", :stanza_id => 56 },
  { :line => "That heaven might native grow on mortal soil.", :no => 219, :created_at => "2013-03-04 14:36:40", :updated_at => "2013-03-04 14:36:40", :stanza_id => 56 },
  { :line => "Hard is it to persuade earth-natureâ€™s change;", :no => 220, :created_at => "2013-03-04 14:36:40", :updated_at => "2013-03-04 14:36:40", :stanza_id => 57 },
  { :line => "Mortality bears ill the eternalâ€™s touch:", :no => 221, :created_at => "2013-03-04 14:36:40", :updated_at => "2013-03-04 14:36:40", :stanza_id => 57 },
  { :line => "It fears the pure divine intolerance", :no => 222, :created_at => "2013-03-04 14:36:41", :updated_at => "2013-03-04 14:36:41", :stanza_id => 57 },
  { :line => "Of that assault of ether and of fire;", :no => 223, :created_at => "2013-03-04 14:36:41", :updated_at => "2013-03-04 14:36:41", :stanza_id => 57 },
  { :line => "It murmurs at its sorrowless happiness,", :no => 224, :created_at => "2013-03-04 14:36:41", :updated_at => "2013-03-04 14:36:41", :stanza_id => 57 },
  { :line => "Almost with hate repels the light it brings;", :no => 225, :created_at => "2013-03-04 14:36:41", :updated_at => "2013-03-04 14:36:41", :stanza_id => 57 },
  { :line => "It trembles at its naked power of Truth", :no => 226, :created_at => "2013-03-04 14:36:41", :updated_at => "2013-03-04 14:36:41", :stanza_id => 57 },
  { :line => "And the might and sweetness of its absolute Voice.", :no => 227, :created_at => "2013-03-04 14:36:42", :updated_at => "2013-03-04 14:36:42", :stanza_id => 57 },
  { :line => "Inflicting on the heights the abysmâ€™s law,", :no => 228, :created_at => "2013-03-04 14:36:42", :updated_at => "2013-03-04 14:36:42", :stanza_id => 58 },
  { :line => "It sullies with its mire heavenâ€™s messengers:", :no => 229, :created_at => "2013-03-04 14:36:42", :updated_at => "2013-03-04 14:36:42", :stanza_id => 58 },
  { :line => "Its thorns of fallen nature are the defence", :no => 230, :created_at => "2013-03-04 14:36:42", :updated_at => "2013-03-04 14:36:42", :stanza_id => 58 },
  { :line => "It turns against the saviour hands of Grace;", :no => 231, :created_at => "2013-03-04 14:36:43", :updated_at => "2013-03-04 14:36:43", :stanza_id => 58 },
  { :line => "It meets the sons of God with death and pain.", :no => 232, :created_at => "2013-03-04 14:36:43", :updated_at => "2013-03-04 14:36:43", :stanza_id => 58 },
  { :line => "A glory of lightnings traversing the earth-scene,", :no => 233, :created_at => "2013-03-04 14:36:43", :updated_at => "2013-03-04 14:36:43", :stanza_id => 59 },
  { :line => "Their sun-thoughts fading, darkened by ignorant minds,", :no => 234, :created_at => "2013-03-04 14:36:43", :updated_at => "2013-03-04 14:36:43", :stanza_id => 59 },
  { :line => "Their work betrayed, their good to evil turned,", :no => 235, :created_at => "2013-03-04 14:36:44", :updated_at => "2013-03-04 14:36:44", :stanza_id => 59 },
  { :line => "The cross their payment for the crown they gave,", :no => 236, :created_at => "2013-03-04 14:36:44", :updated_at => "2013-03-04 14:36:44", :stanza_id => 59 },
  { :line => "Only they leave behind a splendid Name.", :no => 237, :created_at => "2013-03-04 14:36:44", :updated_at => "2013-03-04 14:36:44", :stanza_id => 59 },
  { :line => "A fire has come and touched menâ€™s hearts and gone;", :no => 238, :created_at => "2013-03-04 14:36:44", :updated_at => "2013-03-04 14:36:44", :stanza_id => 60 },
  { :line => "A few have caught flame and risen to greater life.", :no => 239, :created_at => "2013-03-04 14:36:45", :updated_at => "2013-03-04 14:36:45", :stanza_id => 60 },
  { :line => "Too unlike the world she came to help and save,", :no => 240, :created_at => "2013-03-04 14:36:45", :updated_at => "2013-03-04 14:36:45", :stanza_id => 61 },
  { :line => "Her greatness weighed upon its ignorant breast,", :no => 241, :created_at => "2013-03-04 14:36:45", :updated_at => "2013-03-04 14:36:45", :stanza_id => 61 },
  { :line => "And from its deep chasms welled a dire return,", :no => 242, :created_at => "2013-03-04 14:36:45", :updated_at => "2013-03-04 14:36:45", :stanza_id => 61 },
  { :line => "A portion of its sorrow, struggle, fall.", :no => 243, :created_at => "2013-03-04 14:36:46", :updated_at => "2013-03-04 14:36:46", :stanza_id => 61 },
  { :line => "To live with grief, to confront death on her road,â€”", :no => 244, :created_at => "2013-03-04 14:36:46", :updated_at => "2013-03-04 14:36:46", :stanza_id => 62 },
  { :line => "The mortalâ€™s lot became the Immortalâ€™s share.", :no => 245, :created_at => "2013-03-04 14:36:46", :updated_at => "2013-03-04 14:36:46", :stanza_id => 62 },
  { :line => "Thus trapped in the gin of earthly destinies,", :no => 246, :created_at => "2013-03-04 14:36:47", :updated_at => "2013-03-04 14:36:47", :stanza_id => 63 },
  { :line => "Awaiting her ordealâ€™s hour abode,", :no => 247, :created_at => "2013-03-04 14:36:47", :updated_at => "2013-03-04 14:36:47", :stanza_id => 63 },
  { :line => "Outcast from her inborn felicity,", :no => 248, :created_at => "2013-03-04 14:36:47", :updated_at => "2013-03-04 14:36:47", :stanza_id => 63 },
  { :line => "Accepting lifeâ€™s obscure terrestrial robe,", :no => 249, :created_at => "2013-03-04 14:36:47", :updated_at => "2013-03-04 14:36:47", :stanza_id => 63 },
  { :line => "Hiding herself even from those she loved,", :no => 250, :created_at => "2013-03-04 14:36:48", :updated_at => "2013-03-04 14:36:48", :stanza_id => 63 },
  { :line => "The godhead greater by a human fate.", :no => 251, :created_at => "2013-03-04 14:36:48", :updated_at => "2013-03-04 14:36:48", :stanza_id => 63 },
  { :line => "A dark foreknowledge separated her", :no => 252, :created_at => "2013-03-04 14:36:48", :updated_at => "2013-03-04 14:36:48", :stanza_id => 64 },
  { :line => "From all of whom she was the star and stay;", :no => 253, :created_at => "2013-03-04 14:36:48", :updated_at => "2013-03-04 14:36:48", :stanza_id => 64 },
  { :line => "Too great to impart the peril and the pain,", :no => 254, :created_at => "2013-03-04 14:36:48", :updated_at => "2013-03-04 14:36:48", :stanza_id => 64 },
  { :line => "In her torn depths she kept the grief to come.", :no => 255, :created_at => "2013-03-04 14:36:49", :updated_at => "2013-03-04 14:36:49", :stanza_id => 64 },
  { :line => "As one who watching over men left blind", :no => 256, :created_at => "2013-03-04 14:36:49", :updated_at => "2013-03-04 14:36:49", :stanza_id => 65 },
  { :line => "Takes up the load of an unwitting race,", :no => 257, :created_at => "2013-03-04 14:36:49", :updated_at => "2013-03-04 14:36:49", :stanza_id => 65 },
  { :line => "Harbouring a foe whom with her heart she must feed,", :no => 258, :created_at => "2013-03-04 14:36:49", :updated_at => "2013-03-04 14:36:49", :stanza_id => 65 },
  { :line => "Unknown her act, unknown the doom she faced,", :no => 259, :created_at => "2013-03-04 14:36:50", :updated_at => "2013-03-04 14:36:50", :stanza_id => 65 },
  { :line => "Unhelped she must foresee and dread and dare.", :no => 260, :created_at => "2013-03-04 14:36:50", :updated_at => "2013-03-04 14:36:50", :stanza_id => 65 },
  { :line => "The long-foreknown and fatal morn was here", :no => 261, :created_at => "2013-03-04 14:36:50", :updated_at => "2013-03-04 14:36:50", :stanza_id => 66 },
  { :line => "Bringing a noon that seemed like every noon.", :no => 262, :created_at => "2013-03-04 14:36:50", :updated_at => "2013-03-04 14:36:50", :stanza_id => 66 },
  { :line => "For Nature walks upon her mighty way", :no => 263, :created_at => "2013-03-04 14:36:51", :updated_at => "2013-03-04 14:36:51", :stanza_id => 67 },
  { :line => "Unheeding when she breaks a soul, a life;", :no => 264, :created_at => "2013-03-04 14:36:51", :updated_at => "2013-03-04 14:36:51", :stanza_id => 67 },
  { :line => "Leaving her slain behind she travels on:", :no => 265, :created_at => "2013-03-04 14:36:51", :updated_at => "2013-03-04 14:36:51", :stanza_id => 67 },
  { :line => "Man only marks and Godâ€™s all-seeing eyes.", :no => 266, :created_at => "2013-03-04 14:36:51", :updated_at => "2013-03-04 14:36:51", :stanza_id => 67 },
  { :line => "Even in this moment of her soulâ€™s despair,", :no => 267, :created_at => "2013-03-04 14:36:52", :updated_at => "2013-03-04 14:36:52", :stanza_id => 68 },
  { :line => "In its grim rendezvous with death and fear,", :no => 268, :created_at => "2013-03-04 14:36:52", :updated_at => "2013-03-04 14:36:52", :stanza_id => 68 },
  { :line => "No cry broke from her lips, no call for aid;", :no => 269, :created_at => "2013-03-04 14:36:52", :updated_at => "2013-03-04 14:36:52", :stanza_id => 68 },
  { :line => "She told the secret of her woe to none:", :no => 270, :created_at => "2013-03-04 14:36:52", :updated_at => "2013-03-04 14:36:52", :stanza_id => 68 },
  { :line => "Calm was her face and courage kept her mute.", :no => 271, :created_at => "2013-03-04 14:36:53", :updated_at => "2013-03-04 14:36:53", :stanza_id => 68 },
  { :line => "Yet only her outward self suffered and strove;", :no => 272, :created_at => "2013-03-04 14:36:53", :updated_at => "2013-03-04 14:36:53", :stanza_id => 69 },
  { :line => "Even her humanity was half divine:", :no => 273, :created_at => "2013-03-04 14:36:53", :updated_at => "2013-03-04 14:36:53", :stanza_id => 69 },
  { :line => "Her spirit opened to the Spirit in all,", :no => 274, :created_at => "2013-03-04 14:36:53", :updated_at => "2013-03-04 14:36:53", :stanza_id => 69 },
  { :line => "Her nature felt all Nature as its own.", :no => 275, :created_at => "2013-03-04 14:36:54", :updated_at => "2013-03-04 14:36:54", :stanza_id => 69 },
  { :line => "Apart, living within, all lives she bore;", :no => 276, :created_at => "2013-03-04 14:36:54", :updated_at => "2013-03-04 14:36:54", :stanza_id => 70 },
  { :line => "Aloof, she carried in herself the world:", :no => 277, :created_at => "2013-03-04 14:36:54", :updated_at => "2013-03-04 14:36:54", :stanza_id => 70 },
  { :line => "Her dread was one with the great cosmic dread,", :no => 278, :created_at => "2013-03-04 14:36:55", :updated_at => "2013-03-04 14:36:55", :stanza_id => 70 },
  { :line => "Her strength was founded on the cosmic mights;", :no => 279, :created_at => "2013-03-04 14:36:55", :updated_at => "2013-03-04 14:36:55", :stanza_id => 70 },
  { :line => "The universal Motherâ€™s love was hers.", :no => 280, :created_at => "2013-03-04 14:36:55", :updated_at => "2013-03-04 14:36:55", :stanza_id => 70 },
  { :line => "Against the evil at lifeâ€™s afflicted roots,", :no => 281, :created_at => "2013-03-04 14:36:55", :updated_at => "2013-03-04 14:36:55", :stanza_id => 71 },
  { :line => "Her own calamity its private sign,", :no => 282, :created_at => "2013-03-04 14:36:56", :updated_at => "2013-03-04 14:36:56", :stanza_id => 71 },
  { :line => "Of her pangs she made a mystic poignant sword.", :no => 283, :created_at => "2013-03-04 14:36:56", :updated_at => "2013-03-04 14:36:56", :stanza_id => 71 },
  { :line => "A solitary mind, a world-wide heart,", :no => 284, :created_at => "2013-03-04 14:36:57", :updated_at => "2013-03-04 14:36:57", :stanza_id => 72 },
  { :line => "To the lone Immortalâ€™s unshared work she rose.", :no => 285, :created_at => "2013-03-04 14:36:57", :updated_at => "2013-03-04 14:36:57", :stanza_id => 72 },
  { :line => "At first life grieved not in her burdened breast:", :no => 286, :created_at => "2013-03-04 14:36:57", :updated_at => "2013-03-04 14:36:57", :stanza_id => 73 },
  { :line => "On the lap of earthâ€™s original somnolence", :no => 287, :created_at => "2013-03-04 14:36:58", :updated_at => "2013-03-04 14:36:58", :stanza_id => 73 },
  { :line => "Inert, released into forgetfulness", :no => 288, :created_at => "2013-03-04 14:36:58", :updated_at => "2013-03-04 14:36:58", :stanza_id => 73 },
  { :line => "Prone it reposed, unconscious on mindâ€™s verge,", :no => 289, :created_at => "2013-03-04 14:36:58", :updated_at => "2013-03-04 14:36:58", :stanza_id => 73 },
  { :line => "Obtuse and tranquil like the stone and star.", :no => 290, :created_at => "2013-03-04 14:36:58", :updated_at => "2013-03-04 14:36:58", :stanza_id => 73 },
  { :line => "In a deep cleft of silence twixt two realms", :no => 291, :created_at => "2013-03-04 14:36:59", :updated_at => "2013-03-04 14:36:59", :stanza_id => 74 },
  { :line => "She lay remote from grief, unsawn by care,", :no => 292, :created_at => "2013-03-04 14:36:59", :updated_at => "2013-03-04 14:36:59", :stanza_id => 74 },
  { :line => "Nothing recalling of the sorrow here.", :no => 293, :created_at => "2013-03-04 14:36:59", :updated_at => "2013-03-04 14:36:59", :stanza_id => 74 },
  { :line => "Then a slow faint remembrance shadowlike moved,", :no => 294, :created_at => "2013-03-04 14:36:59", :updated_at => "2013-03-04 14:36:59", :stanza_id => 75 },
  { :line => "And sighing she laid her hand upon her bosom", :no => 295, :created_at => "2013-03-04 14:37:00", :updated_at => "2013-03-04 14:37:00", :stanza_id => 75 },
  { :line => "And recognised the close and lingering ache,", :no => 296, :created_at => "2013-03-04 14:37:00", :updated_at => "2013-03-04 14:37:00", :stanza_id => 75 },
  { :line => "Deep, quiet, old, made natural to its place,", :no => 297, :created_at => "2013-03-04 14:37:00", :updated_at => "2013-03-04 14:37:00", :stanza_id => 75 },
  { :line => "But knew not why it was there nor whence it came.", :no => 298, :created_at => "2013-03-04 14:37:00", :updated_at => "2013-03-04 14:37:00", :stanza_id => 75 },
  { :line => "The Power that kindles mind was still withdrawn:", :no => 299, :created_at => "2013-03-04 14:37:01", :updated_at => "2013-03-04 14:37:01", :stanza_id => 76 },
  { :line => "Heavy, unwilling were lifeâ€™s servitors", :no => 300, :created_at => "2013-03-04 14:37:01", :updated_at => "2013-03-04 14:37:01", :stanza_id => 76 },
  { :line => "Like workers with no wages of delight;", :no => 301, :created_at => "2013-03-04 14:37:01", :updated_at => "2013-03-04 14:37:01", :stanza_id => 76 },
  { :line => "Sullen, the torch of sense refused to burn;", :no => 302, :created_at => "2013-03-04 14:37:01", :updated_at => "2013-03-04 14:37:01", :stanza_id => 76 },
  { :line => "The unassisted brain found not its past.", :no => 303, :created_at => "2013-03-04 14:37:01", :updated_at => "2013-03-04 14:37:01", :stanza_id => 76 },
  { :line => "Only a vague earth-nature held the frame.", :no => 304, :created_at => "2013-03-04 14:37:02", :updated_at => "2013-03-04 14:37:02", :stanza_id => 77 },
  { :line => "But now she stirred, her life shared the cosmic load.", :no => 305, :created_at => "2013-03-04 14:37:02", :updated_at => "2013-03-04 14:37:02", :stanza_id => 78 },
  { :line => "At the summons of her bodyâ€™s voiceless call", :no => 306, :created_at => "2013-03-04 14:37:02", :updated_at => "2013-03-04 14:37:02", :stanza_id => 79 },
  { :line => "Her strong far-winging spirit travelled back,", :no => 307, :created_at => "2013-03-04 14:37:03", :updated_at => "2013-03-04 14:37:03", :stanza_id => 79 },
  { :line => "Back to the yoke of ignorance and fate,", :no => 308, :created_at => "2013-03-04 14:37:03", :updated_at => "2013-03-04 14:37:03", :stanza_id => 79 },
  { :line => "Back to the labour and stress of mortal days,", :no => 309, :created_at => "2013-03-04 14:37:03", :updated_at => "2013-03-04 14:37:03", :stanza_id => 79 },
  { :line => "Lighting a pathway through strange symbol dreams", :no => 310, :created_at => "2013-03-04 14:37:03", :updated_at => "2013-03-04 14:37:03", :stanza_id => 79 },
  { :line => "Across the ebbing of the seas of sleep.", :no => 311, :created_at => "2013-03-04 14:37:04", :updated_at => "2013-03-04 14:37:04", :stanza_id => 79 },
  { :line => "Her house of Nature felt an unseen sway,", :no => 312, :created_at => "2013-03-04 14:37:04", :updated_at => "2013-03-04 14:37:04", :stanza_id => 80 },
  { :line => "Illumined swiftly were lifeâ€™s darkened rooms,", :no => 313, :created_at => "2013-03-04 14:37:04", :updated_at => "2013-03-04 14:37:04", :stanza_id => 80 },
  { :line => "And memoryâ€™s casements opened on the hours", :no => 314, :created_at => "2013-03-04 14:37:05", :updated_at => "2013-03-04 14:37:05", :stanza_id => 80 },
  { :line => "And the tired feet of thought approached her doors.", :no => 315, :created_at => "2013-03-04 14:37:05", :updated_at => "2013-03-04 14:37:05", :stanza_id => 80 },
  { :line => "All came back to her: Earth and Love and Doom,", :no => 316, :created_at => "2013-03-04 14:37:05", :updated_at => "2013-03-04 14:37:05", :stanza_id => 81 },
  { :line => "The ancient disputants, encircled her", :no => 317, :created_at => "2013-03-04 14:37:05", :updated_at => "2013-03-04 14:37:05", :stanza_id => 81 },
  { :line => "Like giant figures wrestling in the night:", :no => 318, :created_at => "2013-03-04 14:37:05", :updated_at => "2013-03-04 14:37:05", :stanza_id => 81 },
  { :line => "The godheads from the dim Inconscient born", :no => 319, :created_at => "2013-03-04 14:37:06", :updated_at => "2013-03-04 14:37:06", :stanza_id => 81 },
  { :line => "Awoke to struggle and the pang divine,", :no => 320, :created_at => "2013-03-04 14:37:06", :updated_at => "2013-03-04 14:37:06", :stanza_id => 81 },
  { :line => "And in the shadow of her flaming heart,", :no => 321, :created_at => "2013-03-04 14:37:06", :updated_at => "2013-03-04 14:37:06", :stanza_id => 81 },
  { :line => "At the sombre centre of the dire debate,", :no => 322, :created_at => "2013-03-04 14:37:06", :updated_at => "2013-03-04 14:37:06", :stanza_id => 81 },
  { :line => "A guardian of the unconsoled abyss", :no => 323, :created_at => "2013-03-04 14:37:06", :updated_at => "2013-03-04 14:37:06", :stanza_id => 81 },
  { :line => "Inheriting the long agony of the globe,", :no => 324, :created_at => "2013-03-04 14:37:07", :updated_at => "2013-03-04 14:37:07", :stanza_id => 81 },
  { :line => "A stone-still figure of high and godlike Pain", :no => 325, :created_at => "2013-03-04 14:37:07", :updated_at => "2013-03-04 14:37:07", :stanza_id => 81 },
  { :line => "Stared into space with fixed regardless eyes", :no => 326, :created_at => "2013-03-04 14:37:07", :updated_at => "2013-03-04 14:37:07", :stanza_id => 81 },
  { :line => "That saw griefâ€™s timeless depths but not lifeâ€™s goal.", :no => 327, :created_at => "2013-03-04 14:37:07", :updated_at => "2013-03-04 14:37:07", :stanza_id => 81 },
  { :line => "Afflicted by his harsh divinity,", :no => 328, :created_at => "2013-03-04 14:37:08", :updated_at => "2013-03-04 14:37:08", :stanza_id => 82 },
  { :line => "Bound to his throne, he waited unappeased", :no => 329, :created_at => "2013-03-04 14:37:08", :updated_at => "2013-03-04 14:37:08", :stanza_id => 82 },
  { :line => "The daily oblation of her unwept tears.", :no => 330, :created_at => "2013-03-04 14:37:08", :updated_at => "2013-03-04 14:37:08", :stanza_id => 82 },
  { :line => "All the fierce question of manâ€™s hours relived.", :no => 331, :created_at => "2013-03-04 14:37:09", :updated_at => "2013-03-04 14:37:09", :stanza_id => 83 },
  { :line => "The sacrifice of suffering and desire", :no => 332, :created_at => "2013-03-04 14:37:09", :updated_at => "2013-03-04 14:37:09", :stanza_id => 84 },
  { :line => "Earth offers to the immortal Ecstasy", :no => 333, :created_at => "2013-03-04 14:37:10", :updated_at => "2013-03-04 14:37:10", :stanza_id => 84 },
  { :line => "Began again beneath the eternal Hand.", :no => 334, :created_at => "2013-03-04 14:37:10", :updated_at => "2013-03-04 14:37:10", :stanza_id => 84 },
  { :line => "Awake she endured the momentsâ€™ serried march", :no => 335, :created_at => "2013-03-04 14:37:10", :updated_at => "2013-03-04 14:37:10", :stanza_id => 85 },
  { :line => "And looked on this green smiling dangerous world,", :no => 336, :created_at => "2013-03-04 14:37:11", :updated_at => "2013-03-04 14:37:11", :stanza_id => 85 },
  { :line => "And heard the ignorant cry of living things.", :no => 337, :created_at => "2013-03-04 14:37:11", :updated_at => "2013-03-04 14:37:11", :stanza_id => 85 },
  { :line => "Amid the trivial sounds, the unchanging scene", :no => 338, :created_at => "2013-03-04 14:37:11", :updated_at => "2013-03-04 14:37:11", :stanza_id => 86 },
  { :line => "Her soul arose confronting Time and Fate.", :no => 339, :created_at => "2013-03-04 14:37:11", :updated_at => "2013-03-04 14:37:11", :stanza_id => 86 },
  { :line => "Immobile in herself, she gathered force.", :no => 340, :created_at => "2013-03-04 14:37:12", :updated_at => "2013-03-04 14:37:12", :stanza_id => 87 },
  { :line => "This was the day when Satyavan must die.", :no => 341, :created_at => "2013-03-04 14:37:12", :updated_at => "2013-03-04 14:37:12", :stanza_id => 88 },
  { :line => "Awhile, withdrawn in secret fields of thought,\n", :no => 342, :created_at => "2013-03-04 14:37:12", :updated_at => "2013-03-04 14:37:12", :stanza_id => 89 },
  { :line => "Her mind moved in a many-imaged past", :no => 343, :created_at => "2013-03-04 14:37:12", :updated_at => "2013-03-04 14:37:12", :stanza_id => 89 },
  { :line => "That lived again and saw its end approach:", :no => 344, :created_at => "2013-03-04 14:37:12", :updated_at => "2013-03-04 14:37:12", :stanza_id => 89 },
  { :line => "Dying, it lived imperishably in her;", :no => 345, :created_at => "2013-03-04 14:37:12", :updated_at => "2013-03-04 14:37:12", :stanza_id => 89 },
  { :line => "Transient and vanishing from transient eyes,", :no => 346, :created_at => "2013-03-04 14:37:12", :updated_at => "2013-03-04 14:37:12", :stanza_id => 89 },
  { :line => "Invisible, a fateful ghost of self,", :no => 347, :created_at => "2013-03-04 14:37:12", :updated_at => "2013-03-04 14:37:12", :stanza_id => 89 },
  { :line => "It bore the future on its phantom breast.", :no => 348, :created_at => "2013-03-04 14:37:12", :updated_at => "2013-03-04 14:37:12", :stanza_id => 89 },
  { :line => "Along the fleeting eventâ€™s far-backward trail", :no => 349, :created_at => "2013-03-05 06:49:18", :updated_at => "2013-03-05 06:49:18", :stanza_id => 91 },
  { :line => "Regressed the stream of the insistent hours,", :no => 350, :created_at => "2013-03-05 06:49:19", :updated_at => "2013-03-05 06:49:19", :stanza_id => 91 },
  { :line => "And on the bank of the mysterious flood", :no => 351, :created_at => "2013-03-05 06:49:19", :updated_at => "2013-03-05 06:49:19", :stanza_id => 91 },
  { :line => "Peopled with well-loved forms now seen no more", :no => 352, :created_at => "2013-03-05 06:49:19", :updated_at => "2013-03-05 06:49:19", :stanza_id => 91 },
  { :line => "And the subtle images of things that were,", :no => 353, :created_at => "2013-03-05 06:49:19", :updated_at => "2013-03-05 06:49:19", :stanza_id => 91 },
  { :line => "Her witness spirit stood reviewing Time.", :no => 354, :created_at => "2013-03-05 06:49:20", :updated_at => "2013-03-05 06:49:20", :stanza_id => 91 },
  { :line => "All that she once had hoped and dreamed and been,", :no => 355, :created_at => "2013-03-05 06:49:20", :updated_at => "2013-03-05 06:49:20", :stanza_id => 92 },
  { :line => "Flew past her eagle-winged through memoryâ€™s skies.", :no => 356, :created_at => "2013-03-05 06:49:20", :updated_at => "2013-03-05 06:49:20", :stanza_id => 92 },
  { :line => "As in a many-hued flaming inner dawn,", :no => 357, :created_at => "2013-03-05 06:49:20", :updated_at => "2013-03-05 06:49:20", :stanza_id => 93 },
  { :line => "Her lifeâ€™s broad highways and its sweet bypaths", :no => 358, :created_at => "2013-03-05 06:49:21", :updated_at => "2013-03-05 06:49:21", :stanza_id => 93 },
  { :line => "Lay mapped to her sun-clear recording view,", :no => 359, :created_at => "2013-03-05 06:49:21", :updated_at => "2013-03-05 06:49:21", :stanza_id => 93 },
  { :line => "From the bright country of her childhoodâ€™s days", :no => 360, :created_at => "2013-03-05 06:49:21", :updated_at => "2013-03-05 06:49:21", :stanza_id => 93 },
  { :line => "And the blue mountains of her soaring youth", :no => 361, :created_at => "2013-03-05 06:49:21", :updated_at => "2013-03-05 06:49:21", :stanza_id => 93 },
  { :line => "And the paradise groves and peacock wings of Love", :no => 362, :created_at => "2013-03-05 06:49:21", :updated_at => "2013-03-05 06:49:21", :stanza_id => 93 },
  { :line => "To joy clutched under the silent shadow of doom", :no => 363, :created_at => "2013-03-05 06:49:22", :updated_at => "2013-03-05 06:49:22", :stanza_id => 93 },
  { :line => "In a last turn where heaven raced with hell.", :no => 364, :created_at => "2013-03-05 06:49:22", :updated_at => "2013-03-05 06:49:22", :stanza_id => 93 },
  { :line => "Twelve passionate months led in a day of fate.", :no => 365, :created_at => "2013-03-05 06:49:22", :updated_at => "2013-03-05 06:49:22", :stanza_id => 94 },
  { :line => "An absolute supernatural darkness falls", :no => 366, :created_at => "2013-03-05 06:49:23", :updated_at => "2013-03-05 06:49:23", :stanza_id => 95 },
  { :line => "On man sometimes when he draws near to God:", :no => 367, :created_at => "2013-03-05 06:49:23", :updated_at => "2013-03-05 06:49:23", :stanza_id => 95 },
  { :line => "An hour comes when fail all Natureâ€™s means;", :no => 368, :created_at => "2013-03-05 06:49:23", :updated_at => "2013-03-05 06:49:23", :stanza_id => 95 },
  { :line => "Forced out from the protecting Ignorance", :no => 369, :created_at => "2013-03-05 06:49:23", :updated_at => "2013-03-05 06:49:23", :stanza_id => 95 },
  { :line => "And flung back on his naked primal need,", :no => 370, :created_at => "2013-03-05 06:49:23", :updated_at => "2013-03-05 06:49:23", :stanza_id => 95 },
  { :line => "He at length must cast from him his surface soul", :no => 371, :created_at => "2013-03-05 06:49:24", :updated_at => "2013-03-05 06:49:24", :stanza_id => 95 },
  { :line => "And be the ungarbed entity within:", :no => 372, :created_at => "2013-03-05 06:49:24", :updated_at => "2013-03-05 06:49:24", :stanza_id => 95 },
  { :line => "That hour had fallen now on Savitri.", :no => 373, :created_at => "2013-03-05 06:49:24", :updated_at => "2013-03-05 06:49:24", :stanza_id => 95 },
  { :line => "A point she had reached where life must be in vain", :no => 374, :created_at => "2013-03-05 06:49:24", :updated_at => "2013-03-05 06:49:24", :stanza_id => 96 },
  { :line => "Or, in her unborn element awake,", :no => 375, :created_at => "2013-03-05 06:49:25", :updated_at => "2013-03-05 06:49:25", :stanza_id => 96 },
  { :line => "Her will must cancel her bodyâ€™s destiny.", :no => 376, :created_at => "2013-03-05 06:49:25", :updated_at => "2013-03-05 06:49:25", :stanza_id => 96 },
  { :line => "For only the unborn spiritâ€™s timeless power", :no => 377, :created_at => "2013-03-05 06:49:25", :updated_at => "2013-03-05 06:49:25", :stanza_id => 97 },
  { :line => "Can lift the yoke imposed by birth in time.", :no => 378, :created_at => "2013-03-05 06:49:25", :updated_at => "2013-03-05 06:49:25", :stanza_id => 97 },
  { :line => "Only the Self that builds this figure of self", :no => 379, :created_at => "2013-03-05 06:49:26", :updated_at => "2013-03-05 06:49:26", :stanza_id => 98 },
  { :line => "Can rase the fixed interminable line", :no => 380, :created_at => "2013-03-05 06:49:26", :updated_at => "2013-03-05 06:49:26", :stanza_id => 98 },
  { :line => "That joins these changing names, these numberless lives,", :no => 381, :created_at => "2013-03-05 06:49:26", :updated_at => "2013-03-05 06:49:26", :stanza_id => 98 },
  { :line => "These new oblivious personalities", :no => 382, :created_at => "2013-03-05 06:49:26", :updated_at => "2013-03-05 06:49:26", :stanza_id => 98 },
  { :line => "And keeps still lurking in our conscious acts", :no => 383, :created_at => "2013-03-05 06:49:27", :updated_at => "2013-03-05 06:49:27", :stanza_id => 98 },
  { :line => "The trail of old forgotten thoughts and deeds,", :no => 384, :created_at => "2013-03-05 06:49:27", :updated_at => "2013-03-05 06:49:27", :stanza_id => 98 },
  { :line => "Disown the legacy of our buried selves,", :no => 385, :created_at => "2013-03-05 06:49:27", :updated_at => "2013-03-05 06:49:27", :stanza_id => 98 },
  { :line => "The burdensome heirship to our vanished forms", :no => 386, :created_at => "2013-03-05 06:49:27", :updated_at => "2013-03-05 06:49:27", :stanza_id => 98 },
  { :line => "Accepted blindly by the body and soul.", :no => 387, :created_at => "2013-03-05 06:49:27", :updated_at => "2013-03-05 06:49:27", :stanza_id => 98 },
  { :line => "An episode in an unremembered tale,", :no => 388, :created_at => "2013-03-05 06:49:28", :updated_at => "2013-03-05 06:49:28", :stanza_id => 99 },
  { :line => "Its beginning lost, its motive and plot concealed,", :no => 389, :created_at => "2013-03-05 06:49:28", :updated_at => "2013-03-05 06:49:28", :stanza_id => 99 },
  { :line => "A once living story has prepared and made", :no => 390, :created_at => "2013-03-05 06:49:28", :updated_at => "2013-03-05 06:49:28", :stanza_id => 99 },
  { :line => "Our present fate, child of past energies.", :no => 391, :created_at => "2013-03-05 06:49:29", :updated_at => "2013-03-05 06:49:29", :stanza_id => 99 },
  { :line => "The fixity of the cosmic sequences", :no => 392, :created_at => "2013-03-05 06:49:29", :updated_at => "2013-03-05 06:49:29", :stanza_id => 100 },
  { :line => "Fastened with hidden inevitable links", :no => 393, :created_at => "2013-03-05 06:49:29", :updated_at => "2013-03-05 06:49:29", :stanza_id => 100 },
  { :line => "She must disrupt, dislodge by her soulâ€™s force", :no => 394, :created_at => "2013-03-05 06:49:29", :updated_at => "2013-03-05 06:49:29", :stanza_id => 100 },
  { :line => "Her past, a block on the immortalâ€™s road,", :no => 395, :created_at => "2013-03-05 06:49:30", :updated_at => "2013-03-05 06:49:30", :stanza_id => 100 },
  { :line => "Make a rased ground and shape anew her fate.", :no => 396, :created_at => "2013-03-05 06:49:30", :updated_at => "2013-03-05 06:49:30", :stanza_id => 100 },
  { :line => "A colloquy of the original Gods", :no => 397, :created_at => "2013-03-05 06:49:30", :updated_at => "2013-03-05 06:49:30", :stanza_id => 101 },
  { :line => "Meeting upon the borders of the unknown,", :no => 398, :created_at => "2013-03-05 06:49:30", :updated_at => "2013-03-05 06:49:30", :stanza_id => 101 },
  { :line => "Her soulâ€™s debate with embodied Nothingness", :no => 399, :created_at => "2013-03-05 06:49:30", :updated_at => "2013-03-05 06:49:30", :stanza_id => 101 },
  { :line => "Must be wrestled out on a dangerous dim background:", :no => 400, :created_at => "2013-03-05 06:49:31", :updated_at => "2013-03-05 06:49:31", :stanza_id => 101 },
  { :line => "Her being must confront its formless Cause,", :no => 401, :created_at => "2013-03-05 06:49:31", :updated_at => "2013-03-05 06:49:31", :stanza_id => 101 },
  { :line => "Against the universe weigh its single self.", :no => 402, :created_at => "2013-03-05 06:49:31", :updated_at => "2013-03-05 06:49:31", :stanza_id => 101 },
  { :line => "On the bare peak where Self is alone with Nought", :no => 403, :created_at => "2013-03-05 06:49:31", :updated_at => "2013-03-05 06:49:31", :stanza_id => 102 },
  { :line => "And life has no sense and love no place to stand,", :no => 404, :created_at => "2013-03-05 06:49:32", :updated_at => "2013-03-05 06:49:32", :stanza_id => 102 },
  { :line => "She must plead her case upon extinctionâ€™s verge,", :no => 405, :created_at => "2013-03-05 06:49:32", :updated_at => "2013-03-05 06:49:32", :stanza_id => 102 },
  { :line => "In the worldâ€™s death-cave uphold lifeâ€™s helpless claim", :no => 406, :created_at => "2013-03-05 06:49:32", :updated_at => "2013-03-05 06:49:32", :stanza_id => 102 },
  { :line => "And vindicate her right to be and love.", :no => 407, :created_at => "2013-03-05 06:49:32", :updated_at => "2013-03-05 06:49:32", :stanza_id => 102 },
  { :line => "Altered must be Natureâ€™s harsh economy;", :no => 408, :created_at => "2013-03-05 06:49:32", :updated_at => "2013-03-05 06:49:32", :stanza_id => 103 },
  { :line => "Acquittance she must win from her pastâ€™s bond,", :no => 409, :created_at => "2013-03-05 06:49:33", :updated_at => "2013-03-05 06:49:33", :stanza_id => 103 },
  { :line => "An old account of suffering exhaust,", :no => 410, :created_at => "2013-03-05 06:49:33", :updated_at => "2013-03-05 06:49:33", :stanza_id => 103 },
  { :line => "Strike out from Time the soulâ€™s long compound debt", :no => 411, :created_at => "2013-03-05 06:49:33", :updated_at => "2013-03-05 06:49:33", :stanza_id => 103 },
  { :line => "And the heavy servitudes of the Karmic Gods,", :no => 412, :created_at => "2013-03-05 06:49:33", :updated_at => "2013-03-05 06:49:33", :stanza_id => 103 },
  { :line => "The slow revenge of unforgiving Law", :no => 413, :created_at => "2013-03-05 06:49:33", :updated_at => "2013-03-05 06:49:33", :stanza_id => 103 },
  { :line => "And the deep need of universal pain", :no => 414, :created_at => "2013-03-05 06:49:34", :updated_at => "2013-03-05 06:49:34", :stanza_id => 103 },
  { :line => "And hard sacrifice and tragic consequence.", :no => 415, :created_at => "2013-03-05 06:49:34", :updated_at => "2013-03-05 06:49:34", :stanza_id => 103 },
  { :line => "Out of a timeless barrier she must break,", :no => 416, :created_at => "2013-03-05 06:49:34", :updated_at => "2013-03-05 06:49:34", :stanza_id => 104 },
  { :line => "Penetrate with her thinking depths the Voidâ€™s monstrous hush,", :no => 417, :created_at => "2013-03-05 06:49:35", :updated_at => "2013-03-05 06:49:35", :stanza_id => 104 },
  { :line => "Look into the lonely eyes of immortal Death", :no => 418, :created_at => "2013-03-05 06:49:35", :updated_at => "2013-03-05 06:49:35", :stanza_id => 104 },
  { :line => "And with her nude spirit measure the Infiniteâ€™s night.", :no => 419, :created_at => "2013-03-05 06:49:35", :updated_at => "2013-03-05 06:49:35", :stanza_id => 104 },
  { :line => "The great and dolorous moment now was close.", :no => 420, :created_at => "2013-03-05 06:49:35", :updated_at => "2013-03-05 06:49:35", :stanza_id => 105 },
  { :line => "A mailed battalion marching to its doom,", :no => 421, :created_at => "2013-03-05 06:49:36", :updated_at => "2013-03-05 06:49:36", :stanza_id => 106 },
  { :line => "The last long days went by with heavy tramp,", :no => 422, :created_at => "2013-03-05 06:49:36", :updated_at => "2013-03-05 06:49:36", :stanza_id => 106 },
  { :line => "Long but too soon to pass, too near the end.", :no => 423, :created_at => "2013-03-05 06:49:36", :updated_at => "2013-03-05 06:49:36", :stanza_id => 106 },
  { :line => "Alone amid the many faces loved,", :no => 424, :created_at => "2013-03-05 06:49:36", :updated_at => "2013-03-05 06:49:36", :stanza_id => 107 },
  { :line => "Aware among unknowing happy hearts,", :no => 425, :created_at => "2013-03-05 06:49:37", :updated_at => "2013-03-05 06:49:37", :stanza_id => 107 },
  { :line => "Her armoured spirit kept watch upon the hours", :no => 426, :created_at => "2013-03-05 06:49:37", :updated_at => "2013-03-05 06:49:37", :stanza_id => 107 },
  { :line => "Listening for a foreseen tremendous step", :no => 427, :created_at => "2013-03-05 06:49:37", :updated_at => "2013-03-05 06:49:37", :stanza_id => 107 },
  { :line => "In the closed beauty of the inhuman wilds.", :no => 428, :created_at => "2013-03-05 06:49:37", :updated_at => "2013-03-05 06:49:37", :stanza_id => 107 },
  { :line => "A combatant in silent dreadful lists,", :no => 429, :created_at => "2013-03-05 06:49:38", :updated_at => "2013-03-05 06:49:38", :stanza_id => 108 },
  { :line => "The world unknowing, for the world she stood:", :no => 430, :created_at => "2013-03-05 06:49:38", :updated_at => "2013-03-05 06:49:38", :stanza_id => 108 },
  { :line => "No helper had she save the Strength within;", :no => 431, :created_at => "2013-03-05 06:49:38", :updated_at => "2013-03-05 06:49:38", :stanza_id => 108 },
  { :line => "There was no witness of terrestrial eyes;", :no => 432, :created_at => "2013-03-05 06:49:38", :updated_at => "2013-03-05 06:49:38", :stanza_id => 108 },
  { :line => "The Gods above and Natureâ€™s soul below", :no => 433, :created_at => "2013-03-05 06:49:38", :updated_at => "2013-03-05 06:49:38", :stanza_id => 108 },
  { :line => "Were the spectators of that mighty strife.", :no => 434, :created_at => "2013-03-05 06:49:39", :updated_at => "2013-03-05 06:49:39", :stanza_id => 108 },
  { :line => "Around her were the austere sky-pointing hills,", :no => 435, :created_at => "2013-03-05 06:49:39", :updated_at => "2013-03-05 06:49:39", :stanza_id => 109 },
  { :line => "And the green murmurous broad deep-thoughted woods", :no => 436, :created_at => "2013-03-05 06:49:39", :updated_at => "2013-03-05 06:49:39", :stanza_id => 109 },
  { :line => "Muttered incessantly their muffled spell.", :no => 437, :created_at => "2013-03-05 06:49:39", :updated_at => "2013-03-05 06:49:39", :stanza_id => 109 },
  { :line => "A dense magnificent coloured self-wrapped life", :no => 438, :created_at => "2013-03-05 06:49:40", :updated_at => "2013-03-05 06:49:40", :stanza_id => 110 },
  { :line => "Draped in the leavesâ€™ vivid emerald monotone", :no => 439, :created_at => "2013-03-05 06:49:40", :updated_at => "2013-03-05 06:49:40", :stanza_id => 110 },
  { :line => "And set with chequered sunbeams and blithe flowers", :no => 440, :created_at => "2013-03-05 06:49:40", :updated_at => "2013-03-05 06:49:40", :stanza_id => 110 },
  { :line => "Immured her destinyâ€™s secluded scene.", :no => 441, :created_at => "2013-03-05 06:49:40", :updated_at => "2013-03-05 06:49:40", :stanza_id => 110 },
  { :line => "There had she grown to the stature of her spirit:", :no => 442, :created_at => "2013-03-05 06:49:41", :updated_at => "2013-03-05 06:49:41", :stanza_id => 111 },
  { :line => "The genius of titanic silences", :no => 443, :created_at => "2013-03-05 06:49:41", :updated_at => "2013-03-05 06:49:41", :stanza_id => 111 },
  { :line => "Steeping her soul in its wide loneliness", :no => 444, :created_at => "2013-03-05 06:49:41", :updated_at => "2013-03-05 06:49:41", :stanza_id => 111 },
  { :line => "Had shown to her her selfâ€™s bare reality", :no => 445, :created_at => "2013-03-05 06:49:41", :updated_at => "2013-03-05 06:49:41", :stanza_id => 111 },
  { :line => "And mated her with her environment.", :no => 446, :created_at => "2013-03-05 06:49:42", :updated_at => "2013-03-05 06:49:42", :stanza_id => 111 },
  { :line => "Its solitude greatened her human hours", :no => 447, :created_at => "2013-03-05 06:49:42", :updated_at => "2013-03-05 06:49:42", :stanza_id => 112 },
  { :line => "With a background of the eternal and unique.", :no => 448, :created_at => "2013-03-05 06:49:42", :updated_at => "2013-03-05 06:49:42", :stanza_id => 112 },
  { :line => "A force of spare direct necessity", :no => 449, :created_at => "2013-03-05 06:49:43", :updated_at => "2013-03-05 06:49:43", :stanza_id => 113 },
  { :line => "Reduced the heavy framework of manâ€™s days", :no => 450, :created_at => "2013-03-05 06:49:43", :updated_at => "2013-03-05 06:49:43", :stanza_id => 113 },
  { :line => "And his overburdening mass of outward needs", :no => 451, :created_at => "2013-03-05 06:49:43", :updated_at => "2013-03-05 06:49:43", :stanza_id => 113 },
  { :line => "To a first thin strip of simple animal wants,", :no => 452, :created_at => "2013-03-05 06:49:43", :updated_at => "2013-03-05 06:49:43", :stanza_id => 113 },
  { :line => "And the mighty wideness of the primitive earth", :no => 453, :created_at => "2013-03-05 06:49:44", :updated_at => "2013-03-05 06:49:44", :stanza_id => 113 },
  { :line => "And the brooding multitude of patient trees", :no => 454, :created_at => "2013-03-05 06:49:44", :updated_at => "2013-03-05 06:49:44", :stanza_id => 113 },
  { :line => "And the musing sapphire leisure of the sky", :no => 455, :created_at => "2013-03-05 06:49:44", :updated_at => "2013-03-05 06:49:44", :stanza_id => 113 },
  { :line => "And the solemn weight of the slowly passing months", :no => 456, :created_at => "2013-03-05 06:49:44", :updated_at => "2013-03-05 06:49:44", :stanza_id => 113 },
  { :line => "Had left in her deep room for thought and God.", :no => 457, :created_at => "2013-03-05 06:49:44", :updated_at => "2013-03-05 06:49:44", :stanza_id => 113 },
  { :line => "There was her dramaâ€™s radiant prologue lived.", :no => 458, :created_at => "2013-03-05 06:49:45", :updated_at => "2013-03-05 06:49:45", :stanza_id => 114 },
  { :line => "A spot for the eternalâ€™s tread on earth", :no => 459, :created_at => "2013-03-05 06:49:45", :updated_at => "2013-03-05 06:49:45", :stanza_id => 115 },
  { :line => "Set in the cloistral yearning of the woods", :no => 460, :created_at => "2013-03-05 06:49:45", :updated_at => "2013-03-05 06:49:45", :stanza_id => 115 },
  { :line => "And watched by the aspiration of the peaks", :no => 461, :created_at => "2013-03-05 06:49:46", :updated_at => "2013-03-05 06:49:46", :stanza_id => 115 },
  { :line => "Appeared through an aureate opening in Time", :no => 462, :created_at => "2013-03-05 06:49:46", :updated_at => "2013-03-05 06:49:46", :stanza_id => 115 },
  { :line => "Where stillness listening felt the unspoken word", :no => 463, :created_at => "2013-03-05 06:49:46", :updated_at => "2013-03-05 06:49:46", :stanza_id => 115 },
  { :line => "And the hours forgot to pass towards grief and change.", :no => 464, :created_at => "2013-03-05 06:49:46", :updated_at => "2013-03-05 06:49:46", :stanza_id => 115 },
  { :line => "Here with the suddenness divine advents have,", :no => 465, :created_at => "2013-03-05 06:49:47", :updated_at => "2013-03-05 06:49:47", :stanza_id => 116 },
  { :line => "Repeating the marvel of the first descent,", :no => 466, :created_at => "2013-03-05 06:49:47", :updated_at => "2013-03-05 06:49:47", :stanza_id => 116 },
  { :line => "Changing to rapture the dull earthly round,", :no => 467, :created_at => "2013-03-05 06:49:47", :updated_at => "2013-03-05 06:49:47", :stanza_id => 116 },
  { :line => "Love came to her hiding the shadow, Death.", :no => 468, :created_at => "2013-03-05 06:49:47", :updated_at => "2013-03-05 06:49:47", :stanza_id => 116 },
  { :line => "Well might he find in her his perfect shrine.", :no => 469, :created_at => "2013-03-05 06:49:48", :updated_at => "2013-03-05 06:49:48", :stanza_id => 117 },
  { :line => "Since first the earth-beingâ€™s heavenward growth began,", :no => 470, :created_at => "2013-03-05 06:49:48", :updated_at => "2013-03-05 06:49:48", :stanza_id => 118 },
  { :line => "Through all the long ordeal of the race,", :no => 471, :created_at => "2013-03-05 06:49:48", :updated_at => "2013-03-05 06:49:48", :stanza_id => 118 },
  { :line => "Never a rarer creature bore his shaft,", :no => 472, :created_at => "2013-03-05 06:49:49", :updated_at => "2013-03-05 06:49:49", :stanza_id => 118 },
  { :line => "That burning test of the godhead in our parts,", :no => 473, :created_at => "2013-03-05 06:49:49", :updated_at => "2013-03-05 06:49:49", :stanza_id => 118 },
  { :line => "A lightning from the heights on our abyss.", :no => 474, :created_at => "2013-03-05 06:49:49", :updated_at => "2013-03-05 06:49:49", :stanza_id => 118 },
  { :line => "All in her pointed to a nobler kind.", :no => 475, :created_at => "2013-03-05 06:49:49", :updated_at => "2013-03-05 06:49:49", :stanza_id => 119 },
  { :line => "Near to earthâ€™s wideness, intimate with heaven,", :no => 476, :created_at => "2013-03-05 06:49:50", :updated_at => "2013-03-05 06:49:50", :stanza_id => 120 },
  { :line => "Exalted and swift her young large-visioned spirit", :no => 477, :created_at => "2013-03-05 06:49:50", :updated_at => "2013-03-05 06:49:50", :stanza_id => 120 },
  { :line => "Voyaging through worlds of splendour and of calm", :no => 478, :created_at => "2013-03-05 06:49:51", :updated_at => "2013-03-05 06:49:51", :stanza_id => 120 },
  { :line => "Overflew the ways of Thought to unborn things.", :no => 479, :created_at => "2013-03-05 06:49:51", :updated_at => "2013-03-05 06:49:51", :stanza_id => 120 },
  { :line => "Ardent was her self-poised unstumbling will;", :no => 480, :created_at => "2013-03-05 06:49:51", :updated_at => "2013-03-05 06:49:51", :stanza_id => 121 },
  { :line => "Her mind, a sea of white sincerity,", :no => 481, :created_at => "2013-03-05 06:49:51", :updated_at => "2013-03-05 06:49:51", :stanza_id => 121 },
  { :line => "Passionate in flow, had not one turbid wave.", :no => 482, :created_at => "2013-03-05 06:49:52", :updated_at => "2013-03-05 06:49:52", :stanza_id => 121 },
  { :line => "As in a mystic and dynamic dance", :no => 483, :created_at => "2013-03-05 06:49:52", :updated_at => "2013-03-05 06:49:52", :stanza_id => 122 },
  { :line => "A priestess of immaculate ecstasies", :no => 484, :created_at => "2013-03-05 06:49:52", :updated_at => "2013-03-05 06:49:52", :stanza_id => 122 },
  { :line => "Inspired and ruled from Truthâ€™s revealing vault", :no => 485, :created_at => "2013-03-05 06:49:52", :updated_at => "2013-03-05 06:49:52", :stanza_id => 122 },
  { :line => "Moves in some prophet cavern of the gods,", :no => 486, :created_at => "2013-03-05 06:49:53", :updated_at => "2013-03-05 06:49:53", :stanza_id => 122 },
  { :line => "A heart of silence in the hands of joy", :no => 487, :created_at => "2013-03-05 06:49:53", :updated_at => "2013-03-05 06:49:53", :stanza_id => 122 },
  { :line => "Inhabited with rich creative beats", :no => 488, :created_at => "2013-03-05 06:49:53", :updated_at => "2013-03-05 06:49:53", :stanza_id => 122 },
  { :line => "A body like a parable of dawn", :no => 489, :created_at => "2013-03-05 06:49:53", :updated_at => "2013-03-05 06:49:53", :stanza_id => 122 },
  { :line => "That seemed a niche for veiled divinity", :no => 490, :created_at => "2013-03-05 06:49:53", :updated_at => "2013-03-05 06:49:53", :stanza_id => 122 },
  { :line => "Or golden temple door to things beyond.", :no => 491, :created_at => "2013-03-05 06:49:54", :updated_at => "2013-03-05 06:49:54", :stanza_id => 122 },
  { :line => "Immortal rhythms swayed in her time-born steps;", :no => 492, :created_at => "2013-03-05 06:49:54", :updated_at => "2013-03-05 06:49:54", :stanza_id => 123 },
  { :line => "Her look, her smile awoke celestial sense", :no => 493, :created_at => "2013-03-05 06:49:54", :updated_at => "2013-03-05 06:49:54", :stanza_id => 123 },
  { :line => "Even in earth-stuff, and their intense delight", :no => 494, :created_at => "2013-03-05 06:49:54", :updated_at => "2013-03-05 06:49:54", :stanza_id => 123 },
  { :line => "Poured a supernal beauty on menâ€™s lives.", :no => 495, :created_at => "2013-03-05 06:49:54", :updated_at => "2013-03-05 06:49:54", :stanza_id => 123 },
  { :line => "A wide self-giving was her native act;", :no => 496, :created_at => "2013-03-05 06:49:55", :updated_at => "2013-03-05 06:49:55", :stanza_id => 124 },
  { :line => "A magnanimity as of sea or sky", :no => 497, :created_at => "2013-03-05 06:49:55", :updated_at => "2013-03-05 06:49:55", :stanza_id => 124 },
  { :line => "Enveloped with its greatness all that came", :no => 498, :created_at => "2013-03-05 06:49:55", :updated_at => "2013-03-05 06:49:55", :stanza_id => 124 },
  { :line => "And gave a sense as of a greatened world:", :no => 499, :created_at => "2013-03-05 06:49:55", :updated_at => "2013-03-05 06:49:55", :stanza_id => 124 },
  { :line => "Her kindly care was a sweet temperate sun,", :no => 500, :created_at => "2013-03-05 06:49:56", :updated_at => "2013-03-05 06:49:56", :stanza_id => 124 },
  { :line => "Her high passion a blue heavenâ€™s equipoise.", :no => 501, :created_at => "2013-03-05 06:49:56", :updated_at => "2013-03-05 06:49:56", :stanza_id => 124 },
  { :line => "As might a soul fly like a hunted bird,", :no => 502, :created_at => "2013-03-05 06:49:56", :updated_at => "2013-03-05 06:49:56", :stanza_id => 125 },
  { :line => "Escaping with tired wings from a world of storms,", :no => 503, :created_at => "2013-03-05 06:49:57", :updated_at => "2013-03-05 06:49:57", :stanza_id => 125 },
  { :line => "And a quiet reach like a remembered breast,", :no => 504, :created_at => "2013-03-05 06:49:57", :updated_at => "2013-03-05 06:49:57", :stanza_id => 125 },
  { :line => "In a haven of safety and splendid soft repose", :no => 505, :created_at => "2013-03-05 06:49:57", :updated_at => "2013-03-05 06:49:57", :stanza_id => 125 },
  { :line => "One could drink life back in streams of honey-fire,", :no => 506, :created_at => "2013-03-05 06:49:57", :updated_at => "2013-03-05 06:49:57", :stanza_id => 125 },
  { :line => "Recover the lost habit of happiness,", :no => 507, :created_at => "2013-03-05 06:49:57", :updated_at => "2013-03-05 06:49:57", :stanza_id => 125 },
  { :line => "Feel her bright natureâ€™s glorious ambiance,", :no => 508, :created_at => "2013-03-05 06:49:58", :updated_at => "2013-03-05 06:49:58", :stanza_id => 125 },
  { :line => "And preen joy in her warmth and colourâ€™s rule.", :no => 509, :created_at => "2013-03-05 06:49:58", :updated_at => "2013-03-05 06:49:58", :stanza_id => 125 },
  { :line => "A deep of compassion, a hushed sanctuary,", :no => 510, :created_at => "2013-03-05 06:49:58", :updated_at => "2013-03-05 06:49:58", :stanza_id => 126 },
  { :line => "Her inward help unbarred a gate in heaven;", :no => 511, :created_at => "2013-03-05 06:49:58", :updated_at => "2013-03-05 06:49:58", :stanza_id => 126 },
  { :line => "Love in her was wider than the universe,", :no => 512, :created_at => "2013-03-05 06:49:59", :updated_at => "2013-03-05 06:49:59", :stanza_id => 126 },
  { :line => "The whole world could take refuge in her single heart.", :no => 513, :created_at => "2013-03-05 06:49:59", :updated_at => "2013-03-05 06:49:59", :stanza_id => 126 },
  { :line => "The great unsatisfied godhead here could dwell:", :no => 514, :created_at => "2013-03-05 06:49:59", :updated_at => "2013-03-05 06:49:59", :stanza_id => 127 },
  { :line => "Vacant of the dwarf selfâ€™s imprisoned air", :no => 515, :created_at => "2013-03-05 06:49:59", :updated_at => "2013-03-05 06:49:59", :stanza_id => 127 },
  { :line => "Her mood could harbour his sublimer breath", :no => 516, :created_at => "2013-03-05 06:49:59", :updated_at => "2013-03-05 06:49:59", :stanza_id => 127 },
  { :line => "Spiritual that can make all things divine.", :no => 517, :created_at => "2013-03-05 06:50:00", :updated_at => "2013-03-05 06:50:00", :stanza_id => 127 },
  { :line => "For even her gulfs were secrecies of light.", :no => 518, :created_at => "2013-03-05 06:50:00", :updated_at => "2013-03-05 06:50:00", :stanza_id => 128 },
  { :line => "At once she was the stillness and the word,", :no => 519, :created_at => "2013-03-05 06:50:00", :updated_at => "2013-03-05 06:50:00", :stanza_id => 129 },
  { :line => "A continent of self-diffusing peace,", :no => 520, :created_at => "2013-03-05 06:50:01", :updated_at => "2013-03-05 06:50:01", :stanza_id => 129 },
  { :line => "An ocean of untrembling virgin fire:", :no => 521, :created_at => "2013-03-05 06:50:01", :updated_at => "2013-03-05 06:50:01", :stanza_id => 129 },
  { :line => "The strength, the silence of the gods were hers.", :no => 522, :created_at => "2013-03-05 06:50:01", :updated_at => "2013-03-05 06:50:01", :stanza_id => 129 },
  { :line => "In her he found a vastness like his own,", :no => 523, :created_at => "2013-03-05 06:50:02", :updated_at => "2013-03-05 06:50:02", :stanza_id => 130 },
  { :line => "His high warm subtle ether he refound", :no => 524, :created_at => "2013-03-05 06:50:02", :updated_at => "2013-03-05 06:50:02", :stanza_id => 130 },
  { :line => "And moved in her as in his natural home.", :no => 525, :created_at => "2013-03-05 06:50:02", :updated_at => "2013-03-05 06:50:02", :stanza_id => 130 },
  { :line => "In her he met his own eternity.", :no => 526, :created_at => "2013-03-05 06:50:02", :updated_at => "2013-03-05 06:50:02", :stanza_id => 131 },
  { :line => "Till then no mournful line had barred this ray.", :no => 527, :created_at => "2013-03-05 06:50:03", :updated_at => "2013-03-05 06:50:03", :stanza_id => 132 },
  { :line => "On the frail breast of this precarious earth,", :no => 528, :created_at => "2013-03-05 06:50:03", :updated_at => "2013-03-05 06:50:03", :stanza_id => 133 },
  { :line => "Since her orbed sight in its breath-fastened house,", :no => 529, :created_at => "2013-03-05 06:50:03", :updated_at => "2013-03-05 06:50:03", :stanza_id => 133 },
  { :line => "Opening in sympathy with happier stars", :no => 530, :created_at => "2013-03-05 06:50:03", :updated_at => "2013-03-05 06:50:03", :stanza_id => 133 },
  { :line => "Where life is not exposed to sorrowful change,", :no => 531, :created_at => "2013-03-05 06:50:04", :updated_at => "2013-03-05 06:50:04", :stanza_id => 133 },
  { :line => "Remembered beauty death-claimed lids ignore", :no => 532, :created_at => "2013-03-05 06:50:04", :updated_at => "2013-03-05 06:50:04", :stanza_id => 133 },
  { :line => "And wondered at this world of fragile forms", :no => 533, :created_at => "2013-03-05 06:50:04", :updated_at => "2013-03-05 06:50:04", :stanza_id => 133 },
  { :line => "Carried on canvas-strips of shimmering Time,", :no => 534, :created_at => "2013-03-05 06:50:04", :updated_at => "2013-03-05 06:50:04", :stanza_id => 133 },
  { :line => "The impunity of unborn Mights was hers.", :no => 535, :created_at => "2013-03-05 06:50:04", :updated_at => "2013-03-05 06:50:04", :stanza_id => 133 },
  { :line => "Although she leaned to bear the human load,", :no => 536, :created_at => "2013-03-05 06:50:05", :updated_at => "2013-03-05 06:50:05", :stanza_id => 134 },
  { :line => "Her walk kept still the measure of the gods.", :no => 537, :created_at => "2013-03-05 06:50:05", :updated_at => "2013-03-05 06:50:05", :stanza_id => 134 },
  { :line => "Earthâ€™s breath had failed to stain that brilliant glass:", :no => 538, :created_at => "2013-03-05 06:50:05", :updated_at => "2013-03-05 06:50:05", :stanza_id => 135 },
  { :line => "Unsmeared with the dust of our mortal atmosphere", :no => 539, :created_at => "2013-03-05 06:50:06", :updated_at => "2013-03-05 06:50:06", :stanza_id => 135 },
  { :line => "It still reflected heavenâ€™s spiritual joy.", :no => 540, :created_at => "2013-03-05 06:50:06", :updated_at => "2013-03-05 06:50:06", :stanza_id => 135 },
  { :line => "Almost they saw who lived within her light", :no => 541, :created_at => "2013-03-05 06:50:06", :updated_at => "2013-03-05 06:50:06", :stanza_id => 136 },
  { :line => "Her playmate in the sempiternal spheres", :no => 542, :created_at => "2013-03-05 06:50:06", :updated_at => "2013-03-05 06:50:06", :stanza_id => 136 },
  { :line => "Descended from its unattainable realms", :no => 543, :created_at => "2013-03-05 06:50:07", :updated_at => "2013-03-05 06:50:07", :stanza_id => 136 },
  { :line => "In her attracting adventâ€™s luminous wake,", :no => 544, :created_at => "2013-03-05 06:50:07", :updated_at => "2013-03-05 06:50:07", :stanza_id => 136 },
  { :line => "The white-fire dragon bird of endless bliss", :no => 545, :created_at => "2013-03-05 06:50:07", :updated_at => "2013-03-05 06:50:07", :stanza_id => 136 },
  { :line => "Drifting with burning wings above her days:", :no => 546, :created_at => "2013-03-05 06:50:07", :updated_at => "2013-03-05 06:50:07", :stanza_id => 136 },
  { :line => "Heavenâ€™s tranquil shield guarded the missioned child.", :no => 547, :created_at => "2013-03-05 06:50:07", :updated_at => "2013-03-05 06:50:07", :stanza_id => 136 },
  { :line => "A glowing orbit was her early term,", :no => 548, :created_at => "2013-03-05 06:50:08", :updated_at => "2013-03-05 06:50:08", :stanza_id => 137 },
  { :line => "Years like gold raiment of the gods that pass;", :no => 549, :created_at => "2013-03-05 06:50:08", :updated_at => "2013-03-05 06:50:08", :stanza_id => 137 },
  { :line => "Her youth sat throned in calm felicity.", :no => 550, :created_at => "2013-03-05 06:50:08", :updated_at => "2013-03-05 06:50:08", :stanza_id => 137 },
  { :line => "But joy cannot endure until the end:", :no => 551, :created_at => "2013-03-05 06:50:09", :updated_at => "2013-03-05 06:50:09", :stanza_id => 138 },
  { :line => "There is a darkness in terrestrial things", :no => 552, :created_at => "2013-03-05 06:50:09", :updated_at => "2013-03-05 06:50:09", :stanza_id => 138 },
  { :line => "That will not suffer long too glad a note.", :no => 553, :created_at => "2013-03-05 06:50:09", :updated_at => "2013-03-05 06:50:09", :stanza_id => 138 },
  { :line => "On her too closed the inescapable Hand:", :no => 554, :created_at => "2013-03-05 06:50:10", :updated_at => "2013-03-05 06:50:10", :stanza_id => 139 },
  { :line => "The armed Immortal bore the snare of Time.", :no => 555, :created_at => "2013-03-05 06:50:10", :updated_at => "2013-03-05 06:50:10", :stanza_id => 139 },
  { :line => "One dealt with her who meets the burdened great.", :no => 556, :created_at => "2013-03-05 06:50:10", :updated_at => "2013-03-05 06:50:10", :stanza_id => 140 },
  { :line => "Assigner of the ordeal and the path", :no => 557, :created_at => "2013-03-05 06:50:11", :updated_at => "2013-03-05 06:50:11", :stanza_id => 141 },
  { :line => "Who chooses in this holocaust of the soul", :no => 558, :created_at => "2013-03-05 06:50:11", :updated_at => "2013-03-05 06:50:11", :stanza_id => 141 },
  { :line => "Death, fall and sorrow as the spiritâ€™s goads,", :no => 559, :created_at => "2013-03-05 06:50:11", :updated_at => "2013-03-05 06:50:11", :stanza_id => 141 },
  { :line => "The dubious godhead with his torch of pain", :no => 560, :created_at => "2013-03-05 06:50:11", :updated_at => "2013-03-05 06:50:11", :stanza_id => 141 },
  { :line => "Lit up the chasm of the unfinished world", :no => 561, :created_at => "2013-03-05 06:50:12", :updated_at => "2013-03-05 06:50:12", :stanza_id => 141 },
  { :line => "And called her to fill with her vast self the abyss.", :no => 562, :created_at => "2013-03-05 06:50:12", :updated_at => "2013-03-05 06:50:12", :stanza_id => 141 },
  { :line => "August and pitiless in his calm outlook,", :no => 563, :created_at => "2013-03-05 06:50:12", :updated_at => "2013-03-05 06:50:12", :stanza_id => 142 },
  { :line => "Heightening the Eternalâ€™s dreadful strategy,", :no => 564, :created_at => "2013-03-05 06:50:12", :updated_at => "2013-03-05 06:50:12", :stanza_id => 142 },
  { :line => "He measured the difficulty with the might", :no => 565, :created_at => "2013-03-05 06:50:12", :updated_at => "2013-03-05 06:50:12", :stanza_id => 142 },
  { :line => "And dug more deep the gulf that all must cross.", :no => 566, :created_at => "2013-03-05 06:50:13", :updated_at => "2013-03-05 06:50:13", :stanza_id => 142 },
  { :line => "Assailing her divinest elements,", :no => 567, :created_at => "2013-03-05 06:50:13", :updated_at => "2013-03-05 06:50:13", :stanza_id => 143 },
  { :line => "He made her heart kin to the striving human heart", :no => 568, :created_at => "2013-03-05 06:50:13", :updated_at => "2013-03-05 06:50:13", :stanza_id => 143 },
  { :line => "And forced her strength to its appointed road.", :no => 569, :created_at => "2013-03-05 06:50:13", :updated_at => "2013-03-05 06:50:13", :stanza_id => 143 },
  { :line => "For this she had accepted mortal breath;", :no => 570, :created_at => "2013-03-05 06:50:14", :updated_at => "2013-03-05 06:50:14", :stanza_id => 144 },
  { :line => "To wrestle with the Shadow she had come", :no => 571, :created_at => "2013-03-05 06:50:14", :updated_at => "2013-03-05 06:50:14", :stanza_id => 144 },
  { :line => "And must confront the riddle of manâ€™s birth", :no => 572, :created_at => "2013-03-05 06:50:14", :updated_at => "2013-03-05 06:50:14", :stanza_id => 144 },
  { :line => "And lifeâ€™s brief struggle in dumb Matterâ€™s night.", :no => 573, :created_at => "2013-03-05 06:50:14", :updated_at => "2013-03-05 06:50:14", :stanza_id => 144 },
  { :line => "Whether to bear with Ignorance and Death", :no => 574, :created_at => "2013-03-05 06:50:15", :updated_at => "2013-03-05 06:50:15", :stanza_id => 145 },
  { :line => "Or hew the ways of Immortality,", :no => 575, :created_at => "2013-03-05 06:50:15", :updated_at => "2013-03-05 06:50:15", :stanza_id => 145 },
  { :line => "To win or lose the godlike game for man,", :no => 576, :created_at => "2013-03-05 06:50:15", :updated_at => "2013-03-05 06:50:15", :stanza_id => 145 },
  { :line => "Was her soulâ€™s issue thrown with Destinyâ€™s dice.", :no => 577, :created_at => "2013-03-05 06:50:16", :updated_at => "2013-03-05 06:50:16", :stanza_id => 145 },
  { :line => "But not to submit and suffer was she born;", :no => 578, :created_at => "2013-03-05 06:50:16", :updated_at => "2013-03-05 06:50:16", :stanza_id => 146 },
  { :line => "To lead, to deliver was her glorious part.", :no => 579, :created_at => "2013-03-05 06:50:16", :updated_at => "2013-03-05 06:50:16", :stanza_id => 146 },
  { :line => "Here was no fabric of terrestrial make", :no => 580, :created_at => "2013-03-05 06:50:16", :updated_at => "2013-03-05 06:50:16", :stanza_id => 147 },
  { :line => "Fit for a dayâ€™s use by busy careless Powers.", :no => 581, :created_at => "2013-03-05 06:50:17", :updated_at => "2013-03-05 06:50:17", :stanza_id => 147 },
  { :line => "An image fluttering on the screen of fate", :no => 582, :created_at => "2013-03-05 06:50:17", :updated_at => "2013-03-05 06:50:17", :stanza_id => 148 },
  { :line => "Half-animated for a passing show,", :no => 583, :created_at => "2013-03-05 06:50:17", :updated_at => "2013-03-05 06:50:17", :stanza_id => 148 },
  { :line => "Or a castaway on the ocean of Desire", :no => 584, :created_at => "2013-03-05 06:50:17", :updated_at => "2013-03-05 06:50:17", :stanza_id => 148 },
  { :line => "Flung to the eddies in a ruthless sport", :no => 585, :created_at => "2013-03-05 06:50:18", :updated_at => "2013-03-05 06:50:18", :stanza_id => 148 },
  { :line => "And tossed along the gulfs of Circumstance,", :no => 586, :created_at => "2013-03-05 06:50:18", :updated_at => "2013-03-05 06:50:18", :stanza_id => 148 },
  { :line => "A creature born to bend beneath the yoke,", :no => 587, :created_at => "2013-03-05 06:50:18", :updated_at => "2013-03-05 06:50:18", :stanza_id => 148 },
  { :line => "A chattel and a plaything of Timeâ€™s lords,", :no => 588, :created_at => "2013-03-05 06:50:18", :updated_at => "2013-03-05 06:50:18", :stanza_id => 148 },
  { :line => "Or one more pawn who comes destined to be pushed", :no => 589, :created_at => "2013-03-05 06:50:18", :updated_at => "2013-03-05 06:50:18", :stanza_id => 148 },
  { :line => "One slow move forward on a measureless board", :no => 590, :created_at => "2013-03-05 06:50:19", :updated_at => "2013-03-05 06:50:19", :stanza_id => 148 },
  { :line => "In the chess-play of the earth-soul with Doom,â€”", :no => 591, :created_at => "2013-03-05 06:50:19", :updated_at => "2013-03-05 06:50:19", :stanza_id => 148 },
  { :line => "Such is the human figure drawn by Time.", :no => 592, :created_at => "2013-03-05 06:50:19", :updated_at => "2013-03-05 06:50:19", :stanza_id => 148 },
  { :line => "A conscious frame was here, a self-born Force.", :no => 593, :created_at => "2013-03-05 06:50:20", :updated_at => "2013-03-05 06:50:20", :stanza_id => 149 },
  { :line => "In this enigma of the dusk of God,", :no => 594, :created_at => "2013-03-05 06:50:20", :updated_at => "2013-03-05 06:50:20", :stanza_id => 150 },
  { :line => "This slow and strange uneasy compromise", :no => 595, :created_at => "2013-03-05 06:50:20", :updated_at => "2013-03-05 06:50:20", :stanza_id => 150 },
  { :line => "Of limiting Nature with a limitless Soul,", :no => 596, :created_at => "2013-03-05 06:50:20", :updated_at => "2013-03-05 06:50:20", :stanza_id => 150 },
  { :line => "Where all must move between an ordered Chance", :no => 597, :created_at => "2013-03-05 06:50:20", :updated_at => "2013-03-05 06:50:20", :stanza_id => 150 },
  { :line => "And an uncaring blind Necessity,", :no => 598, :created_at => "2013-03-05 06:50:21", :updated_at => "2013-03-05 06:50:21", :stanza_id => 150 },
  { :line => "Too high the fire spiritual dare not blaze.", :no => 599, :created_at => "2013-03-05 06:50:21", :updated_at => "2013-03-05 06:50:21", :stanza_id => 150 },
  { :line => "If once it met the intense original Flame,", :no => 600, :created_at => "2013-03-05 06:50:21", :updated_at => "2013-03-05 06:50:21", :stanza_id => 151 },
  { :line => "An answering touch might shatter all measures made", :no => 601, :created_at => "2013-03-05 06:50:22", :updated_at => "2013-03-05 06:50:22", :stanza_id => 151 },
  { :line => "And earth sink down with the weight of the Infinite.", :no => 602, :created_at => "2013-03-05 06:50:22", :updated_at => "2013-03-05 06:50:22", :stanza_id => 151 },
  { :line => "A gaol is this immense material world.", :no => 603, :created_at => "2013-03-05 06:50:22", :updated_at => "2013-03-05 06:50:22", :stanza_id => 152 },
  { :line => "Across each road stands armed a stone-eyed law,", :no => 604, :created_at => "2013-03-05 06:50:23", :updated_at => "2013-03-05 06:50:23", :stanza_id => 153 },
  { :line => "At every gate the huge dim sentinels pace.", :no => 605, :created_at => "2013-03-05 06:50:23", :updated_at => "2013-03-05 06:50:23", :stanza_id => 153 },
  { :line => "A grey tribunal of the Ignorance,", :no => 606, :created_at => "2013-03-05 06:50:23", :updated_at => "2013-03-05 06:50:23", :stanza_id => 154 },
  { :line => "An Inquisition of the priests of Night", :no => 607, :created_at => "2013-03-05 06:50:23", :updated_at => "2013-03-05 06:50:23", :stanza_id => 154 },
  { :line => "In judgment sit on the adventurer soul,", :no => 608, :created_at => "2013-03-05 06:50:23", :updated_at => "2013-03-05 06:50:23", :stanza_id => 154 },
  { :line => "And the dual tables and the Karmic norm", :no => 609, :created_at => "2013-03-05 06:50:24", :updated_at => "2013-03-05 06:50:24", :stanza_id => 154 },
  { :line => "Restrain the Titan in us and the God:", :no => 610, :created_at => "2013-03-05 06:50:24", :updated_at => "2013-03-05 06:50:24", :stanza_id => 154 },
  { :line => "Pain with its lash, joy with its silver bribe", :no => 611, :created_at => "2013-03-05 06:50:24", :updated_at => "2013-03-05 06:50:24", :stanza_id => 154 },
  { :line => "Guard the Wheelâ€™s circling immobility,", :no => 612, :created_at => "2013-03-05 06:50:24", :updated_at => "2013-03-05 06:50:24", :stanza_id => 154 },
  { :line => "A bond is put on the high climbing mind,", :no => 613, :created_at => "2013-03-05 06:50:24", :updated_at => "2013-03-05 06:50:24", :stanza_id => 154 },
  { :line => "A seal on the too large wide-open heart;", :no => 614, :created_at => "2013-03-05 06:50:25", :updated_at => "2013-03-05 06:50:25", :stanza_id => 154 },
  { :line => "Death stays the journeying discoverer, Life.", :no => 615, :created_at => "2013-03-05 06:50:25", :updated_at => "2013-03-05 06:50:25", :stanza_id => 154 },
  { :line => "Thus is the throne of the Inconscient safe", :no => 616, :created_at => "2013-03-05 06:50:26", :updated_at => "2013-03-05 06:50:26", :stanza_id => 155 },
  { :line => "While the tardy coilings of the aeons pass", :no => 617, :created_at => "2013-03-05 06:50:26", :updated_at => "2013-03-05 06:50:26", :stanza_id => 155 },
  { :line => "And the Animal browses in the sacred fence", :no => 618, :created_at => "2013-03-05 06:50:26", :updated_at => "2013-03-05 06:50:26", :stanza_id => 155 },
  { :line => "And the gold Hawk can cross the skies no more.", :no => 619, :created_at => "2013-03-05 06:50:26", :updated_at => "2013-03-05 06:50:26", :stanza_id => 155 },
  { :line => "But one stood up and lit the limitless flame.", :no => 620, :created_at => "2013-03-05 06:50:26", :updated_at => "2013-03-05 06:50:26", :stanza_id => 156 },
  { :line => "Arraigned by the dark Power that hates all bliss", :no => 621, :created_at => "2013-03-05 06:50:27", :updated_at => "2013-03-05 06:50:27", :stanza_id => 157 },
  { :line => "In the dire court where life must pay for joy,", :no => 622, :created_at => "2013-03-05 06:50:27", :updated_at => "2013-03-05 06:50:27", :stanza_id => 157 },
  { :line => "Sentenced by the mechanic justicer", :no => 623, :created_at => "2013-03-05 06:50:27", :updated_at => "2013-03-05 06:50:27", :stanza_id => 157 },
  { :line => "To the afflicting penalty of manâ€™s hopes,", :no => 624, :created_at => "2013-03-05 06:50:28", :updated_at => "2013-03-05 06:50:28", :stanza_id => 157 },
  { :line => "Her head she bowed not to the stark decree", :no => 625, :created_at => "2013-03-05 06:50:28", :updated_at => "2013-03-05 06:50:28", :stanza_id => 157 },
  { :line => "Baring her helpless heart to destinyâ€™s stroke.", :no => 626, :created_at => "2013-03-05 06:50:28", :updated_at => "2013-03-05 06:50:28", :stanza_id => 157 },
  { :line => "So bows and must the mind-born will in man", :no => 627, :created_at => "2013-03-05 06:50:28", :updated_at => "2013-03-05 06:50:28", :stanza_id => 158 },
  { :line => "Obedient to the statutes fixed of old,", :no => 628, :created_at => "2013-03-05 06:50:29", :updated_at => "2013-03-05 06:50:29", :stanza_id => 158 },
  { :line => "Admitting without appeal the nether gods.", :no => 629, :created_at => "2013-03-05 06:50:29", :updated_at => "2013-03-05 06:50:29", :stanza_id => 158 },
  { :line => "In her the superhuman cast its seed.", :no => 630, :created_at => "2013-03-05 06:50:29", :updated_at => "2013-03-05 06:50:29", :stanza_id => 159 },
  { :line => "Inapt to fold its mighty wings of dream", :no => 631, :created_at => "2013-03-05 06:50:30", :updated_at => "2013-03-05 06:50:30", :stanza_id => 160 },
  { :line => "Her spirit refused to hug the common soil,", :no => 632, :created_at => "2013-03-05 06:50:30", :updated_at => "2013-03-05 06:50:30", :stanza_id => 160 },
  { :line => "Or, finding all lifeâ€™s golden meanings robbed,", :no => 633, :created_at => "2013-03-05 06:50:30", :updated_at => "2013-03-05 06:50:30", :stanza_id => 160 },
  { :line => "Compound with earth, struck from the starry list,", :no => 634, :created_at => "2013-03-05 06:50:30", :updated_at => "2013-03-05 06:50:30", :stanza_id => 160 },
  { :line => "Or quench with black despair the God-given light.", :no => 635, :created_at => "2013-03-05 06:50:30", :updated_at => "2013-03-05 06:50:30", :stanza_id => 160 },
  { :line => "Accustomed to the eternal and the true,", :no => 636, :created_at => "2013-03-05 06:50:31", :updated_at => "2013-03-05 06:50:31", :stanza_id => 161 },
  { :line => "Her being conscious of its divine founts", :no => 637, :created_at => "2013-03-05 06:50:31", :updated_at => "2013-03-05 06:50:31", :stanza_id => 161 },
  { :line => "Asked not from mortal frailty painâ€™s relief,", :no => 638, :created_at => "2013-03-05 06:50:31", :updated_at => "2013-03-05 06:50:31", :stanza_id => 161 },
  { :line => "Patched not with failure bargain or compromise.", :no => 639, :created_at => "2013-03-05 06:50:32", :updated_at => "2013-03-05 06:50:32", :stanza_id => 161 },
  { :line => "A work she had to do, a word to speak;", :no => 640, :created_at => "2013-03-05 06:50:32", :updated_at => "2013-03-05 06:50:32", :stanza_id => 162 },
  { :line => "Writing the unfinished story of her soul", :no => 641, :created_at => "2013-03-05 06:50:32", :updated_at => "2013-03-05 06:50:32", :stanza_id => 162 },
  { :line => "In thoughts and actions graved in Natureâ€™s book,", :no => 642, :created_at => "2013-03-05 06:50:33", :updated_at => "2013-03-05 06:50:33", :stanza_id => 162 },
  { :line => "She accepted not to close the luminous page,", :no => 643, :created_at => "2013-03-05 06:50:33", :updated_at => "2013-03-05 06:50:33", :stanza_id => 162 },
  { :line => "Cancel her commerce with eternity,", :no => 644, :created_at => "2013-03-05 06:50:33", :updated_at => "2013-03-05 06:50:33", :stanza_id => 162 },
  { :line => "Or set a signature of weak assent", :no => 645, :created_at => "2013-03-05 06:50:33", :updated_at => "2013-03-05 06:50:33", :stanza_id => 162 },
  { :line => "To the brute balance of the worldâ€™s exchange.", :no => 646, :created_at => "2013-03-05 06:50:33", :updated_at => "2013-03-05 06:50:33", :stanza_id => 162 },
  { :line => "A force in her that toiled since earth was made,", :no => 647, :created_at => "2013-03-05 06:50:34", :updated_at => "2013-03-05 06:50:34", :stanza_id => 163 },
  { :line => "Accomplishing in life the great world-plan,", :no => 648, :created_at => "2013-03-05 06:50:34", :updated_at => "2013-03-05 06:50:34", :stanza_id => 163 },
  { :line => "Pursuing after death immortal aims,", :no => 649, :created_at => "2013-03-05 06:50:34", :updated_at => "2013-03-05 06:50:34", :stanza_id => 163 },
  { :line => "Repugned to admit frustrationâ€™s barren role,", :no => 650, :created_at => "2013-03-05 06:50:34", :updated_at => "2013-03-05 06:50:34", :stanza_id => 163 },
  { :line => "Forfeit the meaning of her birth in Time,", :no => 651, :created_at => "2013-03-05 06:50:35", :updated_at => "2013-03-05 06:50:35", :stanza_id => 163 },
  { :line => "Obey the government of the casual fact", :no => 652, :created_at => "2013-03-05 06:50:35", :updated_at => "2013-03-05 06:50:35", :stanza_id => 163 },
  { :line => "Or yield her high destiny up to passing Chance.", :no => 653, :created_at => "2013-03-05 06:50:35", :updated_at => "2013-03-05 06:50:35", :stanza_id => 163 },
  { :line => "In her own self she found her high recourse;", :no => 654, :created_at => "2013-03-05 06:50:36", :updated_at => "2013-03-05 06:50:36", :stanza_id => 164 },
  { :line => "She matched with the iron law her sovereign right:", :no => 655, :created_at => "2013-03-05 06:50:36", :updated_at => "2013-03-05 06:50:36", :stanza_id => 164 },
  { :line => "Her single will opposed the cosmic rule.", :no => 656, :created_at => "2013-03-05 06:50:36", :updated_at => "2013-03-05 06:50:36", :stanza_id => 164 },
  { :line => "To stay the wheels of Doom this greatness rose.", :no => 657, :created_at => "2013-03-05 06:50:36", :updated_at => "2013-03-05 06:50:36", :stanza_id => 165 },
  { :line => "At the Unseenâ€™s knock upon the hidden gates", :no => 658, :created_at => "2013-03-05 06:50:37", :updated_at => "2013-03-05 06:50:37", :stanza_id => 166 },
  { :line => "Her strength made greater by the lightningâ€™s touch", :no => 659, :created_at => "2013-03-05 06:50:37", :updated_at => "2013-03-05 06:50:37", :stanza_id => 166 },
  { :line => "Awoke from slumber in her heartâ€™s recess.", :no => 660, :created_at => "2013-03-05 06:50:37", :updated_at => "2013-03-05 06:50:37", :stanza_id => 166 },
  { :line => "It bore the stroke of That which kills and saves.", :no => 661, :created_at => "2013-03-05 06:50:38", :updated_at => "2013-03-05 06:50:38", :stanza_id => 167 },
  { :line => "Across the awful march no eye can see,", :no => 662, :created_at => "2013-03-05 06:50:38", :updated_at => "2013-03-05 06:50:38", :stanza_id => 168 },
  { :line => "Barring its dreadful route no will can change,", :no => 663, :created_at => "2013-03-05 06:50:38", :updated_at => "2013-03-05 06:50:38", :stanza_id => 168 },
  { :line => "She faced the engines of the universe;", :no => 664, :created_at => "2013-03-05 06:50:39", :updated_at => "2013-03-05 06:50:39", :stanza_id => 168 },
  { :line => "A heart stood in the way of the driving wheels:", :no => 665, :created_at => "2013-03-05 06:50:39", :updated_at => "2013-03-05 06:50:39", :stanza_id => 168 },
  { :line => "Its giant workings paused in front of a mind,", :no => 666, :created_at => "2013-03-05 06:50:39", :updated_at => "2013-03-05 06:50:39", :stanza_id => 168 },
  { :line => "Its stark conventions met the flame of a soul.", :no => 667, :created_at => "2013-03-05 06:50:39", :updated_at => "2013-03-05 06:50:39", :stanza_id => 168 },
  { :line => "A magic leverage suddenly is caught", :no => 668, :created_at => "2013-03-05 06:50:40", :updated_at => "2013-03-05 06:50:40", :stanza_id => 169 },
  { :line => "That moves the veiled Ineffableâ€™s timeless will:", :no => 669, :created_at => "2013-03-05 06:50:40", :updated_at => "2013-03-05 06:50:40", :stanza_id => 169 },
  { :line => "A prayer, a master act, a king idea", :no => 670, :created_at => "2013-03-05 06:50:40", :updated_at => "2013-03-05 06:50:40", :stanza_id => 169 },
  { :line => "Can link manâ€™s strength to a transcendent Force.", :no => 671, :created_at => "2013-03-05 06:50:40", :updated_at => "2013-03-05 06:50:40", :stanza_id => 169 },
  { :line => "Then miracle is made the common rule,", :no => 672, :created_at => "2013-03-05 06:50:40", :updated_at => "2013-03-05 06:50:40", :stanza_id => 170 },
  { :line => "One mighty deed can change the course of things;", :no => 673, :created_at => "2013-03-05 06:50:41", :updated_at => "2013-03-05 06:50:41", :stanza_id => 170 },
  { :line => "A lonely thought becomes omnipotent.", :no => 674, :created_at => "2013-03-05 06:50:41", :updated_at => "2013-03-05 06:50:41", :stanza_id => 170 },
  { :line => "All now seems Natureâ€™s massed machinery;", :no => 675, :created_at => "2013-03-05 06:50:41", :updated_at => "2013-03-05 06:50:41", :stanza_id => 171 },
  { :line => "An endless servitude to material rule", :no => 676, :created_at => "2013-03-05 06:50:42", :updated_at => "2013-03-05 06:50:42", :stanza_id => 171 },
  { :line => "And long determinationâ€™s rigid chain,", :no => 677, :created_at => "2013-03-05 06:50:42", :updated_at => "2013-03-05 06:50:42", :stanza_id => 171 },
  { :line => "Her firm and changeless habits aping Law,", :no => 678, :created_at => "2013-03-05 06:50:42", :updated_at => "2013-03-05 06:50:42", :stanza_id => 171 },
  { :line => "Her empire of unconscious deft device", :no => 679, :created_at => "2013-03-05 06:50:42", :updated_at => "2013-03-05 06:50:42", :stanza_id => 171 },
  { :line => "Annul the claim of manâ€™s free human will.", :no => 680, :created_at => "2013-03-05 06:50:42", :updated_at => "2013-03-05 06:50:42", :stanza_id => 171 },
  { :line => "He too is a machine amid machines;", :no => 681, :created_at => "2013-03-05 06:50:43", :updated_at => "2013-03-05 06:50:43", :stanza_id => 172 },
  { :line => "A piston brain pumps out the shapes of thought,", :no => 682, :created_at => "2013-03-05 06:50:43", :updated_at => "2013-03-05 06:50:43", :stanza_id => 172 },
  { :line => "A beating heart cuts out emotionâ€™s modes;", :no => 683, :created_at => "2013-03-05 06:50:43", :updated_at => "2013-03-05 06:50:43", :stanza_id => 172 },
  { :line => "An insentient energy fabricates a soul.", :no => 684, :created_at => "2013-03-05 06:50:43", :updated_at => "2013-03-05 06:50:43", :stanza_id => 172 },
  { :line => "Or the figure of the world reveals the signs", :no => 685, :created_at => "2013-03-05 06:50:44", :updated_at => "2013-03-05 06:50:44", :stanza_id => 173 },
  { :line => "Of a tied Chance repeating her old steps", :no => 686, :created_at => "2013-03-05 06:50:44", :updated_at => "2013-03-05 06:50:44", :stanza_id => 173 },
  { :line => "In circles around Matterâ€™s binding-posts.", :no => 687, :created_at => "2013-03-05 06:50:44", :updated_at => "2013-03-05 06:50:44", :stanza_id => 173 },
  { :line => "A random series of inept events", :no => 688, :created_at => "2013-03-05 06:50:45", :updated_at => "2013-03-05 06:50:45", :stanza_id => 174 },
  { :line => "To which reason lends illusive sense, is here,", :no => 689, :created_at => "2013-03-05 06:50:45", :updated_at => "2013-03-05 06:50:45", :stanza_id => 174 },
  { :line => "Or the empiric Lifeâ€™s instinctive search,", :no => 690, :created_at => "2013-03-05 06:50:45", :updated_at => "2013-03-05 06:50:45", :stanza_id => 174 },
  { :line => "Or a vast ignorant mindâ€™s colossal work.", :no => 691, :created_at => "2013-03-05 06:50:45", :updated_at => "2013-03-05 06:50:45", :stanza_id => 174 },
  { :line => "But wisdom comes, and vision grows within;", :no => 692, :created_at => "2013-03-05 06:50:46", :updated_at => "2013-03-05 06:50:46", :stanza_id => 175 },
  { :line => "Then Natureâ€™s instrument crowns himself her king;", :no => 693, :created_at => "2013-03-05 06:50:46", :updated_at => "2013-03-05 06:50:46", :stanza_id => 175 },
  { :line => "He feels his witnessing self and conscious power;", :no => 694, :created_at => "2013-03-05 06:50:46", :updated_at => "2013-03-05 06:50:46", :stanza_id => 175 },
  { :line => "His soul steps back and sees the Light supreme.", :no => 695, :created_at => "2013-03-05 06:50:46", :updated_at => "2013-03-05 06:50:46", :stanza_id => 175 },
  { :line => "A Godhead stands behind the brute machine.", :no => 696, :created_at => "2013-03-05 06:50:47", :updated_at => "2013-03-05 06:50:47", :stanza_id => 176 },
  { :line => "This truth broke in in a triumph of fire;", :no => 697, :created_at => "2013-03-05 06:50:47", :updated_at => "2013-03-05 06:50:47", :stanza_id => 177 },
  { :line => "A victory was won for God in man,", :no => 698, :created_at => "2013-03-05 06:50:47", :updated_at => "2013-03-05 06:50:47", :stanza_id => 177 },
  { :line => "The deity revealed its hidden face.", :no => 699, :created_at => "2013-03-05 06:50:48", :updated_at => "2013-03-05 06:50:48", :stanza_id => 177 },
  { :line => "The great World-Mother now in her arose:", :no => 700, :created_at => "2013-03-05 06:50:48", :updated_at => "2013-03-05 06:50:48", :stanza_id => 178 },
  { :line => "A living choice reversed fateâ€™s cold dead turn,", :no => 701, :created_at => "2013-03-05 06:50:48", :updated_at => "2013-03-05 06:50:48", :stanza_id => 178 },
  { :line => "Affirmed the spiritâ€™s tread on Circumstance,", :no => 702, :created_at => "2013-03-05 06:50:49", :updated_at => "2013-03-05 06:50:49", :stanza_id => 178 },
  { :line => "Pressed back the senseless dire revolving Wheel", :no => 703, :created_at => "2013-03-05 06:50:49", :updated_at => "2013-03-05 06:50:49", :stanza_id => 178 },
  { :line => "And stopped the mute march of Necessity.", :no => 704, :created_at => "2013-03-05 06:50:49", :updated_at => "2013-03-05 06:50:49", :stanza_id => 178 },
  { :line => "A flaming warrior from the eternal peaks", :no => 705, :created_at => "2013-03-05 06:50:49", :updated_at => "2013-03-05 06:50:49", :stanza_id => 179 },
  { :line => "Empowered to force the door denied and closed", :no => 706, :created_at => "2013-03-05 06:50:50", :updated_at => "2013-03-05 06:50:50", :stanza_id => 179 },
  { :line => "Smote from Deathâ€™s visage its dumb absolute", :no => 707, :created_at => "2013-03-05 06:50:50", :updated_at => "2013-03-05 06:50:50", :stanza_id => 179 },
  { :line => "And burst the bounds of consciousness and Time.", :no => 708, :created_at => "2013-03-05 06:50:50", :updated_at => "2013-03-05 06:50:50", :stanza_id => 179 },
  { :line => "A worldâ€™s desire compelled her mortal birth.", :no => 709, :created_at => "2013-03-05 06:50:50", :updated_at => "2013-03-05 06:50:50", :stanza_id => 180 },
  { :line => "One in the front of the immemorial quest,", :no => 710, :created_at => "2013-03-05 09:15:56", :updated_at => "2013-03-05 09:15:56", :stanza_id => 181 },
  { :line => "Protagonist of the mysterious play", :no => 711, :created_at => "2013-03-05 09:15:56", :updated_at => "2013-03-05 09:15:56", :stanza_id => 181 },
  { :line => "In which the Unknown pursues himself through forms", :no => 712, :created_at => "2013-03-05 09:15:56", :updated_at => "2013-03-05 09:15:56", :stanza_id => 181 },
  { :line => "And limits his eternity by the hours", :no => 713, :created_at => "2013-03-05 09:15:56", :updated_at => "2013-03-05 09:15:56", :stanza_id => 181 },
  { :line => "And the blind Void struggles to live and see,", :no => 714, :created_at => "2013-03-05 09:15:56", :updated_at => "2013-03-05 09:15:56", :stanza_id => 181 },
  { :line => "A thinker and toiler in the idealâ€™s air,", :no => 715, :created_at => "2013-03-05 09:15:57", :updated_at => "2013-03-05 09:15:57", :stanza_id => 181 },
  { :line => "Brought down to earthâ€™s dumb need her radiant power.", :no => 716, :created_at => "2013-03-05 09:15:57", :updated_at => "2013-03-05 09:15:57", :stanza_id => 181 },
  { :line => "His was a spirit that stooped from larger spheres", :no => 717, :created_at => "2013-03-05 09:15:57", :updated_at => "2013-03-05 09:15:57", :stanza_id => 182 },
  { :line => "Into our province of ephemeral sight,", :no => 718, :created_at => "2013-03-05 09:15:57", :updated_at => "2013-03-05 09:15:57", :stanza_id => 182 },
  { :line => "A colonist from immortality.", :no => 719, :created_at => "2013-03-05 09:15:57", :updated_at => "2013-03-05 09:15:57", :stanza_id => 182 },
  { :line => "A pointing beam on earthâ€™s uncertain roads,", :no => 720, :created_at => "2013-03-05 09:15:58", :updated_at => "2013-03-05 09:15:58", :stanza_id => 183 },
  { :line => "His birth held up a symbol and a sign;", :no => 721, :created_at => "2013-03-05 09:15:58", :updated_at => "2013-03-05 09:15:58", :stanza_id => 183 },
  { :line => "His human self like a translucent cloak", :no => 722, :created_at => "2013-03-05 09:15:58", :updated_at => "2013-03-05 09:15:58", :stanza_id => 183 },
  { :line => "Covered the All-Wise who leads the unseeing world.", :no => 723, :created_at => "2013-03-05 09:15:58", :updated_at => "2013-03-05 09:15:58", :stanza_id => 183 },
  { :line => "Affiliated to cosmic Space and Time", :no => 724, :created_at => "2013-03-05 09:15:59", :updated_at => "2013-03-05 09:15:59", :stanza_id => 184 },
  { :line => "And paying here Godâ€™s debt to earth and man", :no => 725, :created_at => "2013-03-05 09:15:59", :updated_at => "2013-03-05 09:15:59", :stanza_id => 184 },
  { :line => "A greater sonship was his divine right.", :no => 726, :created_at => "2013-03-05 09:15:59", :updated_at => "2013-03-05 09:15:59", :stanza_id => 184 },
  { :line => "Although consenting to mortal ignorance,", :no => 727, :created_at => "2013-03-05 09:15:59", :updated_at => "2013-03-05 09:15:59", :stanza_id => 185 },
  { :line => "His knowledge shared the Light ineffable.", :no => 728, :created_at => "2013-03-05 09:15:59", :updated_at => "2013-03-05 09:15:59", :stanza_id => 185 },
  { :line => "A strength of the original Permanence", :no => 729, :created_at => "2013-03-05 09:16:00", :updated_at => "2013-03-05 09:16:00", :stanza_id => 186 },
  { :line => "Entangled in the moment and its flow,", :no => 730, :created_at => "2013-03-05 09:16:00", :updated_at => "2013-03-05 09:16:00", :stanza_id => 186 },
  { :line => "He kept the vision of the Vasts behind:", :no => 731, :created_at => "2013-03-05 09:16:00", :updated_at => "2013-03-05 09:16:00", :stanza_id => 186 },
  { :line => "A power was in him from the Unknowable.", :no => 732, :created_at => "2013-03-05 09:16:00", :updated_at => "2013-03-05 09:16:00", :stanza_id => 186 },
  { :line => "An archivist of the symbols of the Beyond,", :no => 733, :created_at => "2013-03-05 09:16:01", :updated_at => "2013-03-05 09:16:01", :stanza_id => 187 },
  { :line => "A treasurer of superhuman dreams,", :no => 734, :created_at => "2013-03-05 09:16:01", :updated_at => "2013-03-05 09:16:01", :stanza_id => 187 },
  { :line => "He bore the stamp of mighty memories", :no => 735, :created_at => "2013-03-05 09:16:01", :updated_at => "2013-03-05 09:16:01", :stanza_id => 187 },
  { :line => "And shed their grandiose ray on human life.", :no => 736, :created_at => "2013-03-05 09:16:01", :updated_at => "2013-03-05 09:16:01", :stanza_id => 187 },
  { :line => "His days were a long growth to the Supreme.", :no => 737, :created_at => "2013-03-05 09:16:02", :updated_at => "2013-03-05 09:16:02", :stanza_id => 188 },
  { :line => "A skyward being nourishing its roots", :no => 738, :created_at => "2013-03-05 09:16:02", :updated_at => "2013-03-05 09:16:02", :stanza_id => 189 },
  { :line => "On sustenance from occult spiritual founts", :no => 739, :created_at => "2013-03-05 09:16:02", :updated_at => "2013-03-05 09:16:02", :stanza_id => 189 },
  { :line => "Climbed through white rays to meet an unseen Sun.", :no => 740, :created_at => "2013-03-05 09:16:02", :updated_at => "2013-03-05 09:16:02", :stanza_id => 189 },
  { :line => "His soul lived as eternityâ€™s delegate,", :no => 741, :created_at => "2013-03-05 09:16:03", :updated_at => "2013-03-05 09:16:03", :stanza_id => 190 },
  { :line => "His mind was like a fire assailing heaven,", :no => 742, :created_at => "2013-03-05 09:16:03", :updated_at => "2013-03-05 09:16:03", :stanza_id => 190 },
  { :line => "His will a hunter in the trails of light.", :no => 743, :created_at => "2013-03-05 09:16:03", :updated_at => "2013-03-05 09:16:03", :stanza_id => 190 },
  { :line => "An ocean impulse lifted every breath;", :no => 744, :created_at => "2013-03-05 09:16:03", :updated_at => "2013-03-05 09:16:03", :stanza_id => 191 },
  { :line => "Each action left the footprints of a God,", :no => 745, :created_at => "2013-03-05 09:16:03", :updated_at => "2013-03-05 09:16:03", :stanza_id => 191 },
  { :line => "Each moment was a beat of puissant wings.", :no => 746, :created_at => "2013-03-05 09:16:04", :updated_at => "2013-03-05 09:16:04", :stanza_id => 191 },
  { :line => "The little plot of our mortality", :no => 747, :created_at => "2013-03-05 09:16:04", :updated_at => "2013-03-05 09:16:04", :stanza_id => 192 },
  { :line => "Touched by this tenant from the heights became", :no => 748, :created_at => "2013-03-05 09:16:04", :updated_at => "2013-03-05 09:16:04", :stanza_id => 192 },
  { :line => "A playground of the living Infinite.", :no => 749, :created_at => "2013-03-05 09:16:04", :updated_at => "2013-03-05 09:16:04", :stanza_id => 192 },
  { :line => "This bodily appearance is not all;", :no => 750, :created_at => "2013-03-05 09:16:05", :updated_at => "2013-03-05 09:16:05", :stanza_id => 193 },
  { :line => "The form deceives, the person is a mask;", :no => 751, :created_at => "2013-03-05 09:16:05", :updated_at => "2013-03-05 09:16:05", :stanza_id => 193 },
  { :line => "Hid deep in man celestial powers can dwell.", :no => 752, :created_at => "2013-03-05 09:16:05", :updated_at => "2013-03-05 09:16:05", :stanza_id => 193 },
  { :line => "His fragile ship conveys through the sea of years", :no => 753, :created_at => "2013-03-05 09:16:05", :updated_at => "2013-03-05 09:16:05", :stanza_id => 194 },
  { :line => "An incognito of the Imperishable.", :no => 754, :created_at => "2013-03-05 09:16:06", :updated_at => "2013-03-05 09:16:06", :stanza_id => 194 },
  { :line => "A spirit that is a flame of God abides,", :no => 755, :created_at => "2013-03-05 09:16:06", :updated_at => "2013-03-05 09:16:06", :stanza_id => 195 },
  { :line => "A fiery portion of the Wonderful,", :no => 756, :created_at => "2013-03-05 09:16:06", :updated_at => "2013-03-05 09:16:06", :stanza_id => 195 },
  { :line => "Artist of his own beauty and delight,", :no => 757, :created_at => "2013-03-05 09:16:06", :updated_at => "2013-03-05 09:16:06", :stanza_id => 195 },
  { :line => "Immortal in our mortal poverty.", :no => 758, :created_at => "2013-03-05 09:16:07", :updated_at => "2013-03-05 09:16:07", :stanza_id => 195 },
  { :line => "This sculptor of the forms of the Infinite,", :no => 759, :created_at => "2013-03-05 09:16:07", :updated_at => "2013-03-05 09:16:07", :stanza_id => 196 },
  { :line => "This screened unrecognised Inhabitant,", :no => 760, :created_at => "2013-03-05 09:16:07", :updated_at => "2013-03-05 09:16:07", :stanza_id => 196 },
  { :line => "Initiate of his own veiled mysteries,", :no => 761, :created_at => "2013-03-05 09:16:07", :updated_at => "2013-03-05 09:16:07", :stanza_id => 196 },
  { :line => "Hides in a small dumb seed his cosmic thought.", :no => 762, :created_at => "2013-03-05 09:16:08", :updated_at => "2013-03-05 09:16:08", :stanza_id => 196 },
  { :line => "In the mute strength of the occult Idea", :no => 763, :created_at => "2013-03-05 09:16:08", :updated_at => "2013-03-05 09:16:08", :stanza_id => 197 },
  { :line => "Determining predestined shape and act,", :no => 764, :created_at => "2013-03-05 09:16:08", :updated_at => "2013-03-05 09:16:08", :stanza_id => 197 },
  { :line => "Passenger from life to life, from scale to scale,", :no => 765, :created_at => "2013-03-05 09:16:08", :updated_at => "2013-03-05 09:16:08", :stanza_id => 197 },
  { :line => "Changing his imaged self from form to form,", :no => 766, :created_at => "2013-03-05 09:16:08", :updated_at => "2013-03-05 09:16:08", :stanza_id => 197 },
  { :line => "He regards the icon growing by his gaze", :no => 767, :created_at => "2013-03-05 09:16:08", :updated_at => "2013-03-05 09:16:08", :stanza_id => 197 },
  { :line => "And in the worm foresees the coming god.", :no => 768, :created_at => "2013-03-05 09:16:09", :updated_at => "2013-03-05 09:16:09", :stanza_id => 197 },
  { :line => "At last the traveller in the paths of Time", :no => 769, :created_at => "2013-03-05 09:16:09", :updated_at => "2013-03-05 09:16:09", :stanza_id => 198 },
  { :line => "Arrives on the frontiers of eternity.", :no => 770, :created_at => "2013-03-05 09:16:09", :updated_at => "2013-03-05 09:16:09", :stanza_id => 198 },
  { :line => "In the transient symbol of humanity draped,", :no => 771, :created_at => "2013-03-05 09:16:09", :updated_at => "2013-03-05 09:16:09", :stanza_id => 199 },
  { :line => "He feels his substance of undying self", :no => 772, :created_at => "2013-03-05 09:16:10", :updated_at => "2013-03-05 09:16:10", :stanza_id => 199 },
  { :line => "And loses his kinship to mortality.", :no => 773, :created_at => "2013-03-05 09:16:10", :updated_at => "2013-03-05 09:16:10", :stanza_id => 199 },
  { :line => "A beam of the Eternal smites his heart,", :no => 774, :created_at => "2013-03-05 09:16:10", :updated_at => "2013-03-05 09:16:10", :stanza_id => 200 },
  { :line => "His thought stretches into infinitude:", :no => 775, :created_at => "2013-03-05 09:16:10", :updated_at => "2013-03-05 09:16:10", :stanza_id => 200 },
  { :line => "All in him turns to spirit vastnesses.", :no => 776, :created_at => "2013-03-05 09:16:11", :updated_at => "2013-03-05 09:16:11", :stanza_id => 200 },
  { :line => "His soul breaks out to join the Oversoul,", :no => 777, :created_at => "2013-03-05 09:16:11", :updated_at => "2013-03-05 09:16:11", :stanza_id => 201 },
  { :line => "His life is oceaned by that superlife.", :no => 778, :created_at => "2013-03-05 09:16:11", :updated_at => "2013-03-05 09:16:11", :stanza_id => 201 },
  { :line => "He has drunk from the breasts of the Mother of the worlds;", :no => 779, :created_at => "2013-03-05 09:16:11", :updated_at => "2013-03-05 09:16:11", :stanza_id => 202 },
  { :line => "A topless supernature fills his frame:", :no => 780, :created_at => "2013-03-05 09:16:12", :updated_at => "2013-03-05 09:16:12", :stanza_id => 202 },
  { :line => "She adopts his spiritâ€™s everlasting ground", :no => 781, :created_at => "2013-03-05 09:16:12", :updated_at => "2013-03-05 09:16:12", :stanza_id => 202 },
  { :line => "As the security of her changing world", :no => 782, :created_at => "2013-03-05 09:16:12", :updated_at => "2013-03-05 09:16:12", :stanza_id => 202 },
  { :line => "And shapes the figure of her unborn mights.", :no => 783, :created_at => "2013-03-05 09:16:12", :updated_at => "2013-03-05 09:16:12", :stanza_id => 202 },
  { :line => "Immortally she conceives herself in him,", :no => 784, :created_at => "2013-03-05 09:16:13", :updated_at => "2013-03-05 09:16:13", :stanza_id => 203 },
  { :line => "In the creature the unveiled creatrix works:", :no => 785, :created_at => "2013-03-05 09:16:13", :updated_at => "2013-03-05 09:16:13", :stanza_id => 203 },
  { :line => "Her face is seen through his face, her eyes through his eyes;", :no => 786, :created_at => "2013-03-05 09:16:13", :updated_at => "2013-03-05 09:16:13", :stanza_id => 203 },
  { :line => "Her being is his through a vast identity.", :no => 787, :created_at => "2013-03-05 09:16:13", :updated_at => "2013-03-05 09:16:13", :stanza_id => 203 },
  { :line => "Then is revealed in man the overt Divine.", :no => 788, :created_at => "2013-03-05 09:16:13", :updated_at => "2013-03-05 09:16:13", :stanza_id => 204 },
  { :line => "A static Oneness and dynamic Power", :no => 789, :created_at => "2013-03-05 09:16:14", :updated_at => "2013-03-05 09:16:14", :stanza_id => 205 },
  { :line => "Descend in him, the integral Godheadâ€™s seals;", :no => 790, :created_at => "2013-03-05 09:16:14", :updated_at => "2013-03-05 09:16:14", :stanza_id => 205 },
  { :line => "His soul and body take that splendid stamp.", :no => 791, :created_at => "2013-03-05 09:16:14", :updated_at => "2013-03-05 09:16:14", :stanza_id => 205 },
  { :line => "A long dim preparation is manâ€™s life,", :no => 792, :created_at => "2013-03-05 09:16:14", :updated_at => "2013-03-05 09:16:14", :stanza_id => 206 },
  { :line => "A circle of toil and hope and war and peace", :no => 793, :created_at => "2013-03-05 09:16:15", :updated_at => "2013-03-05 09:16:15", :stanza_id => 206 },
  { :line => "Tracked out by Life on Matterâ€™s obscure ground", :no => 794, :created_at => "2013-03-05 09:16:15", :updated_at => "2013-03-05 09:16:15", :stanza_id => 206 },
  { :line => "In his climb to a peak no feet have ever trod,", :no => 795, :created_at => "2013-03-05 09:16:15", :updated_at => "2013-03-05 09:16:15", :stanza_id => 206 },
  { :line => "He seeks through a penumbra shot with flame", :no => 796, :created_at => "2013-03-05 09:16:15", :updated_at => "2013-03-05 09:16:15", :stanza_id => 206 },
  { :line => "A veiled reality half-known, ever missed,", :no => 797, :created_at => "2013-03-05 09:16:15", :updated_at => "2013-03-05 09:16:15", :stanza_id => 206 },
  { :line => "A search for something or someone never found,", :no => 798, :created_at => "2013-03-05 09:16:15", :updated_at => "2013-03-05 09:16:15", :stanza_id => 206 },
  { :line => "Cult of an ideal never made real here,", :no => 799, :created_at => "2013-03-05 09:16:16", :updated_at => "2013-03-05 09:16:16", :stanza_id => 206 },
  { :line => "An endless spiral of ascent and fall", :no => 800, :created_at => "2013-03-05 09:16:16", :updated_at => "2013-03-05 09:16:16", :stanza_id => 206 },
  { :line => "Until at last is reached the giant point", :no => 801, :created_at => "2013-03-05 09:16:16", :updated_at => "2013-03-05 09:16:16", :stanza_id => 206 },
  { :line => "Through which his Glory shines for whom we were made", :no => 802, :created_at => "2013-03-05 09:16:16", :updated_at => "2013-03-05 09:16:16", :stanza_id => 206 },
  { :line => "And we break into the infinity of God.", :no => 803, :created_at => "2013-03-05 09:16:16", :updated_at => "2013-03-05 09:16:16", :stanza_id => 206 },
  { :line => "Across our natureâ€™s border line we escape", :no => 804, :created_at => "2013-03-05 09:16:17", :updated_at => "2013-03-05 09:16:17", :stanza_id => 207 },
  { :line => "Into supernatureâ€™s arc of living light.", :no => 805, :created_at => "2013-03-05 09:16:17", :updated_at => "2013-03-05 09:16:17", :stanza_id => 207 },
  { :line => "This now was witnessed in that son of Force,", :no => 806, :created_at => "2013-03-05 09:16:17", :updated_at => "2013-03-05 09:16:17", :stanza_id => 208 },
  { :line => "In him that high transition laid its base.", :no => 807, :created_at => "2013-03-05 09:16:18", :updated_at => "2013-03-05 09:16:18", :stanza_id => 208 },
  { :line => "Original and supernal Immanence", :no => 808, :created_at => "2013-03-05 09:16:18", :updated_at => "2013-03-05 09:16:18", :stanza_id => 209 },
  { :line => "Of which all Natureâ€™s process is the art,", :no => 809, :created_at => "2013-03-05 09:16:18", :updated_at => "2013-03-05 09:16:18", :stanza_id => 209 },
  { :line => "The cosmic Worker set his secret hand", :no => 810, :created_at => "2013-03-05 09:16:18", :updated_at => "2013-03-05 09:16:18", :stanza_id => 209 },
  { :line => "To turn this frail mud-engine to heaven-use.", :no => 811, :created_at => "2013-03-05 09:16:19", :updated_at => "2013-03-05 09:16:19", :stanza_id => 209 },
  { :line => "A Presence wrought behind the ambiguous screen:", :no => 812, :created_at => "2013-03-05 09:16:19", :updated_at => "2013-03-05 09:16:19", :stanza_id => 210 },
  { :line => "It beat his soil to bear a Titanâ€™s weight,", :no => 813, :created_at => "2013-03-05 09:16:19", :updated_at => "2013-03-05 09:16:19", :stanza_id => 210 },
  { :line => "Refining half-hewn blocks of natural strength", :no => 814, :created_at => "2013-03-05 09:16:19", :updated_at => "2013-03-05 09:16:19", :stanza_id => 210 },
  { :line => "It built his soul into a statued God.", :no => 815, :created_at => "2013-03-05 09:16:19", :updated_at => "2013-03-05 09:16:19", :stanza_id => 210 },
  { :line => "The Craftsman of the magic stuff of self", :no => 816, :created_at => "2013-03-05 09:16:20", :updated_at => "2013-03-05 09:16:20", :stanza_id => 211 },
  { :line => "Who labours at his high and difficult plan", :no => 817, :created_at => "2013-03-05 09:16:20", :updated_at => "2013-03-05 09:16:20", :stanza_id => 211 },
  { :line => "In the wide workshop of the wonderful world,", :no => 818, :created_at => "2013-03-05 09:16:20", :updated_at => "2013-03-05 09:16:20", :stanza_id => 211 },
  { :line => "Modelled in inward Time his rhythmic parts.", :no => 819, :created_at => "2013-03-05 09:16:20", :updated_at => "2013-03-05 09:16:20", :stanza_id => 211 },
  { :line => "Then came the abrupt transcendent miracle:", :no => 820, :created_at => "2013-03-05 09:16:21", :updated_at => "2013-03-05 09:16:21", :stanza_id => 212 },
  { :line => "The masked immaculate Grandeur could outline,", :no => 821, :created_at => "2013-03-05 09:16:21", :updated_at => "2013-03-05 09:16:21", :stanza_id => 212 },
  { :line => "At travail in the occult womb of life,", :no => 822, :created_at => "2013-03-05 09:16:21", :updated_at => "2013-03-05 09:16:21", :stanza_id => 212 },
  { :line => "His dreamed magnificence of things to be.", :no => 823, :created_at => "2013-03-05 09:16:21", :updated_at => "2013-03-05 09:16:21", :stanza_id => 212 },
  { :line => "A crown of the architecture of the worlds,", :no => 824, :created_at => "2013-03-05 09:16:22", :updated_at => "2013-03-05 09:16:22", :stanza_id => 213 },
  { :line => "A mystery of married Earth and Heaven", :no => 825, :created_at => "2013-03-05 09:16:22", :updated_at => "2013-03-05 09:16:22", :stanza_id => 213 },
  { :line => "Annexed divinity to the mortal scheme.", :no => 826, :created_at => "2013-03-05 09:16:22", :updated_at => "2013-03-05 09:16:22", :stanza_id => 213 },
  { :line => "A Seer was born, a shining Guest of Time.", :no => 827, :created_at => "2013-03-05 09:16:22", :updated_at => "2013-03-05 09:16:22", :stanza_id => 214 },
  { :line => "For him mindâ€™s limiting firmament ceased above,", :no => 828, :created_at => "2013-03-05 09:16:23", :updated_at => "2013-03-05 09:16:23", :stanza_id => 215 },
  { :line => "In the griffin forefront of the Night and Day", :no => 829, :created_at => "2013-03-05 09:16:23", :updated_at => "2013-03-05 09:16:23", :stanza_id => 215 },
  { :line => "A gap was rent in the all-concealing vault;", :no => 830, :created_at => "2013-03-05 09:16:23", :updated_at => "2013-03-05 09:16:23", :stanza_id => 215 },
  { :line => "The conscious ends of being went rolling back:", :no => 831, :created_at => "2013-03-05 09:16:23", :updated_at => "2013-03-05 09:16:23", :stanza_id => 215 },
  { :line => "The landmarks of the little person fell,", :no => 832, :created_at => "2013-03-05 09:16:23", :updated_at => "2013-03-05 09:16:23", :stanza_id => 215 },
  { :line => "The island ego joined its continent:", :no => 833, :created_at => "2013-03-05 09:16:24", :updated_at => "2013-03-05 09:16:24", :stanza_id => 215 },
  { :line => "Overpassed was this world of rigid limiting forms:", :no => 834, :created_at => "2013-03-05 09:16:24", :updated_at => "2013-03-05 09:16:24", :stanza_id => 215 },
  { :line => "Lifeâ€™s barriers opened into the Unknown.", :no => 835, :created_at => "2013-03-05 09:16:24", :updated_at => "2013-03-05 09:16:24", :stanza_id => 215 },
  { :line => "Abolished were conceptionâ€™s covenants", :no => 836, :created_at => "2013-03-05 09:16:24", :updated_at => "2013-03-05 09:16:24", :stanza_id => 216 },
  { :line => "And, striking off subjectionâ€™s rigorous clause", :no => 837, :created_at => "2013-03-05 09:16:25", :updated_at => "2013-03-05 09:16:25", :stanza_id => 216 },
  { :line => "Annulled the soulâ€™s treaty with Natureâ€™s nescience.", :no => 838, :created_at => "2013-03-05 09:16:25", :updated_at => "2013-03-05 09:16:25", :stanza_id => 216 },
  { :line => "All the grey inhibitions were torn off", :no => 839, :created_at => "2013-03-05 09:16:25", :updated_at => "2013-03-05 09:16:25", :stanza_id => 217 },
  { :line => "And broken the intellectâ€™s hard and lustrous lid;", :no => 840, :created_at => "2013-03-05 09:16:25", :updated_at => "2013-03-05 09:16:25", :stanza_id => 217 },
  { :line => "Truth unpartitioned found immense sky-room;", :no => 841, :created_at => "2013-03-05 09:16:25", :updated_at => "2013-03-05 09:16:25", :stanza_id => 217 },
  { :line => "An empyrean vision saw and knew;", :no => 842, :created_at => "2013-03-05 09:16:26", :updated_at => "2013-03-05 09:16:26", :stanza_id => 217 },
  { :line => "The bounded mind became a boundless light,", :no => 843, :created_at => "2013-03-05 09:16:26", :updated_at => "2013-03-05 09:16:26", :stanza_id => 217 },
  { :line => "The finite self mated with Infinity.", :no => 844, :created_at => "2013-03-05 09:16:26", :updated_at => "2013-03-05 09:16:26", :stanza_id => 217 },
  { :line => "His march now soared into an eagleâ€™s flight.", :no => 845, :created_at => "2013-03-05 09:16:26", :updated_at => "2013-03-05 09:16:26", :stanza_id => 218 },
  { :line => "Out of apprenticeship to Ignorance", :no => 846, :created_at => "2013-03-05 09:16:27", :updated_at => "2013-03-05 09:16:27", :stanza_id => 219 },
  { :line => "Wisdom upraised him to her master craft", :no => 847, :created_at => "2013-03-05 09:16:27", :updated_at => "2013-03-05 09:16:27", :stanza_id => 219 },
  { :line => "And made him an arch-mason of the soul,", :no => 848, :created_at => "2013-03-05 09:16:27", :updated_at => "2013-03-05 09:16:27", :stanza_id => 219 },
  { :line => "A builder of the Immortalâ€™s secret house,", :no => 849, :created_at => "2013-03-05 09:16:28", :updated_at => "2013-03-05 09:16:28", :stanza_id => 219 },
  { :line => "An aspirant to supernal Timelessness:", :no => 850, :created_at => "2013-03-05 09:16:28", :updated_at => "2013-03-05 09:16:28", :stanza_id => 219 },
  { :line => "Freedom and empire called to him from on high;", :no => 851, :created_at => "2013-03-05 09:16:28", :updated_at => "2013-03-05 09:16:28", :stanza_id => 219 },
  { :line => "Above mindâ€™s twilight and lifeâ€™s star-led night", :no => 852, :created_at => "2013-03-05 09:16:28", :updated_at => "2013-03-05 09:16:28", :stanza_id => 219 },
  { :line => "There gleamed the dawn of a spiritual day.", :no => 853, :created_at => "2013-03-05 09:16:28", :updated_at => "2013-03-05 09:16:28", :stanza_id => 219 },
  { :line => "As so he grew into his larger self,", :no => 854, :created_at => "2013-03-05 09:16:29", :updated_at => "2013-03-05 09:16:29", :stanza_id => 220 },
  { :line => "Humanity framed his movements less and less,", :no => 855, :created_at => "2013-03-05 09:16:29", :updated_at => "2013-03-05 09:16:29", :stanza_id => 220 },
  { :line => "A greater being saw a greater world.", :no => 856, :created_at => "2013-03-05 09:16:29", :updated_at => "2013-03-05 09:16:29", :stanza_id => 220 },
  { :line => "A fearless will for knowledge dared to erase", :no => 857, :created_at => "2013-03-05 09:16:29", :updated_at => "2013-03-05 09:16:29", :stanza_id => 221 },
  { :line => "The lines of safety reason draws that bar", :no => 858, :created_at => "2013-03-05 09:16:29", :updated_at => "2013-03-05 09:16:29", :stanza_id => 221 },
  { :line => "Mindâ€™s soar, soulâ€™s dive into the Infinite.", :no => 859, :created_at => "2013-03-05 09:16:30", :updated_at => "2013-03-05 09:16:30", :stanza_id => 221 },
  { :line => "Even his first steps broke our small earth-bounds", :no => 860, :created_at => "2013-03-05 09:16:30", :updated_at => "2013-03-05 09:16:30", :stanza_id => 222 },
  { :line => "And loitered in a vaster freer air.", :no => 861, :created_at => "2013-03-05 09:16:30", :updated_at => "2013-03-05 09:16:30", :stanza_id => 222 },
  { :line => "In hands sustained by a transfiguring Might", :no => 862, :created_at => "2013-03-05 09:16:30", :updated_at => "2013-03-05 09:16:30", :stanza_id => 223 },
  { :line => "He caught up lightly like a giantâ€™s bow", :no => 863, :created_at => "2013-03-05 09:16:31", :updated_at => "2013-03-05 09:16:31", :stanza_id => 223 },
  { :line => "Left slumbering in a sealed and secret cave", :no => 864, :created_at => "2013-03-05 09:16:31", :updated_at => "2013-03-05 09:16:31", :stanza_id => 223 },
  { :line => "The powers that sleep unused in man within.", :no => 865, :created_at => "2013-03-05 09:16:31", :updated_at => "2013-03-05 09:16:31", :stanza_id => 223 },
  { :line => "He made of miracle a normal act", :no => 866, :created_at => "2013-03-05 09:16:31", :updated_at => "2013-03-05 09:16:31", :stanza_id => 224 },
  { :line => "And, turned to a common part of divine works,", :no => 867, :created_at => "2013-03-05 09:16:32", :updated_at => "2013-03-05 09:16:32", :stanza_id => 224 },
  { :line => "Magnificently natural at this height", :no => 868, :created_at => "2013-03-05 09:16:32", :updated_at => "2013-03-05 09:16:32", :stanza_id => 224 },
  { :line => "Efforts that would shatter the strength of mortal hearts,", :no => 869, :created_at => "2013-03-05 09:16:32", :updated_at => "2013-03-05 09:16:32", :stanza_id => 224 },
  { :line => "Pursued in a royalty of mighty ease", :no => 870, :created_at => "2013-03-05 09:16:32", :updated_at => "2013-03-05 09:16:32", :stanza_id => 224 },
  { :line => "Aims too sublime for Natureâ€™s daily will:", :no => 871, :created_at => "2013-03-05 09:16:32", :updated_at => "2013-03-05 09:16:32", :stanza_id => 224 },
  { :line => "The gifts of the spirit crowding came to him;", :no => 872, :created_at => "2013-03-05 09:16:33", :updated_at => "2013-03-05 09:16:33", :stanza_id => 224 },
  { :line => "They were his lifeâ€™s pattern and his privilege.", :no => 873, :created_at => "2013-03-05 09:16:33", :updated_at => "2013-03-05 09:16:33", :stanza_id => 224 },
  { :line => "A pure perception lent its lucent joy:", :no => 874, :created_at => "2013-03-05 09:16:33", :updated_at => "2013-03-05 09:16:33", :stanza_id => 225 },
  { :line => "Its intimate vision waited not to think;", :no => 875, :created_at => "2013-03-05 09:16:33", :updated_at => "2013-03-05 09:16:33", :stanza_id => 225 },
  { :line => "It enveloped all Nature in a single glance,", :no => 876, :created_at => "2013-03-05 09:16:34", :updated_at => "2013-03-05 09:16:34", :stanza_id => 225 },
  { :line => "It looked into the very self of things;", :no => 877, :created_at => "2013-03-05 09:16:34", :updated_at => "2013-03-05 09:16:34", :stanza_id => 225 },
  { :line => "Deceived no more by form he saw the soul.", :no => 878, :created_at => "2013-03-05 09:16:34", :updated_at => "2013-03-05 09:16:34", :stanza_id => 225 },
  { :line => "In beings it knew what lurked to them unknown;", :no => 879, :created_at => "2013-03-05 09:16:34", :updated_at => "2013-03-05 09:16:34", :stanza_id => 226 },
  { :line => "It seized the idea in mind, the wish in the heart;", :no => 880, :created_at => "2013-03-05 09:16:34", :updated_at => "2013-03-05 09:16:34", :stanza_id => 226 },
  { :line => "It plucked out from grey folds of secrecy", :no => 881, :created_at => "2013-03-05 09:16:35", :updated_at => "2013-03-05 09:16:35", :stanza_id => 226 },
  { :line => "The motives which from their own sight men hide.", :no => 882, :created_at => "2013-03-05 09:16:35", :updated_at => "2013-03-05 09:16:35", :stanza_id => 226 },
  { :line => "He felt the beating life in other men", :no => 883, :created_at => "2013-03-05 09:16:35", :updated_at => "2013-03-05 09:16:35", :stanza_id => 227 },
  { :line => "Invade him with their happiness and their grief;", :no => 884, :created_at => "2013-03-05 09:16:35", :updated_at => "2013-03-05 09:16:35", :stanza_id => 227 },
  { :line => "Their love, their anger, their unspoken hopes", :no => 885, :created_at => "2013-03-05 09:16:35", :updated_at => "2013-03-05 09:16:35", :stanza_id => 227 },
  { :line => "Entered in currents or in pouring waves", :no => 886, :created_at => "2013-03-05 09:16:36", :updated_at => "2013-03-05 09:16:36", :stanza_id => 227 },
  { :line => "Into the immobile ocean of his calm.", :no => 887, :created_at => "2013-03-05 09:16:36", :updated_at => "2013-03-05 09:16:36", :stanza_id => 227 },
  { :line => "He heard the inspired sound of his own thoughts", :no => 888, :created_at => "2013-03-05 09:16:36", :updated_at => "2013-03-05 09:16:36", :stanza_id => 228 },
  { :line => "Re-echoed in the vault of other minds;", :no => 889, :created_at => "2013-03-05 09:16:36", :updated_at => "2013-03-05 09:16:36", :stanza_id => 228 },
  { :line => "The worldâ€™s thought-streams travelled into his ken;", :no => 890, :created_at => "2013-03-05 09:16:36", :updated_at => "2013-03-05 09:16:36", :stanza_id => 228 },
  { :line => "His inner self grew near to othersâ€™ selves", :no => 891, :created_at => "2013-03-05 09:16:37", :updated_at => "2013-03-05 09:16:37", :stanza_id => 228 },
  { :line => "And bore a kinshipâ€™s weight, a common tie,", :no => 892, :created_at => "2013-03-05 09:16:37", :updated_at => "2013-03-05 09:16:37", :stanza_id => 228 },
  { :line => "Yet stood untouched, king of itself, alone.", :no => 893, :created_at => "2013-03-05 09:16:37", :updated_at => "2013-03-05 09:16:37", :stanza_id => 228 },
  { :line => "A magical accord quickened and attuned", :no => 894, :created_at => "2013-03-05 09:16:37", :updated_at => "2013-03-05 09:16:37", :stanza_id => 229 },
  { :line => "To ethereal symphonies the old earthy strings;", :no => 895, :created_at => "2013-03-05 09:16:38", :updated_at => "2013-03-05 09:16:38", :stanza_id => 229 },
  { :line => "It raised the servitors of mind and life", :no => 896, :created_at => "2013-03-05 09:16:38", :updated_at => "2013-03-05 09:16:38", :stanza_id => 229 },
  { :line => "To be happy partners in the soulâ€™s response,", :no => 897, :created_at => "2013-03-05 09:16:38", :updated_at => "2013-03-05 09:16:38", :stanza_id => 229 },
  { :line => "Tissue and nerve were turned to sensitive chords,", :no => 898, :created_at => "2013-03-05 09:16:38", :updated_at => "2013-03-05 09:16:38", :stanza_id => 229 },
  { :line => "Records of lustre and ecstasy; it made", :no => 899, :created_at => "2013-03-05 09:16:38", :updated_at => "2013-03-05 09:16:38", :stanza_id => 229 },
  { :line => "The bodyâ€™s means the spiritâ€™s acolytes.", :no => 900, :created_at => "2013-03-05 09:16:39", :updated_at => "2013-03-05 09:16:39", :stanza_id => 229 },
  { :line => "A heavenlier function with a finer mode", :no => 901, :created_at => "2013-03-05 09:16:39", :updated_at => "2013-03-05 09:16:39", :stanza_id => 230 },
  { :line => "Lit with its grace manâ€™s outward earthliness;", :no => 902, :created_at => "2013-03-05 09:16:39", :updated_at => "2013-03-05 09:16:39", :stanza_id => 230 },
  { :line => "The soulâ€™s experience of its deeper sheaths", :no => 903, :created_at => "2013-03-05 09:16:39", :updated_at => "2013-03-05 09:16:39", :stanza_id => 230 },
  { :line => "No more slept drugged by Matterâ€™s dominance.", :no => 904, :created_at => "2013-03-05 09:16:39", :updated_at => "2013-03-05 09:16:39", :stanza_id => 230 },
  { :line => "In the dead wall closing us from wider self,", :no => 905, :created_at => "2013-03-05 09:16:40", :updated_at => "2013-03-05 09:16:40", :stanza_id => 231 },
  { :line => "Into a secrecy of apparent sleep,", :no => 906, :created_at => "2013-03-05 09:16:40", :updated_at => "2013-03-05 09:16:40", :stanza_id => 231 },
  { :line => "The mystic tract beyond our waking thoughts,", :no => 907, :created_at => "2013-03-05 09:16:40", :updated_at => "2013-03-05 09:16:40", :stanza_id => 231 },
  { :line => "A door parted, built in by Matterâ€™s force,", :no => 908, :created_at => "2013-03-05 09:16:40", :updated_at => "2013-03-05 09:16:40", :stanza_id => 231 },
  { :line => "Releasing things unseized by earthly sense:", :no => 909, :created_at => "2013-03-05 09:16:40", :updated_at => "2013-03-05 09:16:40", :stanza_id => 231 },
  { :line => "A world unseen, unknown by outward mind", :no => 910, :created_at => "2013-03-05 09:16:41", :updated_at => "2013-03-05 09:16:41", :stanza_id => 231 },
  { :line => "Appeared in the silent spaces of the soul.", :no => 911, :created_at => "2013-03-05 09:16:41", :updated_at => "2013-03-05 09:16:41", :stanza_id => 231 },
  { :line => "He sat in secret chambers looking out", :no => 912, :created_at => "2013-03-05 09:16:41", :updated_at => "2013-03-05 09:16:41", :stanza_id => 232 },
  { :line => "Into the luminous countries of the unborn", :no => 913, :created_at => "2013-03-05 09:16:41", :updated_at => "2013-03-05 09:16:41", :stanza_id => 232 },
  { :line => "Where all things dreamed by the mind are seen and true", :no => 914, :created_at => "2013-03-05 09:16:42", :updated_at => "2013-03-05 09:16:42", :stanza_id => 232 },
  { :line => "And all that the life longs for is drawn close.", :no => 915, :created_at => "2013-03-05 09:16:42", :updated_at => "2013-03-05 09:16:42", :stanza_id => 232 },
  { :line => "He saw the Perfect in their starry homes", :no => 916, :created_at => "2013-03-05 09:16:42", :updated_at => "2013-03-05 09:16:42", :stanza_id => 233 },
  { :line => "Wearing the glory of a deathless form", :no => 917, :created_at => "2013-03-05 09:16:42", :updated_at => "2013-03-05 09:16:42", :stanza_id => 233 },
  { :line => "Lain in the arms of the Eternalâ€™s peace,", :no => 918, :created_at => "2013-03-05 09:16:42", :updated_at => "2013-03-05 09:16:42", :stanza_id => 233 },
  { :line => "Rapt in the heart-beats of God-ecstasy.", :no => 919, :created_at => "2013-03-05 09:16:43", :updated_at => "2013-03-05 09:16:43", :stanza_id => 233 },
  { :line => "He lived in the mystic space where thought is born", :no => 920, :created_at => "2013-03-05 09:16:43", :updated_at => "2013-03-05 09:16:43", :stanza_id => 234 },
  { :line => "And will is nursed by an ethereal Power", :no => 921, :created_at => "2013-03-05 09:16:43", :updated_at => "2013-03-05 09:16:43", :stanza_id => 234 },
  { :line => "And fed on the white milk of the Eternalâ€™s strengths", :no => 922, :created_at => "2013-03-05 09:16:44", :updated_at => "2013-03-05 09:16:44", :stanza_id => 234 },
  { :line => "Till it grows into the likeness of a god.", :no => 923, :created_at => "2013-03-05 09:16:44", :updated_at => "2013-03-05 09:16:44", :stanza_id => 234 },
  { :line => "In the Witnessâ€™s occult rooms with mind-built walls", :no => 924, :created_at => "2013-03-05 09:16:44", :updated_at => "2013-03-05 09:16:44", :stanza_id => 235 },
  { :line => "On hidden interiors, lurking passages", :no => 925, :created_at => "2013-03-05 09:16:44", :updated_at => "2013-03-05 09:16:44", :stanza_id => 235 },
  { :line => "Opened the windows of the inner sight.", :no => 926, :created_at => "2013-03-05 09:16:45", :updated_at => "2013-03-05 09:16:45", :stanza_id => 235 },
  { :line => "He owned the house of undivided Time.", :no => 927, :created_at => "2013-03-05 09:16:45", :updated_at => "2013-03-05 09:16:45", :stanza_id => 236 },
  { :line => "Lifting the heavy curtain of the flesh", :no => 928, :created_at => "2013-03-05 09:16:45", :updated_at => "2013-03-05 09:16:45", :stanza_id => 237 },
  { :line => "He stood upon a threshold serpent-watched,", :no => 929, :created_at => "2013-03-05 09:16:45", :updated_at => "2013-03-05 09:16:45", :stanza_id => 237 },
  { :line => "And peered into gleaming endless corridors,", :no => 930, :created_at => "2013-03-05 09:16:46", :updated_at => "2013-03-05 09:16:46", :stanza_id => 237 },
  { :line => "Silent and listening in the silent heart", :no => 931, :created_at => "2013-03-05 09:16:46", :updated_at => "2013-03-05 09:16:46", :stanza_id => 237 },
  { :line => "For the coming of the new and the unknown.", :no => 932, :created_at => "2013-03-05 09:16:46", :updated_at => "2013-03-05 09:16:46", :stanza_id => 237 },
  { :line => "He gazed across the empty stillnesses", :no => 933, :created_at => "2013-03-05 09:16:46", :updated_at => "2013-03-05 09:16:46", :stanza_id => 238 },
  { :line => "And heard the footsteps of the undreamed Idea", :no => 934, :created_at => "2013-03-05 09:16:46", :updated_at => "2013-03-05 09:16:46", :stanza_id => 238 },
  { :line => "In the far avenues of the Beyond.", :no => 935, :created_at => "2013-03-05 09:16:47", :updated_at => "2013-03-05 09:16:47", :stanza_id => 238 },
  { :line => "He heard the secret Voice, the Word that knows,", :no => 936, :created_at => "2013-03-05 09:16:47", :updated_at => "2013-03-05 09:16:47", :stanza_id => 239 },
  { :line => "And saw the secret face that is our own.", :no => 937, :created_at => "2013-03-05 09:16:47", :updated_at => "2013-03-05 09:16:47", :stanza_id => 239 },
  { :line => "The inner planes uncovered their crystal doors;", :no => 938, :created_at => "2013-03-05 09:16:48", :updated_at => "2013-03-05 09:16:48", :stanza_id => 240 },
  { :line => "Strange powers and influences touched his life.", :no => 939, :created_at => "2013-03-05 09:16:48", :updated_at => "2013-03-05 09:16:48", :stanza_id => 240 },
  { :line => "A vision came of higher realms than ours,", :no => 940, :created_at => "2013-03-05 09:16:48", :updated_at => "2013-03-05 09:16:48", :stanza_id => 241 },
  { :line => "A consciousness of brighter fields and skies,", :no => 941, :created_at => "2013-03-05 09:16:48", :updated_at => "2013-03-05 09:16:48", :stanza_id => 241 },
  { :line => "Of beings less circumscribed than brief-lived men", :no => 942, :created_at => "2013-03-05 09:16:48", :updated_at => "2013-03-05 09:16:48", :stanza_id => 241 },
  { :line => "And subtler bodies than these passing frames,", :no => 943, :created_at => "2013-03-05 09:16:49", :updated_at => "2013-03-05 09:16:49", :stanza_id => 241 },
  { :line => "Objects too fine for our material grasp,", :no => 944, :created_at => "2013-03-05 09:16:49", :updated_at => "2013-03-05 09:16:49", :stanza_id => 241 },
  { :line => "Acts vibrant with a superhuman light", :no => 945, :created_at => "2013-03-05 09:16:49", :updated_at => "2013-03-05 09:16:49", :stanza_id => 241 },
  { :line => "And movements pushed by a superconscient force,", :no => 946, :created_at => "2013-03-05 09:16:49", :updated_at => "2013-03-05 09:16:49", :stanza_id => 241 },
  { :line => "And joys that never flowed through mortal limbs,", :no => 947, :created_at => "2013-03-05 09:16:49", :updated_at => "2013-03-05 09:16:49", :stanza_id => 241 },
  { :line => "And lovelier scenes than earthâ€™s and happier lives.", :no => 948, :created_at => "2013-03-05 09:16:50", :updated_at => "2013-03-05 09:16:50", :stanza_id => 241 },
  { :line => "A consciousness of beauty and of bliss,", :no => 949, :created_at => "2013-03-05 09:16:50", :updated_at => "2013-03-05 09:16:50", :stanza_id => 242 },
  { :line => "A knowledge which became what it perceived,", :no => 950, :created_at => "2013-03-05 09:16:50", :updated_at => "2013-03-05 09:16:50", :stanza_id => 242 },
  { :line => "Replaced the separated sense and heart", :no => 951, :created_at => "2013-03-05 09:16:50", :updated_at => "2013-03-05 09:16:50", :stanza_id => 242 },
  { :line => "And drew all Nature into its embrace.", :no => 952, :created_at => "2013-03-05 09:16:50", :updated_at => "2013-03-05 09:16:50", :stanza_id => 242 },
  { :line => "The mind leaned out to meet the hidden worlds.", :no => 953, :created_at => "2013-03-05 09:16:51", :updated_at => "2013-03-05 09:16:51", :stanza_id => 243 },
  { :line => "Air glowed and teemed with marvellous shapes and hues,", :no => 954, :created_at => "2013-03-05 09:16:51", :updated_at => "2013-03-05 09:16:51", :stanza_id => 244 },
  { :line => "In the nostrils quivered celestial fragrances,", :no => 955, :created_at => "2013-03-05 09:16:51", :updated_at => "2013-03-05 09:16:51", :stanza_id => 244 },
  { :line => "On the tongue lingered the honey of paradise.", :no => 956, :created_at => "2013-03-05 09:16:51", :updated_at => "2013-03-05 09:16:51", :stanza_id => 244 },
  { :line => "A channel of universal harmony,", :no => 957, :created_at => "2013-03-05 09:16:52", :updated_at => "2013-03-05 09:16:52", :stanza_id => 245 },
  { :line => "Hearing was a stream of magic audience,", :no => 958, :created_at => "2013-03-05 09:16:52", :updated_at => "2013-03-05 09:16:52", :stanza_id => 245 },
  { :line => "A bed for occult sounds earth cannot hear.", :no => 959, :created_at => "2013-03-05 09:16:52", :updated_at => "2013-03-05 09:16:52", :stanza_id => 245 },
  { :line => "Out of a covert tract of slumber self", :no => 960, :created_at => "2013-03-05 09:16:53", :updated_at => "2013-03-05 09:16:53", :stanza_id => 246 },
  { :line => "The voice came of a truth submerged, unknown", :no => 961, :created_at => "2013-03-05 09:16:53", :updated_at => "2013-03-05 09:16:53", :stanza_id => 246 },
  { :line => "That flows beneath the cosmic surfaces", :no => 962, :created_at => "2013-03-05 09:16:53", :updated_at => "2013-03-05 09:16:53", :stanza_id => 246 },
  { :line => "Only mid an omniscient silence heard,", :no => 963, :created_at => "2013-03-05 09:16:53", :updated_at => "2013-03-05 09:16:53", :stanza_id => 246 },
  { :line => "Held by intuitive heart and secret sense.", :no => 964, :created_at => "2013-03-05 09:16:53", :updated_at => "2013-03-05 09:16:53", :stanza_id => 246 },
  { :line => "It caught the burden of secrecies sealed and dumb,", :no => 965, :created_at => "2013-03-05 09:16:54", :updated_at => "2013-03-05 09:16:54", :stanza_id => 247 },
  { :line => "It voiced the unfulfilled demand of earth", :no => 966, :created_at => "2013-03-05 09:16:54", :updated_at => "2013-03-05 09:16:54", :stanza_id => 247 },
  { :line => "And the song of promise of unrealised heavens", :no => 967, :created_at => "2013-03-05 09:16:54", :updated_at => "2013-03-05 09:16:54", :stanza_id => 247 },
  { :line => "And all that hides in an omnipotent Sleep.", :no => 968, :created_at => "2013-03-05 09:16:54", :updated_at => "2013-03-05 09:16:54", :stanza_id => 247 },
  { :line => "In the unceasing drama carried by Time", :no => 969, :created_at => "2013-03-05 09:16:55", :updated_at => "2013-03-05 09:16:55", :stanza_id => 248 },
  { :line => "On its long listening flood that bears the worldâ€™s", :no => 970, :created_at => "2013-03-05 09:16:55", :updated_at => "2013-03-05 09:16:55", :stanza_id => 248 },
  { :line => "Insoluble doubt on a pilgrimage without goal,", :no => 971, :created_at => "2013-03-05 09:16:55", :updated_at => "2013-03-05 09:16:55", :stanza_id => 248 },
  { :line => "A laughter of sleepless pleasure foamed and spumed", :no => 972, :created_at => "2013-03-05 09:16:55", :updated_at => "2013-03-05 09:16:55", :stanza_id => 248 },
  { :line => "And murmurings of desire that cannot die:", :no => 973, :created_at => "2013-03-05 09:16:55", :updated_at => "2013-03-05 09:16:55", :stanza_id => 248 },
  { :line => "A cry came of the worldâ€™s delight to be,", :no => 974, :created_at => "2013-03-05 09:16:56", :updated_at => "2013-03-05 09:16:56", :stanza_id => 248 },
  { :line => "The grandeur and greatness of its will to live,", :no => 975, :created_at => "2013-03-05 09:16:56", :updated_at => "2013-03-05 09:16:56", :stanza_id => 248 },
  { :line => "Recall of the soulâ€™s adventure into space,", :no => 976, :created_at => "2013-03-05 09:16:56", :updated_at => "2013-03-05 09:16:56", :stanza_id => 248 },
  { :line => "A traveller through the magic centuries", :no => 977, :created_at => "2013-03-05 09:16:56", :updated_at => "2013-03-05 09:16:56", :stanza_id => 248 },
  { :line => "And beingâ€™s labour in Matterâ€™s universe,", :no => 978, :created_at => "2013-03-05 09:16:56", :updated_at => "2013-03-05 09:16:56", :stanza_id => 248 },
  { :line => "Its search for the mystic meaning of its birth", :no => 979, :created_at => "2013-03-05 09:16:57", :updated_at => "2013-03-05 09:16:57", :stanza_id => 248 },
  { :line => "And joy of high spiritual response", :no => 980, :created_at => "2013-03-05 09:16:57", :updated_at => "2013-03-05 09:16:57", :stanza_id => 248 },
  { :line => "Its throb of satisfaction and content,", :no => 981, :created_at => "2013-03-05 09:16:57", :updated_at => "2013-03-05 09:16:57", :stanza_id => 248 },
  { :line => "In all the sweetness of the gifts of life,", :no => 982, :created_at => "2013-03-05 09:16:57", :updated_at => "2013-03-05 09:16:57", :stanza_id => 248 },
  { :line => "Its large breath and pulse and thrill of hope and fear,", :no => 983, :created_at => "2013-03-05 09:16:58", :updated_at => "2013-03-05 09:16:58", :stanza_id => 248 },
  { :line => "Its taste of pangs and tears and ecstasy,", :no => 984, :created_at => "2013-03-05 09:16:58", :updated_at => "2013-03-05 09:16:58", :stanza_id => 248 },
  { :line => "Its raptureâ€™s poignant beat of sudden bliss,", :no => 985, :created_at => "2013-03-05 09:16:58", :updated_at => "2013-03-05 09:16:58", :stanza_id => 248 },
  { :line => "The sob of its passion and unending pain.", :no => 986, :created_at => "2013-03-05 09:16:58", :updated_at => "2013-03-05 09:16:58", :stanza_id => 248 },
  { :line => "The murmur and whisper of the unheard sounds", :no => 987, :created_at => "2013-03-05 09:16:59", :updated_at => "2013-03-05 09:16:59", :stanza_id => 249 },
  { :line => "Which crowd around our hearts but find no window", :no => 988, :created_at => "2013-03-05 09:16:59", :updated_at => "2013-03-05 09:16:59", :stanza_id => 249 },
  { :line => "To enter, swelled into a canticle", :no => 989, :created_at => "2013-03-05 09:16:59", :updated_at => "2013-03-05 09:16:59", :stanza_id => 249 },
  { :line => "Of all that suffers to be still unknown", :no => 990, :created_at => "2013-03-05 09:16:59", :updated_at => "2013-03-05 09:16:59", :stanza_id => 249 },
  { :line => "And all that labours vainly to be born", :no => 991, :created_at => "2013-03-05 09:16:59", :updated_at => "2013-03-05 09:16:59", :stanza_id => 249 },
  { :line => "And all the sweetness none will ever taste", :no => 992, :created_at => "2013-03-05 09:16:59", :updated_at => "2013-03-05 09:16:59", :stanza_id => 249 },
  { :line => "And all the beauty that will never be.", :no => 993, :created_at => "2013-03-05 09:17:00", :updated_at => "2013-03-05 09:17:00", :stanza_id => 249 },
  { :line => "Inaudible to our deaf mortal ears", :no => 994, :created_at => "2013-03-05 09:17:00", :updated_at => "2013-03-05 09:17:00", :stanza_id => 250 },
  { :line => "The wide world-rhythms wove their stupendous chant", :no => 995, :created_at => "2013-03-05 09:17:00", :updated_at => "2013-03-05 09:17:00", :stanza_id => 250 },
  { :line => "To which life strives to fit our rhyme-beats here,", :no => 996, :created_at => "2013-03-05 09:17:00", :updated_at => "2013-03-05 09:17:00", :stanza_id => 250 },
  { :line => "Melting our limits in the illimitable,", :no => 997, :created_at => "2013-03-05 09:17:01", :updated_at => "2013-03-05 09:17:01", :stanza_id => 250 },
  { :line => "Tuning the finite to infinity.", :no => 998, :created_at => "2013-03-05 09:17:01", :updated_at => "2013-03-05 09:17:01", :stanza_id => 250 },
  { :line => "A low muttering rose from the subconscient caves,", :no => 999, :created_at => "2013-03-05 09:17:01", :updated_at => "2013-03-05 09:17:01", :stanza_id => 251 },
  { :line => "The stammer of the primal ignorance;", :no => 1000, :created_at => "2013-03-05 09:17:01", :updated_at => "2013-03-05 09:17:01", :stanza_id => 251 },
  { :line => "Answer to that inarticulate questioning,", :no => 1001, :created_at => "2013-03-05 09:17:01", :updated_at => "2013-03-05 09:17:01", :stanza_id => 251 },
  { :line => "There stooped with lightning neck and thunderâ€™s wings", :no => 1002, :created_at => "2013-03-05 09:17:02", :updated_at => "2013-03-05 09:17:02", :stanza_id => 251 },
  { :line => "A radiant hymn to the Inexpressible", :no => 1003, :created_at => "2013-03-05 09:17:02", :updated_at => "2013-03-05 09:17:02", :stanza_id => 251 },
  { :line => "And the anthem of the superconscient light.", :no => 1004, :created_at => "2013-03-05 09:17:02", :updated_at => "2013-03-05 09:17:02", :stanza_id => 251 },
  { :line => "All was revealed there none can here express;", :no => 1005, :created_at => "2013-03-05 09:17:02", :updated_at => "2013-03-05 09:17:02", :stanza_id => 252 },
  { :line => "Vision and dream were fables spoken by truth", :no => 1006, :created_at => "2013-03-05 09:17:03", :updated_at => "2013-03-05 09:17:03", :stanza_id => 252 },
  { :line => "Or symbols more veridical than fact,", :no => 1007, :created_at => "2013-03-05 09:17:03", :updated_at => "2013-03-05 09:17:03", :stanza_id => 252 },
  { :line => "Or were truths enforced by supernatural seals.", :no => 1008, :created_at => "2013-03-05 09:17:03", :updated_at => "2013-03-05 09:17:03", :stanza_id => 252 },
  { :line => "Immortal eyes approached and looked in his,", :no => 1009, :created_at => "2013-03-05 09:17:03", :updated_at => "2013-03-05 09:17:03", :stanza_id => 253 },
  { :line => "And beings of many kingdoms neared and spoke:", :no => 1010, :created_at => "2013-03-05 09:17:03", :updated_at => "2013-03-05 09:17:03", :stanza_id => 253 },
  { :line => "The ever-living whom we name as dead", :no => 1011, :created_at => "2013-03-05 09:17:04", :updated_at => "2013-03-05 09:17:04", :stanza_id => 253 },
  { :line => "Could leave their glory beyond death and birth", :no => 1012, :created_at => "2013-03-05 09:17:04", :updated_at => "2013-03-05 09:17:04", :stanza_id => 253 },
  { :line => "To utter the wisdom which exceeds all phrase:", :no => 1013, :created_at => "2013-03-05 09:17:04", :updated_at => "2013-03-05 09:17:04", :stanza_id => 253 },
  { :line => "The kings of evil and the kings of good,", :no => 1014, :created_at => "2013-03-05 09:17:04", :updated_at => "2013-03-05 09:17:04", :stanza_id => 253 },
  { :line => "Appellants at the reasonâ€™s judgment seat,", :no => 1015, :created_at => "2013-03-05 09:17:05", :updated_at => "2013-03-05 09:17:05", :stanza_id => 253 },
  { :line => "Proclaimed the gospel of their opposites,", :no => 1016, :created_at => "2013-03-05 09:17:05", :updated_at => "2013-03-05 09:17:05", :stanza_id => 253 },
  { :line => "And all believed themselves spokesmen of God:", :no => 1017, :created_at => "2013-03-05 09:17:05", :updated_at => "2013-03-05 09:17:05", :stanza_id => 253 },
  { :line => "The gods of light and titans of the dark", :no => 1018, :created_at => "2013-03-05 09:17:05", :updated_at => "2013-03-05 09:17:05", :stanza_id => 253 },
  { :line => "Battled for his soul as for a costly prize.", :no => 1019, :created_at => "2013-03-05 09:17:05", :updated_at => "2013-03-05 09:17:05", :stanza_id => 253 },
  { :line => "In every hour loosed from the quiver of Time", :no => 1020, :created_at => "2013-03-05 09:17:06", :updated_at => "2013-03-05 09:17:06", :stanza_id => 254 },
  { :line => "There rose a song of new discovery,", :no => 1021, :created_at => "2013-03-05 09:17:06", :updated_at => "2013-03-05 09:17:06", :stanza_id => 254 },
  { :line => "A bow-twangâ€™s hum of young experiment.", :no => 1022, :created_at => "2013-03-05 09:17:06", :updated_at => "2013-03-05 09:17:06", :stanza_id => 254 },
  { :line => "Each day was a spiritual romance,", :no => 1023, :created_at => "2013-03-05 09:17:07", :updated_at => "2013-03-05 09:17:07", :stanza_id => 255 },
  { :line => "As if he was born into a bright new world;", :no => 1024, :created_at => "2013-03-05 09:17:07", :updated_at => "2013-03-05 09:17:07", :stanza_id => 255 },
  { :line => "Adventure leaped an unexpected friend,", :no => 1025, :created_at => "2013-03-05 09:17:07", :updated_at => "2013-03-05 09:17:07", :stanza_id => 255 },
  { :line => "And danger brought a keen sweet tang of joy:", :no => 1026, :created_at => "2013-03-05 09:17:07", :updated_at => "2013-03-05 09:17:07", :stanza_id => 255 },
  { :line => "Each happening was a deep experience.", :no => 1027, :created_at => "2013-03-05 09:17:07", :updated_at => "2013-03-05 09:17:07", :stanza_id => 255 },
  { :line => "There were high encounters, epic colloquies,", :no => 1028, :created_at => "2013-03-05 09:17:08", :updated_at => "2013-03-05 09:17:08", :stanza_id => 256 },
  { :line => "And counsels came couched in celestial speech,", :no => 1029, :created_at => "2013-03-05 09:17:08", :updated_at => "2013-03-05 09:17:08", :stanza_id => 256 },
  { :line => "And honeyed pleadings breathed from occult lips", :no => 1030, :created_at => "2013-03-05 09:17:08", :updated_at => "2013-03-05 09:17:08", :stanza_id => 256 },
  { :line => "To help the heart to yield to raptureâ€™s call,", :no => 1031, :created_at => "2013-03-05 09:17:08", :updated_at => "2013-03-05 09:17:08", :stanza_id => 256 },
  { :line => "And sweet temptations stole from beautyâ€™s realms", :no => 1032, :created_at => "2013-03-05 09:17:08", :updated_at => "2013-03-05 09:17:08", :stanza_id => 256 },
  { :line => "And sudden ecstasies from a world of bliss.", :no => 1033, :created_at => "2013-03-05 09:17:09", :updated_at => "2013-03-05 09:17:09", :stanza_id => 256 },
  { :line => "It was a reign of wonder and delight;", :no => 1034, :created_at => "2013-03-05 09:17:09", :updated_at => "2013-03-05 09:17:09", :stanza_id => 257 },
  { :line => "All now his bright clairaudience could receive,", :no => 1035, :created_at => "2013-03-05 09:17:09", :updated_at => "2013-03-05 09:17:09", :stanza_id => 257 },
  { :line => "A contact thrilled of mighty unknown things.", :no => 1036, :created_at => "2013-03-05 09:17:09", :updated_at => "2013-03-05 09:17:09", :stanza_id => 257 },
  { :line => "Awakened to new unearthly closenesses,", :no => 1037, :created_at => "2013-03-05 09:17:10", :updated_at => "2013-03-05 09:17:10", :stanza_id => 258 },
  { :line => "The touch replied to subtle infinities,", :no => 1038, :created_at => "2013-03-05 09:17:10", :updated_at => "2013-03-05 09:17:10", :stanza_id => 258 },
  { :line => "And with a silver cry of opening gates", :no => 1039, :created_at => "2013-03-05 09:17:10", :updated_at => "2013-03-05 09:17:10", :stanza_id => 258 },
  { :line => "Sightâ€™s lightnings leaped into the invisible.", :no => 1040, :created_at => "2013-03-05 09:17:10", :updated_at => "2013-03-05 09:17:10", :stanza_id => 258 },
  { :line => "Ever his consciousness and vision grew;", :no => 1041, :created_at => "2013-03-05 09:17:10", :updated_at => "2013-03-05 09:17:10", :stanza_id => 259 },
  { :line => "They took an ampler sweep, a loftier flight;", :no => 1042, :created_at => "2013-03-05 09:17:11", :updated_at => "2013-03-05 09:17:11", :stanza_id => 259 },
  { :line => "He passed the border marked for Matterâ€™s rule", :no => 1043, :created_at => "2013-03-05 09:17:11", :updated_at => "2013-03-05 09:17:11", :stanza_id => 259 },
  { :line => "And passed the zone where thought replaces life.", :no => 1044, :created_at => "2013-03-05 09:17:11", :updated_at => "2013-03-05 09:17:11", :stanza_id => 259 },
  { :line => "Out of this world of signs suddenly he came", :no => 1045, :created_at => "2013-03-05 09:17:11", :updated_at => "2013-03-05 09:17:11", :stanza_id => 260 },
  { :line => "Into a silent self where world was not", :no => 1046, :created_at => "2013-03-05 09:17:12", :updated_at => "2013-03-05 09:17:12", :stanza_id => 260 },
  { :line => "And looked beyond into a nameless vast.", :no => 1047, :created_at => "2013-03-05 09:17:12", :updated_at => "2013-03-05 09:17:12", :stanza_id => 260 },
  { :line => "These symbol figures lost their right to live,", :no => 1048, :created_at => "2013-03-05 09:17:12", :updated_at => "2013-03-05 09:17:12", :stanza_id => 261 },
  { :line => "All tokens dropped our sense can recognise;", :no => 1049, :created_at => "2013-03-05 09:17:12", :updated_at => "2013-03-05 09:17:12", :stanza_id => 261 },
  { :line => "There the heart beat no more at bodyâ€™s touch,", :no => 1050, :created_at => "2013-03-05 09:17:12", :updated_at => "2013-03-05 09:17:12", :stanza_id => 261 },
  { :line => "There the eyes gazed no more on beautyâ€™s shape,", :no => 1051, :created_at => "2013-03-05 09:17:13", :updated_at => "2013-03-05 09:17:13", :stanza_id => 261 },
  { :line => "In rare and lucent intervals of hush", :no => 1052, :created_at => "2013-03-05 09:17:13", :updated_at => "2013-03-05 09:17:13", :stanza_id => 261 },
  { :line => "Into a signless region he could soar", :no => 1053, :created_at => "2013-03-05 09:17:13", :updated_at => "2013-03-05 09:17:13", :stanza_id => 261 },
  { :line => "Packed with the deep contents of formlessness", :no => 1054, :created_at => "2013-03-05 09:17:13", :updated_at => "2013-03-05 09:17:13", :stanza_id => 261 },
  { :line => "Where world was into a single being rapt", :no => 1055, :created_at => "2013-03-05 09:17:13", :updated_at => "2013-03-05 09:17:13", :stanza_id => 261 },
  { :line => "And all was known by the light of identity", :no => 1056, :created_at => "2013-03-05 09:17:14", :updated_at => "2013-03-05 09:17:14", :stanza_id => 261 },
  { :line => "And spirit was its own self-evidence.", :no => 1057, :created_at => "2013-03-05 09:17:14", :updated_at => "2013-03-05 09:17:14", :stanza_id => 261 },
  { :line => "The Supremeâ€™s gaze looked out through human eyes", :no => 1058, :created_at => "2013-03-05 09:17:14", :updated_at => "2013-03-05 09:17:14", :stanza_id => 262 },
  { :line => "And saw all things and creatures as itself", :no => 1059, :created_at => "2013-03-05 09:17:14", :updated_at => "2013-03-05 09:17:14", :stanza_id => 262 },
  { :line => "And knew all thought and word as its own voice.", :no => 1060, :created_at => "2013-03-05 09:17:15", :updated_at => "2013-03-05 09:17:15", :stanza_id => 262 },
  { :line => "There unity is too close for search and clasp", :no => 1061, :created_at => "2013-03-05 09:17:15", :updated_at => "2013-03-05 09:17:15", :stanza_id => 263 },
  { :line => "And love is a yearning of the One for the One,", :no => 1062, :created_at => "2013-03-05 09:17:15", :updated_at => "2013-03-05 09:17:15", :stanza_id => 263 },
  { :line => "And beauty is a sweet difference of the Same", :no => 1063, :created_at => "2013-03-05 09:17:15", :updated_at => "2013-03-05 09:17:15", :stanza_id => 263 },
  { :line => "And oneness is the soul of multitude.", :no => 1064, :created_at => "2013-03-05 09:17:15", :updated_at => "2013-03-05 09:17:15", :stanza_id => 263 },
  { :line => "There all the truths unite in a single truth,", :no => 1065, :created_at => "2013-03-05 09:17:16", :updated_at => "2013-03-05 09:17:16", :stanza_id => 264 },
  { :line => "And all ideas rejoin Reality.", :no => 1066, :created_at => "2013-03-05 09:17:16", :updated_at => "2013-03-05 09:17:16", :stanza_id => 264 },
  { :line => "There knowing herself by her own termless self,", :no => 1067, :created_at => "2013-03-05 09:17:16", :updated_at => "2013-03-05 09:17:16", :stanza_id => 265 },
  { :line => "Wisdom supernal, wordless, absolute", :no => 1068, :created_at => "2013-03-05 09:17:17", :updated_at => "2013-03-05 09:17:17", :stanza_id => 265 },
  { :line => "Sat uncompanioned in the eternal Calm,", :no => 1069, :created_at => "2013-03-05 09:17:17", :updated_at => "2013-03-05 09:17:17", :stanza_id => 265 },
  { :line => "All-seeing, motionless, sovereign and alone.", :no => 1070, :created_at => "2013-03-05 09:17:17", :updated_at => "2013-03-05 09:17:17", :stanza_id => 265 },
  { :line => "There knowledge needs not words to embody Idea;", :no => 1071, :created_at => "2013-03-05 09:17:17", :updated_at => "2013-03-05 09:17:17", :stanza_id => 266 },
  { :line => "Idea seeking a house in boundlessness,", :no => 1072, :created_at => "2013-03-05 09:17:17", :updated_at => "2013-03-05 09:17:17", :stanza_id => 266 },
  { :line => "Weary of its homeless immortality,", :no => 1073, :created_at => "2013-03-05 09:17:18", :updated_at => "2013-03-05 09:17:18", :stanza_id => 266 },
  { :line => "Asks not in thoughtâ€™s carved brilliant cell to rest", :no => 1074, :created_at => "2013-03-05 09:17:18", :updated_at => "2013-03-05 09:17:18", :stanza_id => 266 },
  { :line => "Whose single windowâ€™s clipped outlook on things", :no => 1075, :created_at => "2013-03-05 09:17:18", :updated_at => "2013-03-05 09:17:18", :stanza_id => 266 },
  { :line => "Sees only a little arc of Godâ€™s vast sky.", :no => 1076, :created_at => "2013-03-05 09:17:18", :updated_at => "2013-03-05 09:17:18", :stanza_id => 266 },
  { :line => "The boundless with the boundless there consorts;", :no => 1077, :created_at => "2013-03-05 09:17:18", :updated_at => "2013-03-05 09:17:18", :stanza_id => 267 },
  { :line => "While there, one can be wider than the world;", :no => 1078, :created_at => "2013-03-05 09:17:19", :updated_at => "2013-03-05 09:17:19", :stanza_id => 267 },
  { :line => "While there, one is oneâ€™s own infinity.", :no => 1079, :created_at => "2013-03-05 09:17:19", :updated_at => "2013-03-05 09:17:19", :stanza_id => 267 },
  { :line => "His centre was no more in earthly mind,", :no => 1080, :created_at => "2013-03-05 09:17:19", :updated_at => "2013-03-05 09:17:19", :stanza_id => 268 },
  { :line => "A power of seeing silence filled his limbs:", :no => 1081, :created_at => "2013-03-05 09:17:19", :updated_at => "2013-03-05 09:17:19", :stanza_id => 268 },
  { :line => "Caught by a voiceless white epiphany", :no => 1082, :created_at => "2013-03-05 09:17:20", :updated_at => "2013-03-05 09:17:20", :stanza_id => 268 },
  { :line => "Into a vision that surpasses forms,", :no => 1083, :created_at => "2013-03-05 09:17:20", :updated_at => "2013-03-05 09:17:20", :stanza_id => 268 },
  { :line => "Into a living that surpasses life,", :no => 1084, :created_at => "2013-03-05 09:17:20", :updated_at => "2013-03-05 09:17:20", :stanza_id => 268 },
  { :line => "He neared the still consciousness sustaining all.", :no => 1085, :created_at => "2013-03-05 09:17:20", :updated_at => "2013-03-05 09:17:20", :stanza_id => 268 },
  { :line => "The voice that only by speech can move the mind", :no => 1086, :created_at => "2013-03-05 09:17:20", :updated_at => "2013-03-05 09:17:20", :stanza_id => 269 },
  { :line => "Became a silent knowledge in the soul;", :no => 1087, :created_at => "2013-03-05 09:17:21", :updated_at => "2013-03-05 09:17:21", :stanza_id => 269 },
  { :line => "The strength that only in action feels its truth", :no => 1088, :created_at => "2013-03-05 09:17:21", :updated_at => "2013-03-05 09:17:21", :stanza_id => 269 },
  { :line => "Was lodged now in a mute omnipotent peace.", :no => 1089, :created_at => "2013-03-05 09:17:21", :updated_at => "2013-03-05 09:17:21", :stanza_id => 269 },
  { :line => "A leisure in the labour of the worlds,", :no => 1090, :created_at => "2013-03-05 09:17:21", :updated_at => "2013-03-05 09:17:21", :stanza_id => 270 },
  { :line => "A pause in the joy and anguish of the search", :no => 1091, :created_at => "2013-03-05 09:17:21", :updated_at => "2013-03-05 09:17:21", :stanza_id => 270 },
  { :line => "Restored the stress of Nature to Godâ€™s calm.", :no => 1092, :created_at => "2013-03-05 09:17:22", :updated_at => "2013-03-05 09:17:22", :stanza_id => 270 },
  { :line => "A vast unanimity ended lifeâ€™s debate.", :no => 1093, :created_at => "2013-03-05 09:17:22", :updated_at => "2013-03-05 09:17:22", :stanza_id => 271 },
  { :line => "The war of thoughts that fathers the universe,", :no => 1094, :created_at => "2013-03-05 09:17:22", :updated_at => "2013-03-05 09:17:22", :stanza_id => 272 },
  { :line => "The clash of forces struggling to prevail", :no => 1095, :created_at => "2013-03-05 09:17:23", :updated_at => "2013-03-05 09:17:23", :stanza_id => 272 },
  { :line => "In the tremendous shock that lights a star", :no => 1096, :created_at => "2013-03-05 09:17:23", :updated_at => "2013-03-05 09:17:23", :stanza_id => 272 },
  { :line => "As in the building of a grain of dust,", :no => 1097, :created_at => "2013-03-05 09:17:23", :updated_at => "2013-03-05 09:17:23", :stanza_id => 272 },
  { :line => "The grooves that turn their dumb ellipse in space", :no => 1098, :created_at => "2013-03-05 09:17:23", :updated_at => "2013-03-05 09:17:23", :stanza_id => 272 },
  { :line => "Ploughed by the seeking of the worldâ€™s desire,", :no => 1099, :created_at => "2013-03-05 09:17:23", :updated_at => "2013-03-05 09:17:23", :stanza_id => 272 },
  { :line => "The long regurgitations in Timeâ€™s flood,", :no => 1100, :created_at => "2013-03-05 09:17:24", :updated_at => "2013-03-05 09:17:24", :stanza_id => 272 },
  { :line => "The torment edging the dire force of lust", :no => 1101, :created_at => "2013-03-05 09:17:24", :updated_at => "2013-03-05 09:17:24", :stanza_id => 272 },
  { :line => "That wakes kinetic in earthâ€™s dullard slime", :no => 1102, :created_at => "2013-03-05 09:17:24", :updated_at => "2013-03-05 09:17:24", :stanza_id => 272 },
  { :line => "And carves a personality out of mud,", :no => 1103, :created_at => "2013-03-05 09:17:24", :updated_at => "2013-03-05 09:17:24", :stanza_id => 272 },
  { :line => "The sorrow by which Natureâ€™s hunger is fed,", :no => 1104, :created_at => "2013-03-05 09:17:24", :updated_at => "2013-03-05 09:17:24", :stanza_id => 272 },
  { :line => "The oestrus which creates with fire of pain,", :no => 1105, :created_at => "2013-03-05 09:17:24", :updated_at => "2013-03-05 09:17:24", :stanza_id => 272 },
  { :line => "The fate that punishes virtue with defeat,", :no => 1106, :created_at => "2013-03-05 09:17:25", :updated_at => "2013-03-05 09:17:25", :stanza_id => 272 },
  { :line => "The tragedy that destroys long happiness,", :no => 1107, :created_at => "2013-03-05 09:17:25", :updated_at => "2013-03-05 09:17:25", :stanza_id => 272 },
  { :line => "The weeping of Love, the quarrel of the Gods,", :no => 1108, :created_at => "2013-03-05 09:17:25", :updated_at => "2013-03-05 09:17:25", :stanza_id => 272 },
  { :line => "Ceased in a truth which lives in its own light.", :no => 1109, :created_at => "2013-03-05 09:17:25", :updated_at => "2013-03-05 09:17:25", :stanza_id => 272 },
  { :line => "His soul stood free, a witness and a king.", :no => 1110, :created_at => "2013-03-05 09:17:26", :updated_at => "2013-03-05 09:17:26", :stanza_id => 273 },
  { :line => "Absorbed no more in the moment-ridden flux", :no => 1111, :created_at => "2013-03-05 09:17:26", :updated_at => "2013-03-05 09:17:26", :stanza_id => 274 },
  { :line => "Where mind incessantly drifts as on a raft", :no => 1112, :created_at => "2013-03-05 09:17:26", :updated_at => "2013-03-05 09:17:26", :stanza_id => 274 },
  { :line => "Hurried from phenomenon to phenomenon,", :no => 1113, :created_at => "2013-03-05 09:17:26", :updated_at => "2013-03-05 09:17:26", :stanza_id => 274 },
  { :line => "He abode at rest in indivisible Time.", :no => 1114, :created_at => "2013-03-05 09:17:27", :updated_at => "2013-03-05 09:17:27", :stanza_id => 274 },
  { :line => "As if a story long written but acted now,", :no => 1115, :created_at => "2013-03-05 09:17:27", :updated_at => "2013-03-05 09:17:27", :stanza_id => 275 },
  { :line => "In his present he held his future and his past,", :no => 1116, :created_at => "2013-03-05 09:17:27", :updated_at => "2013-03-05 09:17:27", :stanza_id => 275 },
  { :line => "Felt in the seconds the uncounted years", :no => 1117, :created_at => "2013-03-05 09:17:27", :updated_at => "2013-03-05 09:17:27", :stanza_id => 275 },
  { :line => "And saw the hours like dots upon a page.", :no => 1118, :created_at => "2013-03-05 09:17:28", :updated_at => "2013-03-05 09:17:28", :stanza_id => 275 },
  { :line => "An aspect of the unknown Reality", :no => 1119, :created_at => "2013-03-05 09:17:28", :updated_at => "2013-03-05 09:17:28", :stanza_id => 276 },
  { :line => "Altered the meaning of the cosmic scene.", :no => 1120, :created_at => "2013-03-05 09:17:28", :updated_at => "2013-03-05 09:17:28", :stanza_id => 276 },
  { :line => "This huge material universe became", :no => 1121, :created_at => "2013-03-05 09:17:29", :updated_at => "2013-03-05 09:17:29", :stanza_id => 277 },
  { :line => "A small result of a stupendous force:", :no => 1122, :created_at => "2013-03-05 09:17:29", :updated_at => "2013-03-05 09:17:29", :stanza_id => 277 },
  { :line => "Overtaking the moment the eternal Ray", :no => 1123, :created_at => "2013-03-05 09:17:29", :updated_at => "2013-03-05 09:17:29", :stanza_id => 277 },
  { :line => "Illumined that which never yet was made.", :no => 1124, :created_at => "2013-03-05 09:17:29", :updated_at => "2013-03-05 09:17:29", :stanza_id => 277 },
  { :line => "Thought lay down in a mighty voicelessness;", :no => 1125, :created_at => "2013-03-05 09:17:29", :updated_at => "2013-03-05 09:17:29", :stanza_id => 278 },
  { :line => "The toiling thinker widened and grew still,", :no => 1126, :created_at => "2013-03-05 09:17:30", :updated_at => "2013-03-05 09:17:30", :stanza_id => 278 },
  { :line => "Wisdom transcendent touched his quivering heart:", :no => 1127, :created_at => "2013-03-05 09:17:30", :updated_at => "2013-03-05 09:17:30", :stanza_id => 278 },
  { :line => "His soul could sail beyond thoughtâ€™s luminous bar;", :no => 1128, :created_at => "2013-03-05 09:17:30", :updated_at => "2013-03-05 09:17:30", :stanza_id => 278 },
  { :line => "Mind screened no more the shoreless infinite.", :no => 1129, :created_at => "2013-03-05 09:17:30", :updated_at => "2013-03-05 09:17:30", :stanza_id => 278 },
  { :line => "Across a void retreating sky he glimpsed", :no => 1130, :created_at => "2013-03-05 09:17:30", :updated_at => "2013-03-05 09:17:30", :stanza_id => 279 },
  { :line => "Through a last glimmer and drift of vanishing stars", :no => 1131, :created_at => "2013-03-05 09:17:31", :updated_at => "2013-03-05 09:17:31", :stanza_id => 279 },
  { :line => "The superconscient realms of motionless peace", :no => 1132, :created_at => "2013-03-05 09:17:31", :updated_at => "2013-03-05 09:17:31", :stanza_id => 279 },
  { :line => "Where judgment ceases and the word is mute", :no => 1133, :created_at => "2013-03-05 09:17:31", :updated_at => "2013-03-05 09:17:31", :stanza_id => 279 },
  { :line => "And the Unconceived lies pathless and alone.", :no => 1134, :created_at => "2013-03-05 09:17:31", :updated_at => "2013-03-05 09:17:31", :stanza_id => 279 },
  { :line => "There came not form or any mounting voice;", :no => 1135, :created_at => "2013-03-05 09:17:32", :updated_at => "2013-03-05 09:17:32", :stanza_id => 280 },
  { :line => "There only were Silence and the Absolute.", :no => 1136, :created_at => "2013-03-05 09:17:32", :updated_at => "2013-03-05 09:17:32", :stanza_id => 280 },
  { :line => "Out of that stillness mind new-born arose", :no => 1137, :created_at => "2013-03-05 09:17:32", :updated_at => "2013-03-05 09:17:32", :stanza_id => 281 },
  { :line => "And woke to truths once inexpressible,", :no => 1138, :created_at => "2013-03-05 09:17:33", :updated_at => "2013-03-05 09:17:33", :stanza_id => 281 },
  { :line => "And forms appeared, dumbly significant,", :no => 1139, :created_at => "2013-03-05 09:17:33", :updated_at => "2013-03-05 09:17:33", :stanza_id => 281 },
  { :line => "A seeing thought, a self-revealing voice.", :no => 1140, :created_at => "2013-03-05 09:17:33", :updated_at => "2013-03-05 09:17:33", :stanza_id => 281 },
  { :line => "He knew the source from which his spirit came:", :no => 1141, :created_at => "2013-03-05 09:17:33", :updated_at => "2013-03-05 09:17:33", :stanza_id => 282 },
  { :line => "Movement was married to the immobile Vast;", :no => 1142, :created_at => "2013-03-05 09:17:34", :updated_at => "2013-03-05 09:17:34", :stanza_id => 282 },
  { :line => "He plunged his roots into the Infinite,", :no => 1143, :created_at => "2013-03-05 09:17:34", :updated_at => "2013-03-05 09:17:34", :stanza_id => 282 },
  { :line => "He based his life upon Eternity.", :no => 1144, :created_at => "2013-03-05 09:17:34", :updated_at => "2013-03-05 09:17:34", :stanza_id => 282 },
  { :line => "Only a while at first these heavenlier states,", :no => 1145, :created_at => "2013-03-05 09:17:34", :updated_at => "2013-03-05 09:17:34", :stanza_id => 283 },
  { :line => "These large wide-poised upliftings could endure.", :no => 1146, :created_at => "2013-03-05 09:17:34", :updated_at => "2013-03-05 09:17:34", :stanza_id => 283 },
  { :line => "The high and luminous tension breaks too soon,", :no => 1147, :created_at => "2013-03-05 09:17:35", :updated_at => "2013-03-05 09:17:35", :stanza_id => 284 },
  { :line => "The bodyâ€™s stone stillness and the lifeâ€™s hushed trance,", :no => 1148, :created_at => "2013-03-05 09:17:35", :updated_at => "2013-03-05 09:17:35", :stanza_id => 284 },
  { :line => "The breathless might and calm of silent mind;", :no => 1149, :created_at => "2013-03-05 09:17:35", :updated_at => "2013-03-05 09:17:35", :stanza_id => 284 },
  { :line => "Or slowly they fail as sets a golden day.", :no => 1150, :created_at => "2013-03-05 09:17:35", :updated_at => "2013-03-05 09:17:35", :stanza_id => 284 },
  { :line => "The restless nether members tire of peace;", :no => 1151, :created_at => "2013-03-05 09:17:36", :updated_at => "2013-03-05 09:17:36", :stanza_id => 285 },
  { :line => "A nostalgia of old little works and joys,", :no => 1152, :created_at => "2013-03-05 09:17:36", :updated_at => "2013-03-05 09:17:36", :stanza_id => 285 },
  { :line => "A need to call back small familiar selves,", :no => 1153, :created_at => "2013-03-05 09:17:36", :updated_at => "2013-03-05 09:17:36", :stanza_id => 285 },
  { :line => "To tread the accustomed and inferior way,", :no => 1154, :created_at => "2013-03-05 09:17:36", :updated_at => "2013-03-05 09:17:36", :stanza_id => 285 },
  { :line => "The need to rest in a natural poise of fall,", :no => 1155, :created_at => "2013-03-05 09:17:37", :updated_at => "2013-03-05 09:17:37", :stanza_id => 285 },
  { :line => "As a child who learns to walk can walk not long,", :no => 1156, :created_at => "2013-03-05 09:17:37", :updated_at => "2013-03-05 09:17:37", :stanza_id => 285 },
  { :line => "Replace the titan will for ever to climb,", :no => 1157, :created_at => "2013-03-05 09:17:37", :updated_at => "2013-03-05 09:17:37", :stanza_id => 285 },
  { :line => "On the heartâ€™s altar dim the sacred fire.", :no => 1158, :created_at => "2013-03-05 09:17:37", :updated_at => "2013-03-05 09:17:37", :stanza_id => 285 },
  { :line => "An old pull of subconscious cords renews;", :no => 1159, :created_at => "2013-03-05 09:17:38", :updated_at => "2013-03-05 09:17:38", :stanza_id => 286 },
  { :line => "It draws the unwilling spirit from the heights,", :no => 1160, :created_at => "2013-03-05 09:17:38", :updated_at => "2013-03-05 09:17:38", :stanza_id => 286 },
  { :line => "Or a dull gravitation drags us down", :no => 1161, :created_at => "2013-03-05 09:17:38", :updated_at => "2013-03-05 09:17:38", :stanza_id => 286 },
  { :line => "To the blind driven inertia of our base.", :no => 1162, :created_at => "2013-03-05 09:17:38", :updated_at => "2013-03-05 09:17:38", :stanza_id => 286 },
  { :line => "This too the supreme Diplomat can use,", :no => 1163, :created_at => "2013-03-05 09:17:39", :updated_at => "2013-03-05 09:17:39", :stanza_id => 287 },
  { :line => "He makes our fall a means for greater rise.", :no => 1164, :created_at => "2013-03-05 09:17:39", :updated_at => "2013-03-05 09:17:39", :stanza_id => 287 },
  { :line => "For into the ignorant natureâ€™s gusty field,", :no => 1165, :created_at => "2013-03-05 09:17:39", :updated_at => "2013-03-05 09:17:39", :stanza_id => 288 },
  { :line => "Into the half-ordered chaos of mortal life", :no => 1166, :created_at => "2013-03-05 09:17:39", :updated_at => "2013-03-05 09:17:39", :stanza_id => 288 },
  { :line => "The formless Power, the Self of eternal light", :no => 1167, :created_at => "2013-03-05 09:17:40", :updated_at => "2013-03-05 09:17:40", :stanza_id => 288 },
  { :line => "Follow in the shadow of the spiritâ€™s descent;", :no => 1168, :created_at => "2013-03-05 09:17:40", :updated_at => "2013-03-05 09:17:40", :stanza_id => 288 },
  { :line => "The twin duality for ever one", :no => 1169, :created_at => "2013-03-05 09:17:40", :updated_at => "2013-03-05 09:17:40", :stanza_id => 288 },
  { :line => "Chooses its home mid the tumults of the sense.", :no => 1170, :created_at => "2013-03-05 09:17:40", :updated_at => "2013-03-05 09:17:40", :stanza_id => 288 },
  { :line => "He comes unseen into our darker parts", :no => 1171, :created_at => "2013-03-05 09:17:40", :updated_at => "2013-03-05 09:17:40", :stanza_id => 289 },
  { :line => "And, curtained by the darkness, does his work,", :no => 1172, :created_at => "2013-03-05 09:17:41", :updated_at => "2013-03-05 09:17:41", :stanza_id => 289 },
  { :line => "A subtle and all-knowing guest and guide,", :no => 1173, :created_at => "2013-03-05 09:17:41", :updated_at => "2013-03-05 09:17:41", :stanza_id => 289 },
  { :line => "Till they too feel the need and will to change.", :no => 1174, :created_at => "2013-03-05 09:17:41", :updated_at => "2013-03-05 09:17:41", :stanza_id => 289 },
  { :line => "All here must learn to obey a higher law,", :no => 1175, :created_at => "2013-03-05 09:17:41", :updated_at => "2013-03-05 09:17:41", :stanza_id => 290 },
  { :line => "Our bodyâ€™s cells must hold the Immortalâ€™s flame.", :no => 1176, :created_at => "2013-03-05 09:17:42", :updated_at => "2013-03-05 09:17:42", :stanza_id => 290 },
  { :line => "Else would the spirit reach alone its source", :no => 1177, :created_at => "2013-03-05 09:17:42", :updated_at => "2013-03-05 09:17:42", :stanza_id => 291 },
  { :line => "Leaving a half-saved world to its dubious fate.", :no => 1178, :created_at => "2013-03-05 09:17:42", :updated_at => "2013-03-05 09:17:42", :stanza_id => 291 },
  { :line => "Nature would ever labour unredeemed;", :no => 1179, :created_at => "2013-03-05 09:17:42", :updated_at => "2013-03-05 09:17:42", :stanza_id => 292 },
  { :line => "Our earth would ever spin unhelped in space,", :no => 1180, :created_at => "2013-03-05 09:17:43", :updated_at => "2013-03-05 09:17:43", :stanza_id => 292 },
  { :line => "And this immense creationâ€™s purpose fail", :no => 1181, :created_at => "2013-03-05 09:17:43", :updated_at => "2013-03-05 09:17:43", :stanza_id => 292 },
  { :line => "Till at last the frustrate universe sank undone.", :no => 1182, :created_at => "2013-03-05 09:17:43", :updated_at => "2013-03-05 09:17:43", :stanza_id => 292 },
  { :line => "Even his godlike strength to rise must fall:", :no => 1183, :created_at => "2013-03-05 09:17:43", :updated_at => "2013-03-05 09:17:43", :stanza_id => 293 },
  { :line => "His greater consciousness withdrew behind;", :no => 1184, :created_at => "2013-03-05 09:17:44", :updated_at => "2013-03-05 09:17:44", :stanza_id => 293 },
  { :line => "Dim and eclipsed, his human outside strove", :no => 1185, :created_at => "2013-03-05 09:17:44", :updated_at => "2013-03-05 09:17:44", :stanza_id => 293 },
  { :line => "To feel again the old sublimities,", :no => 1186, :created_at => "2013-03-05 09:17:44", :updated_at => "2013-03-05 09:17:44", :stanza_id => 293 },
  { :line => "Bring the high saving touch, the ethereal flame,", :no => 1187, :created_at => "2013-03-05 09:17:44", :updated_at => "2013-03-05 09:17:44", :stanza_id => 293 },
  { :line => "Call back to its dire need the divine Force.", :no => 1188, :created_at => "2013-03-05 09:17:44", :updated_at => "2013-03-05 09:17:44", :stanza_id => 293 },
  { :line => "Always the power poured back like sudden rain,", :no => 1189, :created_at => "2013-03-05 09:17:45", :updated_at => "2013-03-05 09:17:45", :stanza_id => 294 },
  { :line => "Or slowly in his breast a presence grew,", :no => 1190, :created_at => "2013-03-05 09:17:45", :updated_at => "2013-03-05 09:17:45", :stanza_id => 294 },
  { :line => "It clambered back to some remembered height", :no => 1191, :created_at => "2013-03-05 09:17:45", :updated_at => "2013-03-05 09:17:45", :stanza_id => 294 },
  { :line => "Or soared above the peak from which it fell.", :no => 1192, :created_at => "2013-03-05 09:17:45", :updated_at => "2013-03-05 09:17:45", :stanza_id => 294 },
  { :line => "Each time he rose there was a larger poise,", :no => 1193, :created_at => "2013-03-05 09:17:46", :updated_at => "2013-03-05 09:17:46", :stanza_id => 295 },
  { :line => "A dwelling on a higher spirit plane;", :no => 1194, :created_at => "2013-03-05 09:17:46", :updated_at => "2013-03-05 09:17:46", :stanza_id => 295 },
  { :line => "The Light remained in him a longer space.", :no => 1195, :created_at => "2013-03-05 09:17:46", :updated_at => "2013-03-05 09:17:46", :stanza_id => 295 },
  { :line => "In this oscillation between earth and heaven,", :no => 1196, :created_at => "2013-03-05 09:17:47", :updated_at => "2013-03-05 09:17:47", :stanza_id => 296 },
  { :line => "In this ineffable communionâ€™s climb", :no => 1197, :created_at => "2013-03-05 09:17:47", :updated_at => "2013-03-05 09:17:47", :stanza_id => 296 },
  { :line => "There grew in him as grows a waxing moon", :no => 1198, :created_at => "2013-03-05 09:17:47", :updated_at => "2013-03-05 09:17:47", :stanza_id => 296 },
  { :line => "The glory of the integer of his soul.", :no => 1199, :created_at => "2013-03-05 09:17:47", :updated_at => "2013-03-05 09:17:47", :stanza_id => 296 },
  { :line => "A union of the Real with the unique,", :no => 1200, :created_at => "2013-03-05 09:17:47", :updated_at => "2013-03-05 09:17:47", :stanza_id => 297 },
  { :line => "A gaze of the Alone from every face,", :no => 1201, :created_at => "2013-03-05 09:17:48", :updated_at => "2013-03-05 09:17:48", :stanza_id => 297 },
  { :line => "The Presence of the Eternal in the hours", :no => 1202, :created_at => "2013-03-05 09:17:48", :updated_at => "2013-03-05 09:17:48", :stanza_id => 297 },
  { :line => "Widening the mortal mindâ€™s half-look on things,", :no => 1203, :created_at => "2013-03-05 09:17:48", :updated_at => "2013-03-05 09:17:48", :stanza_id => 297 },
  { :line => "Bridging the gap between manâ€™s force and Fate", :no => 1204, :created_at => "2013-03-05 09:17:48", :updated_at => "2013-03-05 09:17:48", :stanza_id => 297 },
  { :line => "Made whole the fragment-being we are here.", :no => 1205, :created_at => "2013-03-05 09:17:48", :updated_at => "2013-03-05 09:17:48", :stanza_id => 297 },
  { :line => "At last was won a firm spiritual poise,", :no => 1206, :created_at => "2013-03-05 09:17:49", :updated_at => "2013-03-05 09:17:49", :stanza_id => 298 },
  { :line => "A constant lodging in the Eternalâ€™s realm,", :no => 1207, :created_at => "2013-03-05 09:17:49", :updated_at => "2013-03-05 09:17:49", :stanza_id => 298 },
  { :line => "A safety in the Silence and the Ray,", :no => 1208, :created_at => "2013-03-05 09:17:49", :updated_at => "2013-03-05 09:17:49", :stanza_id => 298 },
  { :line => "A settlement in the Immutable.", :no => 1209, :created_at => "2013-03-05 09:17:49", :updated_at => "2013-03-05 09:17:49", :stanza_id => 298 },
  { :line => "His heights of being lived in the still Self;", :no => 1210, :created_at => "2013-03-05 09:17:50", :updated_at => "2013-03-05 09:17:50", :stanza_id => 299 },
  { :line => "His mind could rest on a supernal ground", :no => 1211, :created_at => "2013-03-05 09:17:50", :updated_at => "2013-03-05 09:17:50", :stanza_id => 299 },
  { :line => "And look down on the magic and the play", :no => 1212, :created_at => "2013-03-05 09:17:50", :updated_at => "2013-03-05 09:17:50", :stanza_id => 299 },
  { :line => "Where the God-child lies on the lap of Night and Dawn", :no => 1213, :created_at => "2013-03-05 09:17:50", :updated_at => "2013-03-05 09:17:50", :stanza_id => 299 },
  { :line => "And the Everlasting puts on Timeâ€™s disguise.", :no => 1214, :created_at => "2013-03-05 09:17:50", :updated_at => "2013-03-05 09:17:50", :stanza_id => 299 },
  { :line => "To the still heights and to the troubled depths", :no => 1215, :created_at => "2013-03-05 09:17:51", :updated_at => "2013-03-05 09:17:51", :stanza_id => 300 },
  { :line => "His equal spirit gave its vast assent:", :no => 1216, :created_at => "2013-03-05 09:17:51", :updated_at => "2013-03-05 09:17:51", :stanza_id => 300 },
  { :line => "A poised serenity of tranquil strength,", :no => 1217, :created_at => "2013-03-05 09:17:51", :updated_at => "2013-03-05 09:17:51", :stanza_id => 300 },
  { :line => "A wide unshaken look on Timeâ€™s unrest", :no => 1218, :created_at => "2013-03-05 09:17:51", :updated_at => "2013-03-05 09:17:51", :stanza_id => 300 },
  { :line => "Faced all experience with unaltered peace.", :no => 1219, :created_at => "2013-03-05 09:17:51", :updated_at => "2013-03-05 09:17:51", :stanza_id => 300 },
  { :line => "Indifferent to the sorrow and delight,", :no => 1220, :created_at => "2013-03-05 09:17:52", :updated_at => "2013-03-05 09:17:52", :stanza_id => 301 },
  { :line => "Untempted by the marvel and the call,", :no => 1221, :created_at => "2013-03-05 09:17:52", :updated_at => "2013-03-05 09:17:52", :stanza_id => 301 },
  { :line => "Immobile it beheld the flux of things,", :no => 1222, :created_at => "2013-03-05 09:17:52", :updated_at => "2013-03-05 09:17:52", :stanza_id => 301 },
  { :line => "Calm and apart supported all that is:", :no => 1223, :created_at => "2013-03-05 09:17:52", :updated_at => "2013-03-05 09:17:52", :stanza_id => 301 },
  { :line => "His spiritâ€™s stillness helped the toiling world.", :no => 1224, :created_at => "2013-03-05 09:17:53", :updated_at => "2013-03-05 09:17:53", :stanza_id => 301 },
  { :line => "Inspired by silence and the closed eyesâ€™ sight", :no => 1225, :created_at => "2013-03-05 09:17:53", :updated_at => "2013-03-05 09:17:53", :stanza_id => 302 },
  { :line => "His force could work with a new luminous art", :no => 1226, :created_at => "2013-03-05 09:17:53", :updated_at => "2013-03-05 09:17:53", :stanza_id => 302 },
  { :line => "On the crude material from which all is made", :no => 1227, :created_at => "2013-03-05 09:17:53", :updated_at => "2013-03-05 09:17:53", :stanza_id => 302 },
  { :line => "And the refusal of Inertiaâ€™s mass", :no => 1228, :created_at => "2013-03-05 09:17:53", :updated_at => "2013-03-05 09:17:53", :stanza_id => 302 },
  { :line => "And the grey front of the worldâ€™s Ignorance", :no => 1229, :created_at => "2013-03-05 09:17:54", :updated_at => "2013-03-05 09:17:54", :stanza_id => 302 },
  { :line => "And nescient Matter and the huge error of life.", :no => 1230, :created_at => "2013-03-05 09:17:54", :updated_at => "2013-03-05 09:17:54", :stanza_id => 302 },
  { :line => "As a sculptor chisels a deity out of stone", :no => 1231, :created_at => "2013-03-05 09:17:54", :updated_at => "2013-03-05 09:17:54", :stanza_id => 303 },
  { :line => "He slowly chipped off the dark envelope,", :no => 1232, :created_at => "2013-03-05 09:17:54", :updated_at => "2013-03-05 09:17:54", :stanza_id => 303 },
  { :line => "Line of defence of Natureâ€™s ignorance,", :no => 1233, :created_at => "2013-03-05 09:17:55", :updated_at => "2013-03-05 09:17:55", :stanza_id => 303 },
  { :line => "The illusion and mystery of the Inconscient", :no => 1234, :created_at => "2013-03-05 09:17:55", :updated_at => "2013-03-05 09:17:55", :stanza_id => 303 },
  { :line => "In whose black pall the Eternal wraps his head", :no => 1235, :created_at => "2013-03-05 09:17:55", :updated_at => "2013-03-05 09:17:55", :stanza_id => 303 },
  { :line => "That he may act unknown in cosmic Time.", :no => 1236, :created_at => "2013-03-05 09:17:55", :updated_at => "2013-03-05 09:17:55", :stanza_id => 303 },
  { :line => "A splendour of self-creation from the peaks,", :no => 1237, :created_at => "2013-03-05 09:17:55", :updated_at => "2013-03-05 09:17:55", :stanza_id => 304 },
  { :line => "A transfiguration in the mystic depths,", :no => 1238, :created_at => "2013-03-05 09:17:56", :updated_at => "2013-03-05 09:17:56", :stanza_id => 304 },
  { :line => "A happier cosmic working could begin", :no => 1239, :created_at => "2013-03-05 09:17:56", :updated_at => "2013-03-05 09:17:56", :stanza_id => 304 },
  { :line => "And fashion the world-shape in him anew,", :no => 1240, :created_at => "2013-03-05 09:17:56", :updated_at => "2013-03-05 09:17:56", :stanza_id => 304 },
  { :line => "God found in Nature, Nature fulfilled in God.", :no => 1241, :created_at => "2013-03-05 09:17:56", :updated_at => "2013-03-05 09:17:56", :stanza_id => 304 },
  { :line => "Already in him was seen that task of Power:", :no => 1242, :created_at => "2013-03-05 09:17:56", :updated_at => "2013-03-05 09:17:56", :stanza_id => 305 },
  { :line => "Life made its home on the high tops of self;", :no => 1243, :created_at => "2013-03-05 09:17:57", :updated_at => "2013-03-05 09:17:57", :stanza_id => 305 },
  { :line => "His soul, mind, heart became a single sun;", :no => 1244, :created_at => "2013-03-05 09:17:57", :updated_at => "2013-03-05 09:17:57", :stanza_id => 305 },
  { :line => "Only lifeâ€™s lower reaches remained dim.", :no => 1245, :created_at => "2013-03-05 09:17:57", :updated_at => "2013-03-05 09:17:57", :stanza_id => 305 },
  { :line => "But there too, in the uncertain shadow of life,", :no => 1246, :created_at => "2013-03-05 09:17:57", :updated_at => "2013-03-05 09:17:57", :stanza_id => 306 },
  { :line => "There was a labour and a fiery breath;", :no => 1247, :created_at => "2013-03-05 09:17:57", :updated_at => "2013-03-05 09:17:57", :stanza_id => 306 },
  { :line => "The ambiguous cowled celestial puissance worked", :no => 1248, :created_at => "2013-03-05 09:17:58", :updated_at => "2013-03-05 09:17:58", :stanza_id => 306 },
  { :line => "Watched by the inner Witnessâ€™s moveless peace.", :no => 1249, :created_at => "2013-03-05 09:17:58", :updated_at => "2013-03-05 09:17:58", :stanza_id => 306 },
  { :line => "Even on the struggling Nature left below", :no => 1250, :created_at => "2013-03-05 09:17:58", :updated_at => "2013-03-05 09:17:58", :stanza_id => 307 },
  { :line => "Strong periods of illumination came:", :no => 1251, :created_at => "2013-03-05 09:17:58", :updated_at => "2013-03-05 09:17:58", :stanza_id => 307 },
  { :line => "Lightnings of glory after glory burned,", :no => 1252, :created_at => "2013-03-05 09:17:59", :updated_at => "2013-03-05 09:17:59", :stanza_id => 307 },
  { :line => "Experience was a tale of blaze and fire,", :no => 1253, :created_at => "2013-03-05 09:17:59", :updated_at => "2013-03-05 09:17:59", :stanza_id => 307 },
  { :line => "Air rippled round the argosies of the Gods,", :no => 1254, :created_at => "2013-03-05 09:17:59", :updated_at => "2013-03-05 09:17:59", :stanza_id => 307 },
  { :line => "Strange riches sailed to him from the Unseen;", :no => 1255, :created_at => "2013-03-05 09:17:59", :updated_at => "2013-03-05 09:17:59", :stanza_id => 307 },
  { :line => "Splendours of insight filled the blank of thought,", :no => 1256, :created_at => "2013-03-05 09:17:59", :updated_at => "2013-03-05 09:17:59", :stanza_id => 307 },
  { :line => "Knowledge spoke to the inconscient stillnesses,", :no => 1257, :created_at => "2013-03-05 09:17:59", :updated_at => "2013-03-05 09:17:59", :stanza_id => 307 },
  { :line => "Rivers poured down of bliss and luminous force,", :no => 1258, :created_at => "2013-03-05 09:18:00", :updated_at => "2013-03-05 09:18:00", :stanza_id => 307 },
  { :line => "Visits of beauty, storm-sweeps of delight", :no => 1259, :created_at => "2013-03-05 09:18:00", :updated_at => "2013-03-05 09:18:00", :stanza_id => 307 },
  { :line => "Rained from the all-powerful Mystery above.", :no => 1260, :created_at => "2013-03-05 09:18:00", :updated_at => "2013-03-05 09:18:00", :stanza_id => 307 },
  { :line => "Thence stooped the eagles of Omniscience.", :no => 1261, :created_at => "2013-03-05 09:18:00", :updated_at => "2013-03-05 09:18:00", :stanza_id => 308 },
  { :line => "A dense veil was rent, a mighty whisper heard;", :no => 1262, :created_at => "2013-03-05 09:18:01", :updated_at => "2013-03-05 09:18:01", :stanza_id => 309 },
  { :line => "Repeated in the privacy of his soul,", :no => 1263, :created_at => "2013-03-05 09:18:01", :updated_at => "2013-03-05 09:18:01", :stanza_id => 309 },
  { :line => "A wisdom-cry from rapt transcendences", :no => 1264, :created_at => "2013-03-05 09:18:01", :updated_at => "2013-03-05 09:18:01", :stanza_id => 309 },
  { :line => "Sang on the mountains of an unseen world;", :no => 1265, :created_at => "2013-03-05 09:18:01", :updated_at => "2013-03-05 09:18:01", :stanza_id => 309 },
  { :line => "The voices that an inner listening hears", :no => 1266, :created_at => "2013-03-05 09:18:01", :updated_at => "2013-03-05 09:18:01", :stanza_id => 309 },
  { :line => "Conveyed to him their prophet utterances,", :no => 1267, :created_at => "2013-03-05 09:18:02", :updated_at => "2013-03-05 09:18:02", :stanza_id => 309 },
  { :line => "And flame-wrapt outbursts of the immortal Word", :no => 1268, :created_at => "2013-03-05 09:18:02", :updated_at => "2013-03-05 09:18:02", :stanza_id => 309 },
  { :line => "And flashes of an occult revealing Light", :no => 1269, :created_at => "2013-03-05 09:18:02", :updated_at => "2013-03-05 09:18:02", :stanza_id => 309 },
  { :line => "Approached him from the unreachable secrecy.", :no => 1270, :created_at => "2013-03-05 09:18:02", :updated_at => "2013-03-05 09:18:02", :stanza_id => 309 },
  { :line => "An inspired Knowledge sat enthroned within", :no => 1271, :created_at => "2013-03-05 09:18:02", :updated_at => "2013-03-05 09:18:02", :stanza_id => 310 },
  { :line => "Whose seconds illumined more than reasonâ€™s years:", :no => 1272, :created_at => "2013-03-05 09:18:03", :updated_at => "2013-03-05 09:18:03", :stanza_id => 310 },
  { :line => "An ictus of revealing lustre fell", :no => 1273, :created_at => "2013-03-05 09:18:03", :updated_at => "2013-03-05 09:18:03", :stanza_id => 310 },
  { :line => "As if a pointing accent upon Truth,", :no => 1274, :created_at => "2013-03-05 09:18:03", :updated_at => "2013-03-05 09:18:03", :stanza_id => 310 },
  { :line => "And like a sky-flare showing all the ground", :no => 1275, :created_at => "2013-03-05 09:18:03", :updated_at => "2013-03-05 09:18:03", :stanza_id => 310 },
  { :line => "A swift intuitive discernment shone.", :no => 1276, :created_at => "2013-03-05 09:18:03", :updated_at => "2013-03-05 09:18:03", :stanza_id => 310 },
  { :line => "One glance could separate the true and false,", :no => 1277, :created_at => "2013-03-05 09:18:04", :updated_at => "2013-03-05 09:18:04", :stanza_id => 311 },
  { :line => "Or raise its rapid torch-fire in the dark", :no => 1278, :created_at => "2013-03-05 09:18:04", :updated_at => "2013-03-05 09:18:04", :stanza_id => 311 },
  { :line => "To check the claimants crowding through mindâ€™s gates", :no => 1279, :created_at => "2013-03-05 09:18:04", :updated_at => "2013-03-05 09:18:04", :stanza_id => 311 },
  { :line => "Covered by the forged signatures of the gods,", :no => 1280, :created_at => "2013-03-05 09:18:04", :updated_at => "2013-03-05 09:18:04", :stanza_id => 311 },
  { :line => "Detect the magic bride in her disguise", :no => 1281, :created_at => "2013-03-05 09:18:05", :updated_at => "2013-03-05 09:18:05", :stanza_id => 311 },
  { :line => "Or scan the apparent face of thought and life.", :no => 1282, :created_at => "2013-03-05 09:18:05", :updated_at => "2013-03-05 09:18:05", :stanza_id => 311 },
  { :line => "Oft inspiration with her lightning feet,", :no => 1283, :created_at => "2013-03-05 09:18:05", :updated_at => "2013-03-05 09:18:05", :stanza_id => 312 },
  { :line => "A sudden messenger from the all-seeing tops,", :no => 1284, :created_at => "2013-03-05 09:18:05", :updated_at => "2013-03-05 09:18:05", :stanza_id => 312 },
  { :line => "Traversed the soundless corridors of his mind", :no => 1285, :created_at => "2013-03-05 09:18:05", :updated_at => "2013-03-05 09:18:05", :stanza_id => 312 },
  { :line => "Bringing her rhythmic sense of hidden things.", :no => 1286, :created_at => "2013-03-05 09:18:06", :updated_at => "2013-03-05 09:18:06", :stanza_id => 312 },
  { :line => "A music spoke transcending mortal speech.", :no => 1287, :created_at => "2013-03-05 09:18:06", :updated_at => "2013-03-05 09:18:06", :stanza_id => 313 },
  { :line => "As if from a golden phial of the All-Bliss,", :no => 1288, :created_at => "2013-03-05 09:18:06", :updated_at => "2013-03-05 09:18:06", :stanza_id => 314 },
  { :line => "A joy of light, a joy of sudden sight,", :no => 1289, :created_at => "2013-03-05 09:18:07", :updated_at => "2013-03-05 09:18:07", :stanza_id => 314 },
  { :line => "A rapture of the thrilled undying Word", :no => 1290, :created_at => "2013-03-05 09:18:07", :updated_at => "2013-03-05 09:18:07", :stanza_id => 314 },
  { :line => "Poured into his heart as into an empty cup,", :no => 1291, :created_at => "2013-03-05 09:18:07", :updated_at => "2013-03-05 09:18:07", :stanza_id => 314 },
  { :line => "A repetition of Godâ€™s first delight", :no => 1292, :created_at => "2013-03-05 09:18:07", :updated_at => "2013-03-05 09:18:07", :stanza_id => 314 },
  { :line => "Creating in a young and virgin Time.", :no => 1293, :created_at => "2013-03-05 09:18:07", :updated_at => "2013-03-05 09:18:07", :stanza_id => 314 },
  { :line => "In a brief moment caught, a little space,", :no => 1294, :created_at => "2013-03-05 09:18:08", :updated_at => "2013-03-05 09:18:08", :stanza_id => 315 },
  { :line => "All-Knowledge packed into great wordless thoughts", :no => 1295, :created_at => "2013-03-05 09:18:08", :updated_at => "2013-03-05 09:18:08", :stanza_id => 315 },
  { :line => "Lodged in the expectant stillness of his depths", :no => 1296, :created_at => "2013-03-05 09:18:08", :updated_at => "2013-03-05 09:18:08", :stanza_id => 315 },
  { :line => "A crystal of the ultimate Absolute,", :no => 1297, :created_at => "2013-03-05 09:18:08", :updated_at => "2013-03-05 09:18:08", :stanza_id => 315 },
  { :line => "A portion of the inexpressible Truth", :no => 1298, :created_at => "2013-03-05 09:18:09", :updated_at => "2013-03-05 09:18:09", :stanza_id => 315 },
  { :line => "Revealed by silence to the silent soul.", :no => 1299, :created_at => "2013-03-05 09:18:09", :updated_at => "2013-03-05 09:18:09", :stanza_id => 315 },
  { :line => "The intense creatrix in his stillness wrought;", :no => 1300, :created_at => "2013-03-05 09:18:09", :updated_at => "2013-03-05 09:18:09", :stanza_id => 316 },
  { :line => "Her power fallen speechless grew more intimate;", :no => 1301, :created_at => "2013-03-05 09:18:09", :updated_at => "2013-03-05 09:18:09", :stanza_id => 316 },
  { :line => "She looked upon the seen and the unforeseen,", :no => 1302, :created_at => "2013-03-05 09:18:09", :updated_at => "2013-03-05 09:18:09", :stanza_id => 316 },
  { :line => "Unguessed domains she made her native field.", :no => 1303, :created_at => "2013-03-05 09:18:10", :updated_at => "2013-03-05 09:18:10", :stanza_id => 316 },
  { :line => "All-vision gathered into a single ray,", :no => 1304, :created_at => "2013-03-05 09:18:10", :updated_at => "2013-03-05 09:18:10", :stanza_id => 317 },
  { :line => "As when the eyes stare at an invisible point", :no => 1305, :created_at => "2013-03-05 09:18:10", :updated_at => "2013-03-05 09:18:10", :stanza_id => 317 },
  { :line => "Till through the intensity of one luminous spot", :no => 1306, :created_at => "2013-03-05 09:18:10", :updated_at => "2013-03-05 09:18:10", :stanza_id => 317 },
  { :line => "An apocalypse of a world of images", :no => 1307, :created_at => "2013-03-05 09:18:11", :updated_at => "2013-03-05 09:18:11", :stanza_id => 317 },
  { :line => "Enters into the kingdom of the seer.", :no => 1308, :created_at => "2013-03-05 09:18:11", :updated_at => "2013-03-05 09:18:11", :stanza_id => 317 },
  { :line => "A great nude arm of splendour suddenly rose;", :no => 1309, :created_at => "2013-03-05 09:18:11", :updated_at => "2013-03-05 09:18:11", :stanza_id => 318 },
  { :line => "It rent the gauze opaque of Nescience:", :no => 1310, :created_at => "2013-03-05 09:18:11", :updated_at => "2013-03-05 09:18:11", :stanza_id => 318 },
  { :line => "Her lifted fingerâ€™s keen unthinkable tip", :no => 1311, :created_at => "2013-03-05 09:18:11", :updated_at => "2013-03-05 09:18:11", :stanza_id => 318 },
  { :line => "Bared with a stab of flame the closed Beyond.", :no => 1312, :created_at => "2013-03-05 09:18:12", :updated_at => "2013-03-05 09:18:12", :stanza_id => 318 },
  { :line => "An eye awake in voiceless heights of trance,", :no => 1313, :created_at => "2013-03-05 09:18:12", :updated_at => "2013-03-05 09:18:12", :stanza_id => 319 },
  { :line => "A mind plucking at the unimaginable,", :no => 1314, :created_at => "2013-03-05 09:18:12", :updated_at => "2013-03-05 09:18:12", :stanza_id => 319 },
  { :line => "Overleaping with a sole and perilous bound", :no => 1315, :created_at => "2013-03-05 09:18:12", :updated_at => "2013-03-05 09:18:12", :stanza_id => 319 },
  { :line => "The high black wall hiding superconscience,", :no => 1316, :created_at => "2013-03-05 09:18:13", :updated_at => "2013-03-05 09:18:13", :stanza_id => 319 },
  { :line => "She broke in with inspired speech for scythe", :no => 1317, :created_at => "2013-03-05 09:18:13", :updated_at => "2013-03-05 09:18:13", :stanza_id => 319 },
  { :line => "And plundered the Unknowableâ€™s vast estate.", :no => 1318, :created_at => "2013-03-05 09:18:13", :updated_at => "2013-03-05 09:18:13", :stanza_id => 319 },
  { :line => "A gleaner of infinitesimal grains of Truth,", :no => 1319, :created_at => "2013-03-05 09:18:13", :updated_at => "2013-03-05 09:18:13", :stanza_id => 320 },
  { :line => "A sheaf-binder of infinite experience,", :no => 1320, :created_at => "2013-03-05 09:18:14", :updated_at => "2013-03-05 09:18:14", :stanza_id => 320 },
  { :line => "She pierced the guarded mysteries of World-Force", :no => 1321, :created_at => "2013-03-05 09:18:14", :updated_at => "2013-03-05 09:18:14", :stanza_id => 320 },
  { :line => "And her magic methods wrapt in a thousand veils;", :no => 1322, :created_at => "2013-03-05 09:18:14", :updated_at => "2013-03-05 09:18:14", :stanza_id => 320 },
  { :line => "Or she gathered the lost secrets dropped by Time", :no => 1323, :created_at => "2013-03-05 09:18:14", :updated_at => "2013-03-05 09:18:14", :stanza_id => 320 },
  { :line => "In the dust and crannies of his mounting route", :no => 1324, :created_at => "2013-03-05 09:18:14", :updated_at => "2013-03-05 09:18:14", :stanza_id => 320 },
  { :line => "Mid old forsaken dreams of hastening Mind", :no => 1325, :created_at => "2013-03-05 09:18:15", :updated_at => "2013-03-05 09:18:15", :stanza_id => 320 },
  { :line => "And buried remnants of forgotten space.", :no => 1326, :created_at => "2013-03-05 09:18:15", :updated_at => "2013-03-05 09:18:15", :stanza_id => 320 },
  { :line => "A traveller between summit and abyss", :no => 1327, :created_at => "2013-03-05 09:18:15", :updated_at => "2013-03-05 09:18:15", :stanza_id => 321 },
  { :line => "She joined the distant ends, the viewless deeps,", :no => 1328, :created_at => "2013-03-05 09:18:15", :updated_at => "2013-03-05 09:18:15", :stanza_id => 321 },
  { :line => "Or streaked along the roads of Heaven and Hell", :no => 1329, :created_at => "2013-03-05 09:18:16", :updated_at => "2013-03-05 09:18:16", :stanza_id => 321 },
  { :line => "Pursuing all knowledge like a questing hound.", :no => 1330, :created_at => "2013-03-05 09:18:16", :updated_at => "2013-03-05 09:18:16", :stanza_id => 321 },
  { :line => "A reporter and scribe of hidden wisdom talk,", :no => 1331, :created_at => "2013-03-05 09:18:16", :updated_at => "2013-03-05 09:18:16", :stanza_id => 322 },
  { :line => "Her shining minutes of celestial speech,", :no => 1332, :created_at => "2013-03-05 09:18:16", :updated_at => "2013-03-05 09:18:16", :stanza_id => 322 },
  { :line => "Passed through the masked office of the occult mind,", :no => 1333, :created_at => "2013-03-05 09:18:17", :updated_at => "2013-03-05 09:18:17", :stanza_id => 322 },
  { :line => "Transmitting gave to prophet and to seer", :no => 1334, :created_at => "2013-03-05 09:18:17", :updated_at => "2013-03-05 09:18:17", :stanza_id => 322 },
  { :line => "The inspired body of the mystic Truth.", :no => 1335, :created_at => "2013-03-05 09:18:17", :updated_at => "2013-03-05 09:18:17", :stanza_id => 322 },
  { :line => "A recorder of the inquiry of the gods,", :no => 1336, :created_at => "2013-03-05 09:18:17", :updated_at => "2013-03-05 09:18:17", :stanza_id => 323 },
  { :line => "Spokesman of the silent seeings of the Supreme,", :no => 1337, :created_at => "2013-03-05 09:18:17", :updated_at => "2013-03-05 09:18:17", :stanza_id => 323 },
  { :line => "She brought immortal words to mortal men.", :no => 1338, :created_at => "2013-03-05 09:18:18", :updated_at => "2013-03-05 09:18:18", :stanza_id => 323 },
  { :line => "Above the reasonâ€™s brilliant slender curve,", :no => 1339, :created_at => "2013-03-05 09:18:18", :updated_at => "2013-03-05 09:18:18", :stanza_id => 324 },
  { :line => "Released like radiant air dimming a moon,", :no => 1340, :created_at => "2013-03-05 09:18:18", :updated_at => "2013-03-05 09:18:18", :stanza_id => 324 },
  { :line => "Broad spaces of a vision without line", :no => 1341, :created_at => "2013-03-05 09:18:18", :updated_at => "2013-03-05 09:18:18", :stanza_id => 324 },
  { :line => "Or limit swam into his spiritâ€™s ken.", :no => 1342, :created_at => "2013-03-05 09:18:18", :updated_at => "2013-03-05 09:18:18", :stanza_id => 324 },
  { :line => "Oceans of being met his voyaging soul", :no => 1343, :created_at => "2013-03-05 09:18:19", :updated_at => "2013-03-05 09:18:19", :stanza_id => 325 },
  { :line => "Calling to infinite discovery;", :no => 1344, :created_at => "2013-03-05 09:18:19", :updated_at => "2013-03-05 09:18:19", :stanza_id => 325 },
  { :line => "Timeless domains of joy and absolute power", :no => 1345, :created_at => "2013-03-05 09:18:19", :updated_at => "2013-03-05 09:18:19", :stanza_id => 325 },
  { :line => "Stretched out surrounded by the eternal hush;", :no => 1346, :created_at => "2013-03-05 09:18:19", :updated_at => "2013-03-05 09:18:19", :stanza_id => 325 },
  { :line => "The ways that lead to endless happiness", :no => 1347, :created_at => "2013-03-05 09:18:20", :updated_at => "2013-03-05 09:18:20", :stanza_id => 325 },
  { :line => "Ran like dream-smiles through meditating vasts:", :no => 1348, :created_at => "2013-03-05 09:18:20", :updated_at => "2013-03-05 09:18:20", :stanza_id => 325 },
  { :line => "Disclosed stood up in a gold momentâ€™s blaze", :no => 1349, :created_at => "2013-03-05 09:18:20", :updated_at => "2013-03-05 09:18:20", :stanza_id => 325 },
  { :line => "White sun-steppes in the pathless Infinite.", :no => 1350, :created_at => "2013-03-05 09:18:20", :updated_at => "2013-03-05 09:18:20", :stanza_id => 325 },
  { :line => "Along a naked curve in bourneless Self", :no => 1351, :created_at => "2013-03-05 09:18:20", :updated_at => "2013-03-05 09:18:20", :stanza_id => 326 },
  { :line => "The points that run through the closed heart of things", :no => 1352, :created_at => "2013-03-05 09:18:21", :updated_at => "2013-03-05 09:18:21", :stanza_id => 326 },
  { :line => "Shadowed the indeterminable line", :no => 1353, :created_at => "2013-03-05 09:18:21", :updated_at => "2013-03-05 09:18:21", :stanza_id => 326 },
  { :line => "That carries the Everlasting through the years.", :no => 1354, :created_at => "2013-03-05 09:18:21", :updated_at => "2013-03-05 09:18:21", :stanza_id => 326 },
  { :line => "The magician order of the cosmic Mind", :no => 1355, :created_at => "2013-03-05 09:18:21", :updated_at => "2013-03-05 09:18:21", :stanza_id => 327 },
  { :line => "Coercing the freedom of infinity", :no => 1356, :created_at => "2013-03-05 09:18:22", :updated_at => "2013-03-05 09:18:22", :stanza_id => 327 },
  { :line => "With the stark array of Natureâ€™s symbol facts", :no => 1357, :created_at => "2013-03-05 09:18:22", :updated_at => "2013-03-05 09:18:22", :stanza_id => 327 },
  { :line => "And lifeâ€™s incessant signals of event,", :no => 1358, :created_at => "2013-03-05 09:18:22", :updated_at => "2013-03-05 09:18:22", :stanza_id => 327 },
  { :line => "Transmuted chance recurrences into laws,", :no => 1359, :created_at => "2013-03-05 09:18:22", :updated_at => "2013-03-05 09:18:22", :stanza_id => 327 },
  { :line => "A chaos of signs into a universe.", :no => 1360, :created_at => "2013-03-05 09:18:22", :updated_at => "2013-03-05 09:18:22", :stanza_id => 327 },
  { :line => "Out of the rich wonders and the intricate whorls", :no => 1361, :created_at => "2013-03-05 09:18:23", :updated_at => "2013-03-05 09:18:23", :stanza_id => 328 },
  { :line => "Of the spiritâ€™s dance with Matter as its mask", :no => 1362, :created_at => "2013-03-05 09:18:23", :updated_at => "2013-03-05 09:18:23", :stanza_id => 328 },
  { :line => "The balance of the worldâ€™s design grew clear,", :no => 1363, :created_at => "2013-03-05 09:18:23", :updated_at => "2013-03-05 09:18:23", :stanza_id => 328 },
  { :line => "Its symmetry of self-arranged effects", :no => 1364, :created_at => "2013-03-05 09:18:23", :updated_at => "2013-03-05 09:18:23", :stanza_id => 328 },
  { :line => "Managed in the deep perspectives of the soul,", :no => 1365, :created_at => "2013-03-05 09:18:23", :updated_at => "2013-03-05 09:18:23", :stanza_id => 328 },
  { :line => "And the realism of its illusive art,", :no => 1366, :created_at => "2013-03-05 09:18:24", :updated_at => "2013-03-05 09:18:24", :stanza_id => 328 },
  { :line => "Its logic of infinite intelligence,", :no => 1367, :created_at => "2013-03-05 09:18:24", :updated_at => "2013-03-05 09:18:24", :stanza_id => 328 },
  { :line => "Its magic of a changing eternity.", :no => 1368, :created_at => "2013-03-05 09:18:24", :updated_at => "2013-03-05 09:18:24", :stanza_id => 328 },
  { :line => "A glimpse was caught of things for ever unknown", :no => 1369, :created_at => "2013-03-05 09:18:24", :updated_at => "2013-03-05 09:18:24", :stanza_id => 329 },
  { :line => "The letters stood out of the unmoving Word.", :no => 1370, :created_at => "2013-03-05 09:18:24", :updated_at => "2013-03-05 09:18:24", :stanza_id => 329 },
  { :line => "In the immutable nameless Origin", :no => 1371, :created_at => "2013-03-05 09:18:25", :updated_at => "2013-03-05 09:18:25", :stanza_id => 330 },
  { :line => "Was seen emerging as from fathomless seas", :no => 1372, :created_at => "2013-03-05 09:18:25", :updated_at => "2013-03-05 09:18:25", :stanza_id => 330 },
  { :line => "The trail of the Ideas that made the world,", :no => 1373, :created_at => "2013-03-05 09:18:25", :updated_at => "2013-03-05 09:18:25", :stanza_id => 330 },
  { :line => "And, sown in the black earth of Natureâ€™s trance,", :no => 1374, :created_at => "2013-03-05 09:18:25", :updated_at => "2013-03-05 09:18:25", :stanza_id => 330 },
  { :line => "The seed of the Spiritâ€™s blind and huge desire", :no => 1375, :created_at => "2013-03-05 09:18:26", :updated_at => "2013-03-05 09:18:26", :stanza_id => 330 },
  { :line => "From which the tree of cosmos was conceived", :no => 1376, :created_at => "2013-03-05 09:18:26", :updated_at => "2013-03-05 09:18:26", :stanza_id => 330 },
  { :line => "And spread its magic arms through a dream of space.", :no => 1377, :created_at => "2013-03-05 09:18:26", :updated_at => "2013-03-05 09:18:26", :stanza_id => 330 },
  { :line => "Immense realities took on a shape:", :no => 1378, :created_at => "2013-03-05 09:18:26", :updated_at => "2013-03-05 09:18:26", :stanza_id => 331 },
  { :line => "There looked out from the shadow of the Unknown", :no => 1379, :created_at => "2013-03-05 09:18:27", :updated_at => "2013-03-05 09:18:27", :stanza_id => 331 },
  { :line => "The bodiless Namelessness that saw God born", :no => 1380, :created_at => "2013-03-05 09:18:27", :updated_at => "2013-03-05 09:18:27", :stanza_id => 331 },
  { :line => "And tries to gain from the mortalâ€™s mind and soul", :no => 1381, :created_at => "2013-03-05 09:18:27", :updated_at => "2013-03-05 09:18:27", :stanza_id => 331 },
  { :line => "A deathless body and a divine name.", :no => 1382, :created_at => "2013-03-05 09:18:27", :updated_at => "2013-03-05 09:18:27", :stanza_id => 331 },
  { :line => "The immobile lips, the great surreal wings,", :no => 1383, :created_at => "2013-03-05 09:18:28", :updated_at => "2013-03-05 09:18:28", :stanza_id => 332 },
  { :line => "The visage masked by superconscient Sleep,", :no => 1384, :created_at => "2013-03-05 09:18:28", :updated_at => "2013-03-05 09:18:28", :stanza_id => 332 },
  { :line => "The eyes with their closed lids that see all things,", :no => 1385, :created_at => "2013-03-05 09:18:28", :updated_at => "2013-03-05 09:18:28", :stanza_id => 332 },
  { :line => "Appeared of the Architect who builds in trance.", :no => 1386, :created_at => "2013-03-05 09:18:28", :updated_at => "2013-03-05 09:18:28", :stanza_id => 332 },
  { :line => "The original Desire born in the Void", :no => 1387, :created_at => "2013-03-05 09:18:29", :updated_at => "2013-03-05 09:18:29", :stanza_id => 333 },
  { :line => "Peered out; he saw the hope that never sleeps,", :no => 1388, :created_at => "2013-03-05 09:18:29", :updated_at => "2013-03-05 09:18:29", :stanza_id => 333 },
  { :line => "The feet that run behind a fleeting fate,", :no => 1389, :created_at => "2013-03-05 09:18:29", :updated_at => "2013-03-05 09:18:29", :stanza_id => 333 },
  { :line => "The ineffable meaning of the endless dream.", :no => 1390, :created_at => "2013-03-05 09:18:29", :updated_at => "2013-03-05 09:18:29", :stanza_id => 333 },
  { :line => "As if a torch held by a power of God,", :no => 1391, :created_at => "2013-03-05 09:18:30", :updated_at => "2013-03-05 09:18:30", :stanza_id => 334 },
  { :line => "The radiant world of the everlasting Truth", :no => 1392, :created_at => "2013-03-05 09:18:30", :updated_at => "2013-03-05 09:18:30", :stanza_id => 334 },
  { :line => "Glimmered like a faint star bordering the night", :no => 1393, :created_at => "2013-03-05 09:18:30", :updated_at => "2013-03-05 09:18:30", :stanza_id => 334 },
  { :line => "Above the golden Overmindâ€™s shimmering ridge.", :no => 1394, :created_at => "2013-03-05 09:18:30", :updated_at => "2013-03-05 09:18:30", :stanza_id => 334 },
  { :line => "Even were caught as through a cunning veil", :no => 1395, :created_at => "2013-03-05 09:18:30", :updated_at => "2013-03-05 09:18:30", :stanza_id => 335 },
  { :line => "The smile of love that sanctions the long game,", :no => 1396, :created_at => "2013-03-05 09:18:31", :updated_at => "2013-03-05 09:18:31", :stanza_id => 335 },
  { :line => "The calm indulgence and maternal breasts", :no => 1397, :created_at => "2013-03-05 09:18:31", :updated_at => "2013-03-05 09:18:31", :stanza_id => 335 },
  { :line => "Of Wisdom suckling the child-laughter of Chance,", :no => 1398, :created_at => "2013-03-05 09:18:31", :updated_at => "2013-03-05 09:18:31", :stanza_id => 335 },
  { :line => "Silence the nurse of the Almightyâ€™s power,", :no => 1399, :created_at => "2013-03-05 09:18:31", :updated_at => "2013-03-05 09:18:31", :stanza_id => 335 },
  { :line => "The omniscient hush, womb of the immortal Word,", :no => 1400, :created_at => "2013-03-05 09:18:31", :updated_at => "2013-03-05 09:18:31", :stanza_id => 335 },
  { :line => "And of the Timeless the still brooding face,", :no => 1401, :created_at => "2013-03-05 09:18:31", :updated_at => "2013-03-05 09:18:31", :stanza_id => 335 },
  { :line => "And the creative eye of Eternity.", :no => 1402, :created_at => "2013-03-05 09:18:32", :updated_at => "2013-03-05 09:18:32", :stanza_id => 335 },
  { :line => "The inspiring goddess entered a mortalâ€™s breast,", :no => 1403, :created_at => "2013-03-05 09:18:32", :updated_at => "2013-03-05 09:18:32", :stanza_id => 336 },
  { :line => "Made there her study of divining thought", :no => 1404, :created_at => "2013-03-05 09:18:32", :updated_at => "2013-03-05 09:18:32", :stanza_id => 336 },
  { :line => "And sanctuary of prophetic speech", :no => 1405, :created_at => "2013-03-05 09:18:32", :updated_at => "2013-03-05 09:18:32", :stanza_id => 336 },
  { :line => "And sat upon the tripod seat of mind:", :no => 1406, :created_at => "2013-03-05 09:18:33", :updated_at => "2013-03-05 09:18:33", :stanza_id => 336 },
  { :line => "All was made wide above, all lit below.", :no => 1407, :created_at => "2013-03-05 09:18:33", :updated_at => "2013-03-05 09:18:33", :stanza_id => 336 },
  { :line => "In darknessâ€™ core she dug out wells of light,", :no => 1408, :created_at => "2013-03-05 09:18:33", :updated_at => "2013-03-05 09:18:33", :stanza_id => 337 },
  { :line => "On the undiscovered depths imposed a form,", :no => 1409, :created_at => "2013-03-05 09:18:33", :updated_at => "2013-03-05 09:18:33", :stanza_id => 337 },
  { :line => "Lent a vibrant cry to the unuttered vasts,", :no => 1410, :created_at => "2013-03-05 09:18:33", :updated_at => "2013-03-05 09:18:33", :stanza_id => 337 },
  { :line => "And through great shoreless, voiceless, starless breadths", :no => 1411, :created_at => "2013-03-05 09:18:34", :updated_at => "2013-03-05 09:18:34", :stanza_id => 337 },
  { :line => "Bore earthward fragments of revealing thought", :no => 1412, :created_at => "2013-03-05 09:18:34", :updated_at => "2013-03-05 09:18:34", :stanza_id => 337 },
  { :line => "Hewn from the silence of the Ineffable.", :no => 1413, :created_at => "2013-03-05 09:18:34", :updated_at => "2013-03-05 09:18:34", :stanza_id => 337 },
  { :line => "A voice in the heart uttered the unspoken Name,", :no => 1414, :created_at => "2013-03-05 09:18:34", :updated_at => "2013-03-05 09:18:34", :stanza_id => 338 },
  { :line => "A dream of seeking thought wandering through space", :no => 1415, :created_at => "2013-03-05 09:18:35", :updated_at => "2013-03-05 09:18:35", :stanza_id => 338 },
  { :line => "Entered the invisible and forbidden house:", :no => 1416, :created_at => "2013-03-05 09:18:35", :updated_at => "2013-03-05 09:18:35", :stanza_id => 338 },
  { :line => "The treasure was found of a supernal Day.", :no => 1417, :created_at => "2013-03-05 09:18:35", :updated_at => "2013-03-05 09:18:35", :stanza_id => 338 },
  { :line => "In the deep subconscient glowed her jewel-lamp;", :no => 1418, :created_at => "2013-03-05 09:18:35", :updated_at => "2013-03-05 09:18:35", :stanza_id => 339 },
  { :line => "Lifted, it showed the riches of the Cave", :no => 1419, :created_at => "2013-03-05 09:18:35", :updated_at => "2013-03-05 09:18:35", :stanza_id => 339 },
  { :line => "Where, by the miser traffickers of sense", :no => 1420, :created_at => "2013-03-05 09:18:36", :updated_at => "2013-03-05 09:18:36", :stanza_id => 339 },
  { :line => "Unused, guarded beneath Nightâ€™s dragon paws,", :no => 1421, :created_at => "2013-03-05 09:18:36", :updated_at => "2013-03-05 09:18:36", :stanza_id => 339 },
  { :line => "In folds of velvet darkness draped they sleep", :no => 1422, :created_at => "2013-03-05 09:18:36", :updated_at => "2013-03-05 09:18:36", :stanza_id => 339 },
  { :line => "Whose priceless value could have saved the world.", :no => 1423, :created_at => "2013-03-05 09:18:36", :updated_at => "2013-03-05 09:18:36", :stanza_id => 339 },
  { :line => "A darkness carrying morning in its breast", :no => 1424, :created_at => "2013-03-05 09:18:37", :updated_at => "2013-03-05 09:18:37", :stanza_id => 340 },
  { :line => "Looked for the eternal wide returning gleam,", :no => 1425, :created_at => "2013-03-05 09:18:37", :updated_at => "2013-03-05 09:18:37", :stanza_id => 340 },
  { :line => "Waiting the advent of a larger ray", :no => 1426, :created_at => "2013-03-05 09:18:37", :updated_at => "2013-03-05 09:18:37", :stanza_id => 340 },
  { :line => "And rescue of the lost herds of the Sun.", :no => 1427, :created_at => "2013-03-05 09:18:37", :updated_at => "2013-03-05 09:18:37", :stanza_id => 340 },
  { :line => "In a splendid extravagance of the waste of God", :no => 1428, :created_at => "2013-03-05 09:18:37", :updated_at => "2013-03-05 09:18:37", :stanza_id => 341 },
  { :line => "Dropped carelessly in creationâ€™s spendthrift work,", :no => 1429, :created_at => "2013-03-05 09:18:38", :updated_at => "2013-03-05 09:18:38", :stanza_id => 341 },
  { :line => "Left in the chantiers of the bottomless world", :no => 1430, :created_at => "2013-03-05 09:18:38", :updated_at => "2013-03-05 09:18:38", :stanza_id => 341 },
  { :line => "And stolen by the robbers of the Deep,", :no => 1431, :created_at => "2013-03-05 09:18:38", :updated_at => "2013-03-05 09:18:38", :stanza_id => 341 },
  { :line => "The golden shekels of the Eternal lie,", :no => 1432, :created_at => "2013-03-05 09:18:38", :updated_at => "2013-03-05 09:18:38", :stanza_id => 341 },
  { :line => "Hoarded from touch and view and thoughtâ€™s desire,", :no => 1433, :created_at => "2013-03-05 09:18:38", :updated_at => "2013-03-05 09:18:38", :stanza_id => 341 },
  { :line => "Locked in blind antres of the ignorant flood", :no => 1434, :created_at => "2013-03-05 09:18:39", :updated_at => "2013-03-05 09:18:39", :stanza_id => 341 },
  { :line => "Lest men should find them and be even as Gods.", :no => 1435, :created_at => "2013-03-05 09:18:39", :updated_at => "2013-03-05 09:18:39", :stanza_id => 341 },
  { :line => "A vision lightened on the viewless heights,", :no => 1436, :created_at => "2013-03-05 09:18:39", :updated_at => "2013-03-05 09:18:39", :stanza_id => 342 },
  { :line => "A wisdom illumined from the voiceless depths:", :no => 1437, :created_at => "2013-03-05 09:18:39", :updated_at => "2013-03-05 09:18:39", :stanza_id => 342 },
  { :line => "A deeper interpretation greatened Truth,", :no => 1438, :created_at => "2013-03-05 09:18:39", :updated_at => "2013-03-05 09:18:39", :stanza_id => 342 },
  { :line => "A grand reversal of the Night and Day;", :no => 1439, :created_at => "2013-03-05 09:18:40", :updated_at => "2013-03-05 09:18:40", :stanza_id => 342 },
  { :line => "All the worldâ€™s values changed heightening lifeâ€™s aim;", :no => 1440, :created_at => "2013-03-05 09:18:40", :updated_at => "2013-03-05 09:18:40", :stanza_id => 342 },
  { :line => "A wiser word, a larger thought came in", :no => 1441, :created_at => "2013-03-05 09:18:40", :updated_at => "2013-03-05 09:18:40", :stanza_id => 342 },
  { :line => "Than what the slow labour of human mind can bring,", :no => 1442, :created_at => "2013-03-05 09:18:40", :updated_at => "2013-03-05 09:18:40", :stanza_id => 342 },
  { :line => "A secret sense awoke that could perceive", :no => 1443, :created_at => "2013-03-05 09:18:40", :updated_at => "2013-03-05 09:18:40", :stanza_id => 342 },
  { :line => "A Presence and a Greatness everywhere.", :no => 1444, :created_at => "2013-03-05 09:18:41", :updated_at => "2013-03-05 09:18:41", :stanza_id => 342 },
  { :line => "The universe was not now this senseless whirl", :no => 1445, :created_at => "2013-03-05 09:18:41", :updated_at => "2013-03-05 09:18:41", :stanza_id => 343 },
  { :line => "Borne round inert on an immense machine;", :no => 1446, :created_at => "2013-03-05 09:18:41", :updated_at => "2013-03-05 09:18:41", :stanza_id => 343 },
  { :line => "It cast away its grandiose lifeless front,", :no => 1447, :created_at => "2013-03-05 09:18:41", :updated_at => "2013-03-05 09:18:41", :stanza_id => 343 },
  { :line => "A mechanism no more or work of Chance,", :no => 1448, :created_at => "2013-03-05 09:18:41", :updated_at => "2013-03-05 09:18:41", :stanza_id => 343 },
  { :line => "But a living movement of the body of God.", :no => 1449, :created_at => "2013-03-05 09:18:42", :updated_at => "2013-03-05 09:18:42", :stanza_id => 343 },
  { :line => "A spirit hid in forces and in forms", :no => 1450, :created_at => "2013-03-05 09:18:42", :updated_at => "2013-03-05 09:18:42", :stanza_id => 344 },
  { :line => "Was the spectator of the mobile scene:", :no => 1451, :created_at => "2013-03-05 09:18:42", :updated_at => "2013-03-05 09:18:42", :stanza_id => 344 },
  { :line => "The beauty and the ceaseless miracle", :no => 1452, :created_at => "2013-03-05 09:18:43", :updated_at => "2013-03-05 09:18:43", :stanza_id => 344 },
  { :line => "Let in a glow of the Unmanifest:", :no => 1453, :created_at => "2013-03-05 09:18:43", :updated_at => "2013-03-05 09:18:43", :stanza_id => 344 },
  { :line => "The formless Everlasting moved in it", :no => 1454, :created_at => "2013-03-05 09:18:43", :updated_at => "2013-03-05 09:18:43", :stanza_id => 344 },
  { :line => "Seeking its own perfect form in souls and things.", :no => 1455, :created_at => "2013-03-05 09:18:43", :updated_at => "2013-03-05 09:18:43", :stanza_id => 344 },
  { :line => "Life kept no more a dull and meaningless shape.", :no => 1456, :created_at => "2013-03-05 09:18:44", :updated_at => "2013-03-05 09:18:44", :stanza_id => 345 },
  { :line => "In the struggle and upheaval of the world", :no => 1457, :created_at => "2013-03-05 09:18:44", :updated_at => "2013-03-05 09:18:44", :stanza_id => 346 },
  { :line => "He saw the labour of a godheadâ€™s birth:", :no => 1458, :created_at => "2013-03-05 09:18:44", :updated_at => "2013-03-05 09:18:44", :stanza_id => 346 },
  { :line => "A secret knowledge masked as Ignorance;", :no => 1459, :created_at => "2013-03-05 09:18:44", :updated_at => "2013-03-05 09:18:44", :stanza_id => 346 },
  { :line => "Fate covered with unseen necessity", :no => 1460, :created_at => "2013-03-05 09:18:45", :updated_at => "2013-03-05 09:18:45", :stanza_id => 346 },
  { :line => "The game of chance of an omnipotent Will.", :no => 1461, :created_at => "2013-03-05 09:18:45", :updated_at => "2013-03-05 09:18:45", :stanza_id => 346 },
  { :line => "A glory and a rapture and a charm,", :no => 1462, :created_at => "2013-03-05 09:18:45", :updated_at => "2013-03-05 09:18:45", :stanza_id => 347 },
  { :line => "The All-Blissful sat unknown within the heart;", :no => 1463, :created_at => "2013-03-05 09:18:45", :updated_at => "2013-03-05 09:18:45", :stanza_id => 347 },
  { :line => "Earthâ€™s pains were the ransom of its prisoned delight.", :no => 1464, :created_at => "2013-03-05 09:18:46", :updated_at => "2013-03-05 09:18:46", :stanza_id => 347 },
  { :line => "A glad communion tinged the passing hours;", :no => 1465, :created_at => "2013-03-05 09:18:46", :updated_at => "2013-03-05 09:18:46", :stanza_id => 348 },
  { :line => "The days were travellers on a destined road,", :no => 1466, :created_at => "2013-03-05 09:18:46", :updated_at => "2013-03-05 09:18:46", :stanza_id => 348 },
  { :line => "The nights companions of his musing spirit.", :no => 1467, :created_at => "2013-03-05 09:18:46", :updated_at => "2013-03-05 09:18:46", :stanza_id => 348 },
  { :line => "A heavenly impetus quickened all his breast;", :no => 1468, :created_at => "2013-03-05 09:18:47", :updated_at => "2013-03-05 09:18:47", :stanza_id => 349 },
  { :line => "The trudge of time changed to a splendid march;", :no => 1469, :created_at => "2013-03-05 09:18:47", :updated_at => "2013-03-05 09:18:47", :stanza_id => 349 },
  { :line => "The divine Dwarf towered to unconquered worlds,", :no => 1470, :created_at => "2013-03-05 09:18:47", :updated_at => "2013-03-05 09:18:47", :stanza_id => 349 },
  { :line => "Earth grew too narrow for his victory.", :no => 1471, :created_at => "2013-03-05 09:18:47", :updated_at => "2013-03-05 09:18:47", :stanza_id => 349 },
  { :line => "Once only registering the heavy tread", :no => 1472, :created_at => "2013-03-05 09:18:48", :updated_at => "2013-03-05 09:18:48", :stanza_id => 350 },
  { :line => "Of a blind Power on human littleness,", :no => 1473, :created_at => "2013-03-05 09:18:48", :updated_at => "2013-03-05 09:18:48", :stanza_id => 350 },
  { :line => "Life now became a sure approach to God,", :no => 1474, :created_at => "2013-03-05 09:18:48", :updated_at => "2013-03-05 09:18:48", :stanza_id => 350 },
  { :line => "Existence a divine experiment", :no => 1475, :created_at => "2013-03-05 09:18:48", :updated_at => "2013-03-05 09:18:48", :stanza_id => 350 },
  { :line => "And cosmos the soulâ€™s opportunity.", :no => 1476, :created_at => "2013-03-05 09:18:48", :updated_at => "2013-03-05 09:18:48", :stanza_id => 350 },
  { :line => "The world was a conception and a birth", :no => 1477, :created_at => "2013-03-05 09:18:49", :updated_at => "2013-03-05 09:18:49", :stanza_id => 351 },
  { :line => "Of Spirit in Matter into living forms,", :no => 1478, :created_at => "2013-03-05 09:18:49", :updated_at => "2013-03-05 09:18:49", :stanza_id => 351 },
  { :line => "And Nature bore the Immortal in her womb,", :no => 1479, :created_at => "2013-03-05 09:18:49", :updated_at => "2013-03-05 09:18:49", :stanza_id => 351 },
  { :line => "That she might climb through him to eternal life.", :no => 1480, :created_at => "2013-03-05 09:18:49", :updated_at => "2013-03-05 09:18:49", :stanza_id => 351 },
  { :line => "His being lay down in bright immobile peace", :no => 1481, :created_at => "2013-03-05 09:18:49", :updated_at => "2013-03-05 09:18:49", :stanza_id => 352 },
  { :line => "And bathed in wells of pure spiritual light;", :no => 1482, :created_at => "2013-03-05 09:18:50", :updated_at => "2013-03-05 09:18:50", :stanza_id => 352 },
  { :line => "It wandered in wide fields of wisdom-self", :no => 1483, :created_at => "2013-03-05 09:18:50", :updated_at => "2013-03-05 09:18:50", :stanza_id => 352 },
  { :line => "Lit by the rays of an everlasting sun.", :no => 1484, :created_at => "2013-03-05 09:18:50", :updated_at => "2013-03-05 09:18:50", :stanza_id => 352 },
  { :line => "Even his bodyâ€™s subtle self within", :no => 1485, :created_at => "2013-03-05 09:18:50", :updated_at => "2013-03-05 09:18:50", :stanza_id => 353 },
  { :line => "Could raise the earthly parts towards higher things", :no => 1486, :created_at => "2013-03-05 09:18:51", :updated_at => "2013-03-05 09:18:51", :stanza_id => 353 },
  { :line => "And feel on it the breath of heavenlier air.", :no => 1487, :created_at => "2013-03-05 09:18:51", :updated_at => "2013-03-05 09:18:51", :stanza_id => 353 },
  { :line => "Already it journeyed towards divinity:", :no => 1488, :created_at => "2013-03-05 09:18:51", :updated_at => "2013-03-05 09:18:51", :stanza_id => 354 },
  { :line => "Upbuoyed upon winged winds of rapid joy,", :no => 1489, :created_at => "2013-03-05 09:18:51", :updated_at => "2013-03-05 09:18:51", :stanza_id => 354 },
  { :line => "Upheld to a Light it could not always hold,", :no => 1490, :created_at => "2013-03-05 09:18:51", :updated_at => "2013-03-05 09:18:51", :stanza_id => 354 },
  { :line => "It left mindâ€™s distance from the Truth supreme", :no => 1491, :created_at => "2013-03-05 09:18:52", :updated_at => "2013-03-05 09:18:52", :stanza_id => 354 },
  { :line => "And lost lifeâ€™s incapacity for bliss.", :no => 1492, :created_at => "2013-03-05 09:18:52", :updated_at => "2013-03-05 09:18:52", :stanza_id => 354 },
  { :line => "All now suppressed in us began to emerge.", :no => 1493, :created_at => "2013-03-05 09:18:52", :updated_at => "2013-03-05 09:18:52", :stanza_id => 355 },
  { :line => "Thus came his soulâ€™s release from Ignorance,", :no => 1494, :created_at => "2013-03-05 09:18:52", :updated_at => "2013-03-05 09:18:52", :stanza_id => 356 },
  { :line => "His mind and bodyâ€™s first spiritual change.", :no => 1495, :created_at => "2013-03-05 09:18:53", :updated_at => "2013-03-05 09:18:53", :stanza_id => 356 },
  { :line => "A wide God-knowledge poured down from above,", :no => 1496, :created_at => "2013-03-05 09:18:53", :updated_at => "2013-03-05 09:18:53", :stanza_id => 357 },
  { :line => "A new world-knowledge broadened from within:", :no => 1497, :created_at => "2013-03-05 09:18:53", :updated_at => "2013-03-05 09:18:53", :stanza_id => 357 },
  { :line => "His daily thoughts looked up to the True and One,", :no => 1498, :created_at => "2013-03-05 09:18:53", :updated_at => "2013-03-05 09:18:53", :stanza_id => 357 },
  { :line => "His commonest doings welled from an inner Light.", :no => 1499, :created_at => "2013-03-05 09:18:54", :updated_at => "2013-03-05 09:18:54", :stanza_id => 357 },
  { :line => "Awakened to the lines that Nature hides,", :no => 1500, :created_at => "2013-03-05 09:18:54", :updated_at => "2013-03-05 09:18:54", :stanza_id => 358 },
  { :line => "Attuned to her movements that exceed our ken,", :no => 1501, :created_at => "2013-03-05 09:18:54", :updated_at => "2013-03-05 09:18:54", :stanza_id => 358 },
  { :line => "He grew one with a covert universe.", :no => 1502, :created_at => "2013-03-05 09:18:54", :updated_at => "2013-03-05 09:18:54", :stanza_id => 358 },
  { :line => "His grasp surprised her mightiest energiesâ€™ springs;", :no => 1503, :created_at => "2013-03-05 09:18:55", :updated_at => "2013-03-05 09:18:55", :stanza_id => 359 },
  { :line => "He spoke with the unknown Guardians of the worlds,", :no => 1504, :created_at => "2013-03-05 09:18:55", :updated_at => "2013-03-05 09:18:55", :stanza_id => 359 },
  { :line => "Forms he descried our mortal eyes see not.", :no => 1505, :created_at => "2013-03-05 09:18:55", :updated_at => "2013-03-05 09:18:55", :stanza_id => 359 },
  { :line => "His wide eyes bodied viewless entities,", :no => 1506, :created_at => "2013-03-05 09:18:55", :updated_at => "2013-03-05 09:18:55", :stanza_id => 360 },
  { :line => "He saw the cosmic forces at their work", :no => 1507, :created_at => "2013-03-05 09:18:55", :updated_at => "2013-03-05 09:18:55", :stanza_id => 360 },
  { :line => "And felt the occult impulse behind manâ€™s will.", :no => 1508, :created_at => "2013-03-05 09:18:56", :updated_at => "2013-03-05 09:18:56", :stanza_id => 360 },
  { :line => "Timeâ€™s secrets were to him an oft-read book;", :no => 1509, :created_at => "2013-03-05 09:18:56", :updated_at => "2013-03-05 09:18:56", :stanza_id => 361 },
  { :line => "The records of the future and the past", :no => 1510, :created_at => "2013-03-05 09:18:56", :updated_at => "2013-03-05 09:18:56", :stanza_id => 361 },
  { :line => "Outlined their excerpts on the etheric page.", :no => 1511, :created_at => "2013-03-05 09:18:56", :updated_at => "2013-03-05 09:18:56", :stanza_id => 361 },
  { :line => "One and harmonious by the Makerâ€™s skill,", :no => 1512, :created_at => "2013-03-05 09:18:57", :updated_at => "2013-03-05 09:18:57", :stanza_id => 362 },
  { :line => "The human in him paced with the divine.", :no => 1513, :created_at => "2013-03-05 09:18:57", :updated_at => "2013-03-05 09:18:57", :stanza_id => 362 },
  { :line => "His acts betrayed not the interior flame.", :no => 1514, :created_at => "2013-03-05 09:18:57", :updated_at => "2013-03-05 09:18:57", :stanza_id => 363 },
  { :line => "This forged the greatness of his front to earth.", :no => 1515, :created_at => "2013-03-05 09:18:58", :updated_at => "2013-03-05 09:18:58", :stanza_id => 364 },
  { :line => "A genius heightened in his bodyâ€™s cells", :no => 1516, :created_at => "2013-03-05 09:18:58", :updated_at => "2013-03-05 09:18:58", :stanza_id => 365 },
  { :line => "That knew the meaning of his fate-hedged works", :no => 1517, :created_at => "2013-03-05 09:18:58", :updated_at => "2013-03-05 09:18:58", :stanza_id => 365 },
  { :line => "Akin to the march of unaccomplished Powers", :no => 1518, :created_at => "2013-03-05 09:18:58", :updated_at => "2013-03-05 09:18:58", :stanza_id => 365 },
  { :line => "Beyond lifeâ€™s arc in spiritâ€™s immensities.", :no => 1519, :created_at => "2013-03-05 09:18:59", :updated_at => "2013-03-05 09:18:59", :stanza_id => 365 },
  { :line => "Apart he lived in his mindâ€™s solitude,", :no => 1520, :created_at => "2013-03-05 09:18:59", :updated_at => "2013-03-05 09:18:59", :stanza_id => 366 },
  { :line => "A demigod shaping the lives of men:", :no => 1521, :created_at => "2013-03-05 09:18:59", :updated_at => "2013-03-05 09:18:59", :stanza_id => 366 },
  { :line => "One soulâ€™s ambition lifted up the race;", :no => 1522, :created_at => "2013-03-05 09:18:59", :updated_at => "2013-03-05 09:18:59", :stanza_id => 366 },
  { :line => "A Power worked, but none knew whence it came.", :no => 1523, :created_at => "2013-03-05 09:19:00", :updated_at => "2013-03-05 09:19:00", :stanza_id => 366 },
  { :line => "The universal strengths were linked with his;", :no => 1524, :created_at => "2013-03-05 09:19:00", :updated_at => "2013-03-05 09:19:00", :stanza_id => 367 },
  { :line => "Feeling earthâ€™s smallness with their boundless breadths,", :no => 1525, :created_at => "2013-03-05 09:19:00", :updated_at => "2013-03-05 09:19:00", :stanza_id => 367 },
  { :line => "He drew the energies that transmute an age.", :no => 1526, :created_at => "2013-03-05 09:19:00", :updated_at => "2013-03-05 09:19:00", :stanza_id => 367 },
  { :line => "Immeasurable by the common look,", :no => 1527, :created_at => "2013-03-05 09:19:01", :updated_at => "2013-03-05 09:19:01", :stanza_id => 368 },
  { :line => "He made great dreams a mould for coming things", :no => 1528, :created_at => "2013-03-05 09:19:01", :updated_at => "2013-03-05 09:19:01", :stanza_id => 368 },
  { :line => "And cast his deeds like bronze to front the years.", :no => 1529, :created_at => "2013-03-05 09:19:01", :updated_at => "2013-03-05 09:19:01", :stanza_id => 368 },
  { :line => "His walk through Time outstripped the human stride.", :no => 1530, :created_at => "2013-03-05 09:19:01", :updated_at => "2013-03-05 09:19:01", :stanza_id => 369 },
  { :line => "Lonely his days and splendid like the sunâ€™s.", :no => 1531, :created_at => "2013-03-05 09:19:02", :updated_at => "2013-03-05 09:19:02", :stanza_id => 370 }
], :without_protection => true )



